(380, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 302, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018profiling\\u2019 means any form of automated processing of personal data consisting of the use of personal data to evaluate certain personal aspects relating to a natural person, in particular to analyse or predict aspects concerning that natural person\\u2019s performance at work, economic situation, health, personal preferences, interests, reliability, behaviour, location or movements\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:09:52', '2024-07-18 11:09:52'),
(381, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 303, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018pseudonymisation\\u2019 means the processing of personal data in such a manner that the personal data can no longer be attributed to a specific data subject without the use of additional information, provided that such additional information is kept separately and is subject to technical and organisational measures to ensure that the personal data are not attributed to an identified or identifiable natural person\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:09:55', '2024-07-18 11:09:55'),
(382, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 304, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018filing system\\u2019 means any structured set of personal data which are accessible according to specific criteria, whether centralised, decentralised or dispersed on a functional or geographical basis\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:09:58', '2024-07-18 11:09:58'),
(383, 'Evidence', 'created', 'App\\Models\\Evidence', 'created', 332, 'App\\Models\\User', 1, '{\"attributes\":{\"name\":\"Evidence to show Protection of Applications from Insecure Communications\",\"createdBy.name\":\"Admin\",\"updatedBy.name\":\"Admin\"}}', NULL, '2024-07-18 11:18:17', '2024-07-18 11:18:17'),
(384, 'Evidence', 'created', 'App\\Models\\Evidence', 'created', 333, 'App\\Models\\User', 1, '{\"attributes\":{\"name\":\"Evidence to show Protection of Applications from Improper Error Handling\",\"createdBy.name\":\"Admin\",\"updatedBy.name\":\"Admin\"}}', NULL, '2024-07-18 11:19:54', '2024-07-18 11:19:54'),
(385, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 305, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018controller\\u2019 means the natural or legal person, public authority, agency or other body which, alone or jointly with others, determines the purposes and means of the processing of personal data; where the purposes and means of such processing are determined by Union or Member State law, the controller or the specific criteria for its nomination may be provided for by Union or Member State law\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:24:37', '2024-07-18 11:24:37'),
(386, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 306, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018processor\\u2019 means a natural or legal person, public authority, agency or other body which processes personal data on behalf of the controller\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:24:38', '2024-07-18 11:24:38'),
(387, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 307, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018recipient\\u2019 means a natural or legal person, public authority, agency or another body, to which the personal data are disclosed, whether a third party or not. However, public authorities which may receive personal data in the framework of a particular inquiry in accordance with Union or Member State law shall not be regarded as recipients; the processing of those data by those public authorities shall be in compliance with the applicable data protection rules according to the purposes of the processing\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:24:39', '2024-07-18 11:24:39');
INSERT INTO `activity_log` (`id`, `log_name`, `description`, `subject_type`, `event`, `subject_id`, `causer_type`, `causer_id`, `properties`, `batch_uuid`, `created_at`, `updated_at`) VALUES
(388, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 308, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018third party\\u2019 means a natural or legal person, public authority, agency or body other than the data subject, controller, processor and persons who, under the direct authority of the controller or processor, are authorised to process personal data\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:24:41', '2024-07-18 11:24:41'),
(389, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 309, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018consent\\u2019 of the data subject means any freely given, specific, informed and unambiguous indication of the data subject\\u2019s wishes by which he or she, by a statement or by a clear affirmative action, signifies agreement to the processing of personal data relating to him or her\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:24:42', '2024-07-18 11:24:42'),
(390, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 310, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018personal data breach\\u2019 means a breach of security leading to the accidental or unlawful destruction, loss, alteration, unauthorised disclosure of, or access to, personal data transmitted, stored or otherwise processed\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:24:44', '2024-07-18 11:24:44'),
(391, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 311, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018genetic data\\u2019 means personal data relating to the inherited or acquired genetic characteristics of a natural person which give unique information about the physiology or the health of that natural person and which result, in particular, from an analysis of a biological sample from the natural person in question\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:24:45', '2024-07-18 11:24:45'),
(392, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 312, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018biometric data\\u2019 means personal data resulting from specific technical processing relating to the physical, physiological or behavioural characteristics of a natural person, which allow or confirm the unique identification of that natural person, such as facial images or dactyloscopic data\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:24:52', '2024-07-18 11:24:52'),
(393, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 313, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018data concerning health\\u2019 means personal data related to the physical or mental health of a natural person, including the provision of health care services, which reveal information about his or her health status\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:24:53', '2024-07-18 11:24:53'),
(394, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 314, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018main establishment\\u2019 means as regards a controller with establishments in more than one Member State, the place of its central administration in the Union, unless the decisions on the purposes and means of the processing of personal data are taken in another establishment of the controller in the Union and the latter establishment has the power to have such decisions implemented, in which case the establishment having taken such decisions is to be considered to be the main establishment\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:24:55', '2024-07-18 11:24:55'),
(395, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 315, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018main establishment\\u2019 means as regards a processor with establishments in more than one Member State, the place of its central administration in the Union, or, if the processor has no central administration in the Union, the establishment of the processor in the Union where the main processing activities in the context of the activities of an establishment of the processor take place to the extent that the processor is subject to specific obligations under this Regulation\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:24:57', '2024-07-18 11:24:57'),
(396, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 316, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018representative\\u2019 means a natural or legal person established in the Union who, designated by the controller or processor in writing pursuant to Article 27, represents the controller or processor with regard to their respective obligations under this Regulation\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:24:58', '2024-07-18 11:24:58'),
(397, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 317, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018enterprise\\u2019 means a natural or legal person engaged in an economic activity, irrespective of its legal form, including partnerships or associations regularly engaged in an economic activity\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:24:59', '2024-07-18 11:24:59'),
(398, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 318, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018group of undertakings\\u2019 means a controlling undertaking and its controlled undertakings\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:25:01', '2024-07-18 11:25:01'),
(399, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 319, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018binding corporate rules\\u2019 means personal data protection policies which are adhered to by a controller or processor established on the territory of a Member State for transfers or a set of transfers of personal data to a controller or processor in one or more third countries within a group of undertakings, or group of enterprises engaged in a joint economic activity\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:25:05', '2024-07-18 11:25:05'),
(400, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 320, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018supervisory authority\\u2019 means an independent public authority which is established by a Member State pursuant to Article 51\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:25:08', '2024-07-18 11:25:08'),
(401, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 321, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018supervisory authority concerned\\u2019 means a supervisory authority which is concerned by the processing of personal data because the controller or processor is established on the territory of the Member State of that supervisory authority\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:25:12', '2024-07-18 11:25:12'),
(402, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 322, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018supervisory authority concerned\\u2019 means a supervisory authority which is concerned by the processing of personal data because data subjects residing in the Member State of that supervisory authority are substantially affected or likely to be substantially affected by the processing\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:25:14', '2024-07-18 11:25:14'),
(403, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 323, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018supervisory authority concerned\\u2019 means a supervisory authority which is concerned by the processing of personal data because a complaint has been lodged with that supervisory authority\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:25:18', '2024-07-18 11:25:18'),
(404, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 324, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018cross-border processing\\u2019 means either processing of personal data which takes place in the context of the activities of establishments in more than one Member State of a controller or processor in the Union where the controller or processor is established in more than one Member State\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:25:19', '2024-07-18 11:25:19'),
(405, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 325, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018cross-border processing\\u2019 means either processing of personal data which takes place in the context of the activities of a single establishment of a controller or processor in the Union but which substantially affects or is likely to substantially affect data subjects in more than one Member State.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:25:21', '2024-07-18 11:25:21'),
(406, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 326, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018relevant and reasoned objection\\u2019 means an objection to a draft decision as to whether there is an infringement of this Regulation, or whether envisaged action in relation to the controller or processor complies with this Regulation, which clearly demonstrates the significance of the risks posed by the draft decision as regards the fundamental rights and freedoms of data subjects and, where applicable, the free flow of personal data within the Union\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:25:22', '2024-07-18 11:25:22'),
(407, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 327, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018information society service\\u2019 means a service as defined in point (b) of Article 1(1) of Directive (EU) 2015\\/1535 of the European Parliament and of the Council\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:25:25', '2024-07-18 11:25:25'),
(408, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 328, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018international organisation\\u2019 means an organisation and its subordinate bodies governed by public international law, or any other body which is set up by, or on the basis of, an agreement between two or more countries.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:25:26', '2024-07-18 11:25:26'),
(409, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 329, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Personal data shall be processed lawfully, fairly and in a transparent manner in relation to the data subject (lawfulness, fairness and transparency)\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:25:27', '2024-07-18 11:25:27'),
(410, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 330, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Personal data shall be processed lawfully, fairly and in a transparent manner in relation to the data subject (lawfulness, fairness and transparency)\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:25:29', '2024-07-18 11:25:29'),
(411, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 331, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Personal data shall be adequate, relevant and limited to what is necessary in relation to the purposes for which they are processed (data minimisation)\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:25:30', '2024-07-18 11:25:30'),
(412, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 332, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Personal data shall be accurate and, where necessary, kept up to date; every reasonable step must be taken to ensure that personal data that are inaccurate, having regard to the purposes for which they are processed, are erased or rectified without delay (accuracy)\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:25:39', '2024-07-18 11:25:39'),
(413, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 333, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Personal data shall be kept in a form which permits identification of data subjects for no longer than is necessary for the purposes for which the personal data are processed; personal data may be stored for longer periods insofar as the personal data will be processed solely for archiving purposes in the public interest, scientific or historical research purposes or statistical purposes in accordance with Article 89(1) subject to implementation of the appropriate technical and organisational measures required by this Regulation in order to safeguard the rights and freedoms of the data subject\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:25:47', '2024-07-18 11:25:47'),
(414, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 334, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Personal data shall be processed in a manner that ensures appropriate security of the personal data, including protection against unauthorised or unlawful processing and against accidental loss, destruction or damage, using appropriate technical or organisational measures (integrity and confidentiality)\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:25:48', '2024-07-18 11:25:48'),
(415, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 335, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller shall be responsible for, and be able to demonstrate compliance with, paragraph 1 (accountability)\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:26:05', '2024-07-18 11:26:05'),
(416, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 336, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing shall be lawful only if and to the extent that the data subject has given consent to the processing of his or her personal data for one or more specific purposes\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:26:06', '2024-07-18 11:26:06'),
(417, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 337, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing shall be lawful only if and to the extent that processing is necessary for the performance of a contract to which the data subject is party or in order to take steps at the request of the data subject prior to entering into a contract\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:26:08', '2024-07-18 11:26:08'),
(418, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 338, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing shall be lawful only if and to the extent that processing is necessary for compliance with a legal obligation to which the controller is subject\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:26:09', '2024-07-18 11:26:09'),
(419, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 339, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing shall be lawful only if and to the extent that processing is necessary in order to protect the vital interests of the data subject or of another natural person\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:26:10', '2024-07-18 11:26:10'),
(420, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 340, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing shall be lawful only if and to the extent that processing is necessary for the performance of a task carried out in the public interest or in the exercise of official authority vested in the controller\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:26:14', '2024-07-18 11:26:14'),
(421, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 341, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing shall be lawful only if and to the extent processing is necessary for the purposes of the legitimate interests pursued by the controller or by a third party, except where such interests are overridden by the interests or fundamental rights and freedoms of the data subject which require protection of personal data, in particular where the data subject is a child. Point (f) of the first subparagraph shall not apply to processing carried out by public authorities in the performance of their tasks.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:26:17', '2024-07-18 11:26:17'),
(422, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 342, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Member States may maintain or introduce more specific provisions to adapt the application of the rules of this Regulation with regard to processing for compliance with points (c) and (e) of paragraph 1 by determining more precisely specific requirements for the processing and other measures to ensure lawful and fair processing including for other specific processing situations as provided for in Chapter IX.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:26:19', '2024-07-18 11:26:19'),
(423, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 343, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The basis for the processing referred to in point (c) and (e) of paragraph 1 shall be laid down by a. Union law; b. Member State law to which the controller is subject. The purpose of the processing shall be determined in that legal basis or, as regards the processing referred to in point (e) of paragraph 1, shall be necessary for the performance of a task carried out in the public interest or in the exercise of official authority vested in the controller. That legal basis may contain specific provisions to adapt the application of rules of this Regulation, inter aliathe general conditions governing the lawfulness of processing by the controller; the types of data which are subject to the processing; the data subjects concerned; the entities to, and the purposes for which, the personal data may be disclosed; the purpose limitation; storage periods; and processing operations and processing procedures, including measures to ensure lawful and fair processing such as those for other specific processing situations as provided for in Chapter IX. The Union or the Member State law shall meet an objective of public interest and be proportionate to the legitimate aim pursued.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:26:22', '2024-07-18 11:26:22'),
(424, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 344, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where the processing for a purpose other than that for which the personal data have been collected is not based on the data subject\\u2019s consent or on a Union or Member State law which constitutes a necessary and proportionate measure in a democratic society to safeguard the objectives referred to in Article 23(1), the controller shall, in order to ascertain whether processing for another purpose is compatible with the purpose for which the personal data are initially collected, take into account, inter alia any link between the purposes for which the personal data have been collected and the purposes of the intended further processing\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:26:25', '2024-07-18 11:26:25'),
(425, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 345, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where the processing for a purpose other than that for which the personal data have been collected is not based on the data subject\\u2019s consent or on a Union or Member State law which constitutes a necessary and proportionate measure in a democratic society to safeguard the objectives referred to in Article 23(1), the controller shall, in order to ascertain whether processing for another purpose is compatible with the purpose for which the personal data are initially collected, take into account, inter alia the context in which the personal data have been collected, in particular regarding the relationship between data subjects and the controller\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:26:27', '2024-07-18 11:26:27'),
(426, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 346, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where the processing for a purpose other than that for which the personal data have been collected is not based on the data subject\\u2019s consent or on a Union or Member State law which constitutes a necessary and proportionate measure in a democratic society to safeguard the objectives referred to in Article 23(1), the controller shall, in order to ascertain whether processing for another purpose is compatible with the purpose for which the personal data are initially collected, take into account, inter alia the nature of the personal data, in particular whether special categories of personal data are processed, pursuant to Article 9, or whether personal data related to criminal convictions and offences are processed, pursuant to Article 10\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:26:33', '2024-07-18 11:26:33'),
(427, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 347, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where the processing for a purpose other than that for which the personal data have been collected is not based on the data subject\\u2019s consent or on a Union or Member State law which constitutes a necessary and proportionate measure in a democratic society to safeguard the objectives referred to in Article 23(1), the controller shall, in order to ascertain whether processing for another purpose is compatible with the purpose for which the personal data are initially collected, take into account, inter alia the possible consequences of the intended further processing for data subjects\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:26:34', '2024-07-18 11:26:34'),
