(429, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 349, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where processing is based on consent, the controller shall be able to demonstrate that the data subject has consented to processing of his or her personal data.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:26:40', '2024-07-18 11:26:40'),
(430, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 350, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"If the data subject\\u2019s consent is given in the context of a written declaration which also concerns other matters, the request for consent shall be presented in a manner which is clearly distinguishable from the other matters, in an intelligible and easily accessible form, using clear and plain language. Any part of such a declaration which constitutes an infringement of this Regulation shall not be binding.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:26:41', '2024-07-18 11:26:41'),
(431, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 351, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data subject shall have the right to withdraw his or her consent at any time. The withdrawal of consent shall not affect the lawfulness of processing based on consent before its withdrawal. Prior to giving consent, the data subject shall be informed thereof. It shall be as easy to withdraw as to give consent.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:26:42', '2024-07-18 11:26:42'),
(432, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 352, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"When assessing whether consent is freely given, utmost account shall be taken of whether, inter alia, the performance of a contract, including the provision of a service, is conditional on consent to the processing of personal data that is not necessary for the performance of that contract.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:26:51', '2024-07-18 11:26:51'),
(433, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 353, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where point (a) of Article 6(1) applies, in relation to the offer of information society services directly to a child, the processing of the personal data of a child shall be lawful where the child is at least 16 years old. Where the child is below the age of 16 years, such processing shall be lawful only if and to the extent that consent is given or authorised by the holder of parental responsibility over the child.Member States may provide by law for a lower age for those purposes provided that such lower age is not below 13 years.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:27:18', '2024-07-18 11:27:18'),
(434, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 354, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller shall make reasonable efforts to verify in such cases that consent is given or authorised by the holder of parental responsibility over the child, taking into consideration available technology.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:27:19', '2024-07-18 11:27:19'),
(435, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 355, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Paragraph 1 shall not affect the general contract law of Member States such as the rules on the validity, formation or effect of a contract in relation to a child.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:27:22', '2024-07-18 11:27:22'),
(436, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 356, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing of personal data revealing racial or ethnic origin, political opinions, religious or philosophical beliefs, or trade union membership, and the processing of genetic data, biometric data for the purpose of uniquely identifying a natural person, data concerning health or data concerning a natural person\\u2019s sex life or sexual orientation shall be prohibited.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:27:23', '2024-07-18 11:27:23'),
(437, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 357, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Paragraph 1 shall not apply if the data subject has given explicit consent to the processing of those personal data for one or more specified purposes, except where Union or Member State law provide that the prohibition referred to in paragraph 1 may not be lifted by the data subject\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:27:33', '2024-07-18 11:27:33'),
(438, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 358, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Paragraph 1 shall not apply if processing is necessary for the purposes of carrying out the obligations and exercising specific rights of the controller or of the data subject in the field of employment and social security and social protection law in so far as it is authorised by Union or Member State law or a collective agreement pursuant to Member State law providing for appropriate safeguards for the fundamental rights and the interests of the data subject\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:27:34', '2024-07-18 11:27:34'),
(439, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 359, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Paragraph 1 shall not apply if processing is necessary to protect the vital interests of the data subject or of another natural person where the data subject is physically or legally incapable of giving consent\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:27:35', '2024-07-18 11:27:35'),
(440, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 360, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Paragraph 1 shall not apply if processing is carried out in the course of its legitimate activities with appropriate safeguards by a foundation, association or any other not-for-profit body with a political, philosophical, religious or trade union aim and on condition that the processing relates solely to the members or to former members of the body or to persons who have regular contact with it in connection with its purposes and that the personal data are not disclosed outside that body without the consent of the data subjects\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:27:37', '2024-07-18 11:27:37'),
(441, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 361, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Paragraph 1 shall not apply if processing relates to personal data which are manifestly made public by the data subject; processing is necessary for the establishment, exercise or defence of legal claims or whenever courts are acting in their judicial capacity\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 11:27:43', '2024-07-18 11:27:43'),
