(451, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 369, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing of personal data relating to criminal convictions and offences or related security measures based on Article 6(1) shall be carried out only under the control of official authority or when the processing is authorised by Union or Member State law providing for appropriate safeguards for the rights and freedoms of data subjects.Any comprehensive register of criminal convictions shall be kept only under the control of official authority\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 13:01:26', '2024-07-18 13:01:26'),
(452, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 370, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"If the purposes for which a controller processes personal data do not or do no longer require the identification of a data subject by the controller, the controller shall not be obliged to maintain, acquire or process additional information in order to identify the data subject for the sole purpose of complying with this Regulation.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 13:01:27', '2024-07-18 13:01:27'),
(453, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 371, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where, in cases referred to in paragraph 1 of this Article, the controller is able to demonstrate that it is not in a position to identify the data subject, the controller shall inform the data subject accordingly, if possible.In such cases, Articles 15 to 20 shall not apply except where the data subject, for the purpose of exercising his or her rights under those articles, provides additional information enabling his or her identification.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-18 13:01:28', '2024-07-18 13:01:28'),
