(556, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 456, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data subject shall have the right to object, on grounds relating to his or her particular situation, at any time to processing of personal data concerning him or her which is based on point (e) or (f) of Article 6(1), including profiling based on those provisions. The controller shall no longer process the personal data unless the controller demonstrates compelling legitimate grounds for the processing which override the interests, rights and freedoms of the data subject or for the establishment, exercise or defence of legal claims.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-19 11:13:51', '2024-07-19 11:13:51'),
(557, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 457, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where personal data are processed for direct marketing purposes, the data subject shall have the right to object at any time to processing of personal data concerning him or her for such marketing, which includes profiling to the extent that it is related to such direct marketing.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-19 11:13:53', '2024-07-19 11:13:53'),
