(559, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 459, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"At the latest at the time of the first communication with the data subject, the right referred to in paragraphs 1 and 2 shall be explicitly brought to the attention of the data subject and shall be presented clearly and separately from any other information.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-19 11:17:09', '2024-07-19 11:17:09'),
(560, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 460, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In the context of the use of information society services, and notwithstanding Directive 2002\\/58\\/EC, the data subject may exercise his or her right to object by automated means using technical specifications.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-19 11:17:12', '2024-07-19 11:17:12'),
(561, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 461, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where personal data are processed for scientific or historical research purposes or statistical purposes pursuant to Article 89(1), the data subject, on grounds relating to his or her particular situation, shall have the right to object to processing of personal data concerning him or her, unless the processing is necessary for the performance of a task carried out for reasons of public interest.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-19 11:17:12', '2024-07-19 11:17:12'),
