(610, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 503, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing by a processor shall be governed by a contract or other legal act under Union or Member State law, that is binding on the processor with regard to the controller and that sets out the subject-matter and duration of the processing, the nature and purpose of the processing, the type of personal data and categories of data subjects and the obligations and rights of the controller. That contract or other legal act shall stipulate, in particular, that the processor processes the personal data only on documented instructions from the controller, including with regard to transfers of personal data to a third country or an international organisation, unless required to do so by Union or Member State law to which the processor is subject; in such a case, the processor shall inform the controller of that legal requirement before processing, unless that law prohibits such information on important grounds of public interest\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:51:33', '2024-07-22 18:51:33'),
(611, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 504, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing by a processor shall be governed by a contract or other legal act under Union or Member State law, that is binding on the processor with regard to the controller and that sets out the subject-matter and duration of the processing, the nature and purpose of the processing, the type of personal data and categories of data subjects and the obligations and rights of the controller. That contract or other legal act shall stipulate, in particular, that the processor ensures that persons authorised to process the personal data have committed themselves to confidentiality or are under an appropriate statutory obligation of confidentiality\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:51:35', '2024-07-22 18:51:35'),
(612, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 505, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing by a processor shall be governed by a contract or other legal act under Union or Member State law, that is binding on the processor with regard to the controller and that sets out the subject-matter and duration of the processing, the nature and purpose of the processing, the type of personal data and categories of data subjects and the obligations and rights of the controller. That contract or other legal act shall stipulate, in particular, that the processor takes all measures required pursuant to Article 32\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:51:38', '2024-07-22 18:51:38'),
(613, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 506, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing by a processor shall be governed by a contract or other legal act under Union or Member State law, that is binding on the processor with regard to the controller and that sets out the subject-matter and duration of the processing, the nature and purpose of the processing, the type of personal data and categories of data subjects and the obligations and rights of the controller. That contract or other legal act shall stipulate, in particular, that the processor respects the conditions referred to in paragraphs 2 and 4 for engaging another processor\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:51:42', '2024-07-22 18:51:42'),
(614, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 507, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing by a processor shall be governed by a contract or other legal act under Union or Member State law, that is binding on the processor with regard to the controller and that sets out the subject-matter and duration of the processing, the nature and purpose of the processing, the type of personal data and categories of data subjects and the obligations and rights of the controller. That contract or other legal act shall stipulate, in particular, that the processor taking into account the nature of the processing, assists the controller by appropriate technical and organisational measures, insofar as this is possible, for the fulfilment of the controller\\u2019s obligation to respond to requests for exercising the data subject\\u2019s rights laid down in Chapter III\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:51:43', '2024-07-22 18:51:43'),
(615, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 508, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing by a processor shall be governed by a contract or other legal act under Union or Member State law, that is binding on the processor with regard to the controller and that sets out the subject-matter and duration of the processing, the nature and purpose of the processing, the type of personal data and categories of data subjects and the obligations and rights of the controller. That contract or other legal act shall stipulate, in particular, that the processor assists the controller in ensuring compliance with the obligations pursuant to Articles 32 to 36 taking into account the nature of processing and the information available to the processor\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:51:45', '2024-07-22 18:51:45'),
(616, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 509, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing by a processor shall be governed by a contract or other legal act under Union or Member State law, that is binding on the processor with regard to the controller and that sets out the subject-matter and duration of the processing, the nature and purpose of the processing, the type of personal data and categories of data subjects and the obligations and rights of the controller. That contract or other legal act shall stipulate, in particular, that the processor at the choice of the controller, deletes or returns all the personal data to the controller after the end of the provision of services relating to processing, and deletes existing copies unless Union or Member State law requires storage of the personal data\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:51:48', '2024-07-22 18:51:48'),
(617, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 510, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing by a processor shall be governed by a contract or other legal act under Union or Member State law, that is binding on the processor with regard to the controller and that sets out the subject-matter and duration of the processing, the nature and purpose of the processing, the type of personal data and categories of data subjects and the obligations and rights of the controller. That contract or other legal act shall stipulate, in particular, that the processor makes available to the controller all information necessary to demonstrate compliance with the obligations laid down in this Article and allow for and contribute to audits, including inspections, conducted by the controller or another auditor mandated by the controller. With regard to point (h) of the first subparagraph, the processor shall immediately inform the controller if, in its opinion, an instruction infringes this Regulation or other Union or Member State data protection provisions.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:51:53', '2024-07-22 18:51:53'),
(618, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 511, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where a processor engages another processor for carrying out specific processing activities on behalf of the controller, the same data protection obligations as set out in the contract or other legal act between the controller and the processor as referred to in paragraph 3 shall be imposed on that other processor by way of a contract or other legal act under Union or Member State law, in particular providing sufficient guarantees to implement appropriate technical and organisational measures in such a manner that the processing will meet the requirements of this Regulation.Where that other processor fails to fulfil its data protection obligations, the initial processor shall remain fully liable to the controller for the performance of that other processor\\u2019s obligations.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:51:54', '2024-07-22 18:51:54'),
(619, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 512, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Adherence of a processor to an approved code of conduct as referred to in Article 40 or an approved certification mechanism as referred to in Article 42 may be used as an element by which to demonstrate sufficient guarantees as referred to in paragraphs 1 and 4 of this Article.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:51:56', '2024-07-22 18:51:56'),
(620, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 513, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Without prejudice to an individual contract between the controller and the processor, the contract or the other legal act referred to in paragraphs 3 and 4 of this Article may be based, in whole or in part, on standard contractual clauses referred to in paragraphs 7 and 8 of this Article, including when they are part of a certification granted to the controller or processor pursuant to Articles 42 and 43.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:51:59', '2024-07-22 18:51:59'),
(621, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 514, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Commission may lay down standard contractual clauses for the matters referred to in paragraph 3 and 4 of this Article and in accordance with the examination procedure referred to in Article 93(2).\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:01', '2024-07-22 18:52:01'),
(622, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 515, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A supervisory authority may adopt standard contractual clauses for the matters referred to in paragraph 3 and 4 of this Article and in accordance with the consistency mechanism referred to in Article 63.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:02', '2024-07-22 18:52:02'),
(623, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 516, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The contract or the other legal act referred to in paragraphs 3 and 4 shall be in writing, including in electronic form.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:05', '2024-07-22 18:52:05'),
(624, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 517, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Without prejudice to Articles 82, 83 and 84, if a processor infringes this Regulation by determining the purposes and means of processing, the processor shall be considered to be a controller in respect of that processing.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:06', '2024-07-22 18:52:06');
INSERT INTO `activity_log` (`id`, `log_name`, `description`, `subject_type`, `event`, `subject_id`, `causer_type`, `causer_id`, `properties`, `batch_uuid`, `created_at`, `updated_at`) VALUES
(625, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 518, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The processor and any person acting under the authority of the controller or of the processor, who has access to personal data, shall not process those data except on instructions from the controller, unless required to do so by Union or Member State law.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:07', '2024-07-22 18:52:07'),
(626, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 519, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Each controller and, where applicable, the controller\\u2019s representative, shall maintain a record of processing activities under its responsibility. That record shall contain the name and contact details of the controller and, where applicable, the joint controller, the controller\\u2019s representative and the data protection officer\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:09', '2024-07-22 18:52:09'),
(627, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 520, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Each controller and, where applicable, the controller\\u2019s representative, shall maintain a record of processing activities under its responsibility. That record shall contain the purposes of the processing\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:10', '2024-07-22 18:52:10'),
(628, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 521, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Each controller and, where applicable, the controller\\u2019s representative, shall maintain a record of processing activities under its responsibility. That record shall contain a description of the categories of data subjects and of the categories of personal data\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:13', '2024-07-22 18:52:13'),
(629, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 522, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Each controller and, where applicable, the controller\\u2019s representative, shall maintain a record of processing activities under its responsibility. That record shall contain the categories of recipients to whom the personal data have been or will be disclosed including recipients in third countries or international organisations\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:14', '2024-07-22 18:52:14'),
(630, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 523, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Each controller and, where applicable, the controller\\u2019s representative, shall maintain a record of processing activities under its responsibility. That record shall contain applicable transfers of personal data to a third country or an international organisation, including the identification of that third country or international organisation and, in the case of transfers referred to in the second subparagraph of Article 49(1), the documentation of suitable safeguards\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:15', '2024-07-22 18:52:15'),
(631, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 524, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Each controller and, where applicable, the controller\\u2019s representative, shall maintain a record of processing activities under its responsibility. That record shall contain all of the following information where possible, the envisaged time limits for erasure of the different categories of data\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:17', '2024-07-22 18:52:17'),
(632, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 525, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Each controller and, where applicable, the controller\\u2019s representative, shall maintain a record of processing activities under its responsibility. That record shall contain a general description of the technical and organisational security measures referred to in Article 32(1).\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:20', '2024-07-22 18:52:20'),
(633, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 526, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Each processor and, where applicable, the processor\\u2019s representative shall maintain a record of all categories of processing activities carried out on behalf of a controller, containing the name and contact details of the processor or processors and of each controller on behalf of which the processor is acting, and, where applicable, of the controller\\u2019s or the processor\\u2019s representative, and the data protection officer\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:21', '2024-07-22 18:52:21'),
(634, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 527, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Each processor and, where applicable, the processor\\u2019s representative shall maintain a record of all categories of processing activities carried out on behalf of a controller, containing the categories of processing carried out on behalf of each controller\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:23', '2024-07-22 18:52:23'),
(635, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 528, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Each processor and, where applicable, the processor\\u2019s representative shall maintain a record of all categories of processing activities carried out on behalf of a controller, containing where applicable, transfers of personal data to a third country or an international organisation, including the identification of that third country or international organisation and, in the case of transfers referred to in the second subparagraph of Article 49(1), the documentation of suitable safeguards\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:24', '2024-07-22 18:52:24'),
(636, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 529, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Each processor and, where applicable, the processor\\u2019s representative shall maintain a record of all categories of processing activities carried out on behalf of a controller, containing where possible, a general description of the technical and organisational security measures referred to in Article 32(1).\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:26', '2024-07-22 18:52:26'),
(637, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 530, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The records referred to in paragraphs 1 and 2 shall be in writing, including in electronic form.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:27', '2024-07-22 18:52:27'),
(638, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 531, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller or the processor and, where applicable, the controller\\u2019s or the processor\\u2019s representative, shall make the record available to the supervisory authority on request.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:29', '2024-07-22 18:52:29'),
(639, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 532, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The obligations referred to in paragraphs 1 and 2 shall not apply to an enterprise or an organisation employing fewer than 250 persons unless the processing it carries out is likely to result in a risk to the rights and freedoms of data subjects, the processing is not occasional, or the processing includes special categories of data as referred to in Article 9(1) or personal data relating to criminal convictions and offences referred to in Article 10.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:30', '2024-07-22 18:52:30'),
(640, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 533, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller and the processor and, where applicable, their representatives, shall cooperate, on request, with the supervisory authority in the performance of its tasks.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:32', '2024-07-22 18:52:32'),
(641, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 534, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Taking into account the state of the art, the costs of implementation and the nature, scope, context and purposes of processing as well as the risk of varying likelihood and severity for the rights and freedoms of natural persons, the controller and the processor shall implement appropriate - technical and organisational measures to ensure a level of security appropriate - to the risk, including inter alia as appropriate - the pseudonymisation and encryption of personal data\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:33', '2024-07-22 18:52:33'),
(642, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 535, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Taking into account the state of the art, the costs of implementation and the nature, scope, context and purposes of processing as well as the risk of varying likelihood and severity for the rights and freedoms of natural persons, the controller and the processor shall implement appropriate - technical and organisational measures to ensure a level of security appropriate - to the risk, including inter alia as appropriate - the ability to ensure the ongoing confidentiality, integrity, availability and resilience of processing systems and services\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:34', '2024-07-22 18:52:34'),
(643, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 536, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Taking into account the state of the art, the costs of implementation and the nature, scope, context and purposes of processing as well as the risk of varying likelihood and severity for the rights and freedoms of natural persons, the controller and the processor shall implement appropriate - technical and organisational measures to ensure a level of security appropriate - to the risk, including inter alia as appropriate - the ability to restore the availability and access to personal data in a timely manner in the event of a physical or technical incident\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:36', '2024-07-22 18:52:36'),
(644, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 537, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Taking into account the state of the art, the costs of implementation and the nature, scope, context and purposes of processing as well as the risk of varying likelihood and severity for the rights and freedoms of natural persons, the controller and the processor shall implement appropriate - technical and organisational measures to ensure a level of security appropriate - to the risk, including inter alia as appropriate - a process for regularly testing, assessing and evaluating the effectiveness of technical and organisational measures for ensuring the security of the processing.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:37', '2024-07-22 18:52:37'),
(645, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 538, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In assessing the appropriate level of security account shall be taken in particular of the risks that are presented by processing, in particular from accidental or unlawful destruction, loss, alteration, unauthorised disclosure of, or access to personal data transmitted, stored or otherwise processed.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:39', '2024-07-22 18:52:39'),
(646, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 539, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Adherence to an approved code of conduct as referred to in Article 40 or an approved certification mechanism as referred to in Article 42 may be used as an element by which to demonstrate compliance with the requirements set out in paragraph 1 of this Article.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:40', '2024-07-22 18:52:40'),
(647, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 540, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller and processor shall take steps to ensure that any natural person acting under the authority of the controller or the processor who has access to personal data does not process them except on instructions from the controller, unless he or she is required to do so by Union or Member State law.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:43', '2024-07-22 18:52:43'),
(648, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 541, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In the case of a personal data breach, the controller shall without undue delay and, where feasible, not later than 72 hours after having become aware of it, notify the personal data breach to the supervisory authority competent in accordance with Article 55, unless the personal data breach is unlikely to result in a risk to the rights and freedoms of natural persons.Where the notification to the supervisory authority is not made within 72 hours, it shall be accompanied by reasons for the delay.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:45', '2024-07-22 18:52:45'),
(649, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 542, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The processor shall notify the controller without undue delay after becoming aware of a personal data breach.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:47', '2024-07-22 18:52:47'),
(650, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 543, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The notification referred to in paragraph 1 shall at least describe the nature of the personal data breach including where possible, the categories and approximate number of data subjects concerned and the categories and approximate number of personal data records concerned;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:48', '2024-07-22 18:52:48'),
(651, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 544, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The notification referred to in paragraph 1 shall at least communicate the name and contact details of the data protection officer or other contact point where more information can be obtained\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:49', '2024-07-22 18:52:49'),
(652, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 545, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The notification referred to in paragraph 1 shall at least describe the likely consequences of the personal data breach\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:51', '2024-07-22 18:52:51'),
(653, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 546, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The notification referred to in paragraph 1 shall at least describe the measures taken or proposed to be taken by the controller to address the personal data breach, including, where appropriate, measures to mitigate its possible adverse effects.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:53', '2024-07-22 18:52:53'),
(654, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 547, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where, and in so far as, it is not possible to provide the information at the same time, the information may be provided in phases without undue further delay.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:55', '2024-07-22 18:52:55'),
(655, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 548, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller shall document any personal data breaches, comprising the facts relating to the personal data breach, its effects and the remedial action taken.That documentation shall enable the supervisory authority to verify compliance with this Article.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:58', '2024-07-22 18:52:58'),
(656, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 549, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"When the personal data breach is likely to result in a high risk to the rights and freedoms of natural persons, the controller shall communicate the personal data breach to the data subject without undue delay.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:52:59', '2024-07-22 18:52:59'),
(657, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 550, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The communication to the data subject referred to in paragraph 1 of this Article shall describe in clear and plain language the nature of the personal data breach and contain at least the information and measures referred to in points (b), (c) and (d) of Article 33(3).\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:53:00', '2024-07-22 18:53:00'),
(658, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 551, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The communication to the data subject referred to in paragraph 1 shall not be required the controller has implemented appropriate technical and organisational protection measures, and those measures were applied to the personal data affected by the personal data breach, in particular those that render the personal data unintelligible to any person who is not authorised to access it, such as encryption\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:53:03', '2024-07-22 18:53:03'),
(659, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 552, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The communication to the data subject referred to in paragraph 1 shall not be required the controller has taken subsequent measures which ensure that the high risk to the rights and freedoms of data subjects referred to in paragraph 1 is no longer likely to materialise\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:53:04', '2024-07-22 18:53:04'),
(660, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 553, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"iThe communication to the data subject referred to in paragraph 1 shall not be required t would involve disproportionate effort. In such a case, there shall instead be a public communication or similar measure whereby the data subjects are informed in an equally effective manner.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:53:05', '2024-07-22 18:53:05'),
(661, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 554, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"If the controller has not already communicated the personal data breach to the data subject, the supervisory authority, having considered the likelihood of the personal data breach resulting in a high risk, may require it to do so or may decide that any of the conditions referred to in paragraph 3 are met.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:53:06', '2024-07-22 18:53:06'),
(662, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 555, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where a type of processing in particular using new technologies, and taking into account the nature, scope, context and purposes of the processing, is likely to result in a high risk to the rights and freedoms of natural persons, the controller shall, prior to the processing, carry out an assessment of the impact of the envisaged processing operations on the protection of personal data.A single assessment may address a set of similar processing operations that present similar high risks\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:53:08', '2024-07-22 18:53:08'),
(663, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 556, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller shall seek the advice of the data protection officer, where designated, when carrying out a data protection impact assessment.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:53:09', '2024-07-22 18:53:09'),
(664, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 557, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A data protection impact assessment referred to in paragraph 1 shall in particular be required in the case of a systematic and extensive evaluation of personal aspects relating to natural persons which is based on automated processing, including profiling, and on which decisions are based that produce legal effects concerning the natural person or similarly significantly affect the natural person\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:53:10', '2024-07-22 18:53:10'),
(665, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 558, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A data protection impact assessment referred to in paragraph 1 shall in particular be required in the case of processing on a large scale of special categories of data referred to in Article 9(1), or of personal data relating to criminal convictions and offences referred to in Article 10 or\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:53:13', '2024-07-22 18:53:13'),
(666, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 559, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A data protection impact assessment referred to in paragraph 1 shall in particular be required in the case of a systematic monitoring of a publicly accessible area on a large scale.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:53:14', '2024-07-22 18:53:14'),
(667, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 560, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The supervisory authority may also establish and make public a list of the kind of processing operations for which no data protection impact assessment is required. The supervisory authority shall communicate those lists to the Board.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:53:17', '2024-07-22 18:53:17'),
(668, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 561, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The supervisory authority shall establish and make public a list of the kind of processing operations which are subject to the requirement for a data protection impact assessment pursuant to paragraph 1.The supervisory authority shall communicate those lists to the Board referred to in Article 68\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:53:18', '2024-07-22 18:53:18'),
(669, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 562, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Prior to the adoption of the lists referred to in paragraphs 4 and 5, the competent supervisory authority shall apply the consistency mechanism referred to in Article 63 where such lists involve processing activities which are related to the offering of goods or services to data subjects or to the monitoring of their behaviour in several Member States, or may substantially affect the free movement of personal data within the Union.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:53:20', '2024-07-22 18:53:20'),
(670, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 563, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The assessment shall contain a systematic description of the envisaged processing operations and the purposes of the processing, including, where applicable, the legitimate interest pursued by the controller\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:53:21', '2024-07-22 18:53:21'),
(671, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 564, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The assessment shall contain an assessment of the necessity and proportionality of the processing operations in relation to the purposes\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-22 18:53:23', '2024-07-22 18:53:23'),
(672, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 565, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The assessment shall contain an assessment of the risks to the rights and freedoms of data subjects referred to in paragraph 1 and\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:41:49', '2024-07-23 17:41:49'),
(673, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 566, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The assessment shall contain the measures envisaged to address the risks, including safeguards, security measures and mechanisms to ensure the protection of personal data and to demonstrate compliance with this Regulation taking into account the rights and legitimate interests of data subjects and other persons concerned.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:41:55', '2024-07-23 17:41:55'),
(674, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 567, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Compliance with approved codes of conduct referred to in Article 40 by the relevant controllers or processors shall be taken into due account in assessing the impact of the processing operations performed by such controllers or processors, in particular for the purposes of a data protection impact assessment.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:41:57', '2024-07-23 17:41:57'),
(675, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 568, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where appropriate, the controller shall seek the views of data subjects or their representatives on the intended processing, without prejudice to the protection of commercial or public interests or the security of processing operations.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:41:58', '2024-07-23 17:41:58'),
(676, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 569, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where processing pursuant to point (c) or (e) of Article 6(1) has a legal basis in Union law or in the law of the Member State to which the controller is subject, that law regulates the specific processing operation or set of operations in question, and a data protection impact assessment has already been carried out as part of a general impact assessment in the context of the adoption of that legal basis, paragraphs 1 to 7 shall not apply unless Member States deem it to be necessary to carry out such an assessment prior to processing activities.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:42:00', '2024-07-23 17:42:00'),
(677, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 570, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where necessary, the controller shall carry out a review to assess if processing is performed in accordance with the data protection impact assessment at least when there is a change of the risk represented by processing operations.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:42:03', '2024-07-23 17:42:03'),
(678, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 571, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller shall consult the supervisory authority prior to processing where a data protection impact assessment under Article 35 indicates that the processing would result in a high risk in the absence of measures taken by the controller to mitigate the risk.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:42:06', '2024-07-23 17:42:06'),
(679, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 572, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where the supervisory authority is of the opinion that the intended processing referred to in paragraph 1 would infringe this Regulation, in particular where the controller has insufficiently identified or mitigated the risk, the supervisory authority shall, within period of up to eight weeks of receipt of the request for consultation, provide written advice to the controller and, where applicable to the processor, and may use any of its powers referred to in Article 58.That period may be extended by six weeks, taking into account the complexity of the intended processing.The supervisory authority shall inform the controller and, where applicable, the processor, of any such extension within one month of receipt of the request for consultation together with the reasons for the delay.Those periods may be suspended until the supervisory authority has obtained information it has requested for the purposes of the consultation.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:42:13', '2024-07-23 17:42:13'),
(680, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 573, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"When consulting the supervisory authority pursuant to paragraph 1, the controller shall provide the supervisory authority with where applicable, the respective responsibilities of the controller, joint controllers and processors involved in the processing, in particular for processing within a group of undertakings\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:42:14', '2024-07-23 17:42:14'),
(681, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 574, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"When consulting the supervisory authority pursuant to paragraph 1, the controller shall provide the supervisory authority with the purposes and means of the intended processing\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:42:16', '2024-07-23 17:42:16'),
(682, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 575, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"When consulting the supervisory authority pursuant to paragraph 1, the controller shall provide the supervisory authority with the measures and safeguards provided to protect the rights and freedoms of data subjects pursuant to this Regulation\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:42:29', '2024-07-23 17:42:29'),
(683, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 576, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"When consulting the supervisory authority pursuant to paragraph 1, the controller shall provide the supervisory authority with where applicable, the contact details of the data protection officer\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:42:30', '2024-07-23 17:42:30'),
(684, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 577, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"When consulting the supervisory authority pursuant to paragraph 1, the controller shall provide the supervisory authority with the data protection impact assessment provided for in Article 35 and\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:42:33', '2024-07-23 17:42:33'),
(685, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 578, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"When consulting the supervisory authority pursuant to paragraph 1, the controller shall provide the supervisory authority with any other information requested by the supervisory authority.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:42:34', '2024-07-23 17:42:34'),
(686, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 579, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Member States shall consult the supervisory authority during the preparation of a proposal for a legislative measure to be adopted by a national parliament, or of a regulatory measure based on such a legislative measure, which relates to processing.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:42:37', '2024-07-23 17:42:37'),
(687, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 580, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Notwithstanding paragraph 1, Member State law may require controllers to consult with, and obtain prior authorisation from, the supervisory authority in relation to processing by a controller for the performance of a task carried out by the controller in the public interest, including processing in relation to social protection and public health.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:42:42', '2024-07-23 17:42:42'),
(688, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 581, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller and the processor shall designate a data protection officer in any case where the processing is carried out by a public authority or body, except for courts acting in their judicial capacity\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:42:43', '2024-07-23 17:42:43'),
(689, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 582, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller and the processor shall designate a data protection officer in any case where the core activities of the controller or the processor consist of processing operations which, by virtue of their nature, their scope and\\/or their purposes, require regular and systematic monitoring of data subjects on a large scale or\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:42:47', '2024-07-23 17:42:47'),
(690, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 583, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller and the processor shall designate a data protection officer in any case where the core activities of the controller or the processor consist of processing on a large scale of special categories of data pursuant to Article 9 or personal data relating to criminal convictions and offences referred to in Article 10.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:42:48', '2024-07-23 17:42:48'),
(691, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 584, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A group of undertakings may appoint a single data protection officer provided that a data protection officer is easily accessible from each establishment.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:42:50', '2024-07-23 17:42:50'),
(692, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 585, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where the controller or the processor is a public authority or body, a single data protection officer may be designated for several such authorities or bodies, taking account of their organisational structure and size.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:42:51', '2024-07-23 17:42:51'),
(693, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 586, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In cases other than those referred to in paragraph 1, the controller or processor or associations and other bodies representing categories of controllers or processors may or, where required by Union or Member State law shall, designate a data protection officer.The data protection officer may act for such associations and other bodies representing controllers or processors.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:42:51', '2024-07-23 17:42:51'),
(694, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 587, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data protection officer shall be designated on the basis of professional qualities and, in particular, expert knowledge of data protection law and practices and the ability to fulfil the tasks referred to in Article 39.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:42:54', '2024-07-23 17:42:54'),
(695, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 588, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data protection officer may be a staff member of the controller or processor, or fulfil the tasks on the basis of a service contract.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:42:55', '2024-07-23 17:42:55'),
(696, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 589, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller or the processor shall publish the contact details of the data protection officer and communicate them to the supervisory authority.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:02', '2024-07-23 17:43:02'),
(697, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 590, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller and the processor shall ensure that the data protection officer is involved, properly and in a timely manner, in all issues which relate to the protection of personal data.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:03', '2024-07-23 17:43:03'),
(698, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 591, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller and processor shall support the data protection officer in performing the tasks referred to in Article 39 by providing resources necessary to carry out those tasks and access to personal data and processing operations, and to maintain his or her expert knowledge.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:04', '2024-07-23 17:43:04'),
(699, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 592, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller and processor shall ensure that the data protection officer does not receive any instructions regarding the exercise of those tasks.He or she shall not be dismissed or penalised by the controller or the processor for performing his tasks.The data protection officer shall directly report to the highest management level of the controller or the processor.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:05', '2024-07-23 17:43:05'),
(700, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 593, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Data subjects may contact the data protection officer with regard to all issues related to processing of their personal data and to the exercise of their rights under this Regulation.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:07', '2024-07-23 17:43:07'),
(701, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 594, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data protection officer shall be bound by secrecy or confidentiality concerning the performance of his or her tasks, in accordance with Union or Member State law.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:09', '2024-07-23 17:43:09'),
(702, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 595, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data protection officer may fulfil other tasks and duties.The controller or processor shall ensure that any such tasks and duties do not result in a conflict of interests.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:16', '2024-07-23 17:43:16'),
(703, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 596, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data protection officer shall have to inform and advise the controller or the processor and the employees who carry out processing of their obligations pursuant to this Regulation and to other Union or Member State data protection provisions\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:17', '2024-07-23 17:43:17'),
(704, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 597, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data protection officer shall have to monitor compliance with this Regulation, with other Union or Member State data protection provisions and with the policies of the controller or processor in relation to the protection of personal data, including the assignment of responsibilities, awareness-raising and training of staff involved in processing operations, and the related audits\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:19', '2024-07-23 17:43:19'),
(705, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 598, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data protection officer shall have to provide advice where requested as regards the data protection impact assessment and monitor its performance pursuant to Article 35\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:21', '2024-07-23 17:43:21'),
(706, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 599, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data protection officer shall have to cooperate with the supervisory authority\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:23', '2024-07-23 17:43:23'),
(707, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 600, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data protection officer shall have to act as the contact point for the supervisory authority on issues relating to processing, including the prior consultation referred to in Article 36, and to consult, where appropriate, with regard to any other matter.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:32', '2024-07-23 17:43:32'),
(708, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 601, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data protection officer shall in the performance of his or her tasks have due regard to the risk associated with processing operations, taking into account the nature, scope, context and purposes of processing.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:34', '2024-07-23 17:43:34'),
(709, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 602, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Member States, the supervisory authorities, the Board and the Commission shall encourage the drawing up of codes of conduct intended to contribute to the proper application of this Regulation, taking account of the specific features of the various processing sectors and the specific needs of micro, small and medium-sized enterprises.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:35', '2024-07-23 17:43:35');
