(710, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 603, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Associations and other bodies representing categories of controllers or processors may prepare codes of conduct, or amend or extend such codes, for the purpose of specifying the application of this Regulation, such as with regard to fair and transparent processing\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:37', '2024-07-23 17:43:37'),
(711, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 604, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Associations and other bodies representing categories of controllers or processors may prepare codes of conduct, or amend or extend such codes, for the purpose of specifying the application of this Regulation, such as with regard to the legitimate interests pursued by controllers in specific contexts\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:38', '2024-07-23 17:43:38'),
(712, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 605, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Associations and other bodies representing categories of controllers or processors may prepare codes of conduct, or amend or extend such codes, for the purpose of specifying the application of this Regulation, such as with regard to the collection of personal data\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:41', '2024-07-23 17:43:41'),
(713, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 606, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Associations and other bodies representing categories of controllers or processors may prepare codes of conduct, or amend or extend such codes, for the purpose of specifying the application of this Regulation, such as with regard to the pseudonymisation of personal data\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:42', '2024-07-23 17:43:42'),
(714, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 607, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Associations and other bodies representing categories of controllers or processors may prepare codes of conduct, or amend or extend such codes, for the purpose of specifying the application of this Regulation, such as with regard to the information provided to the public and to data subjects\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:44', '2024-07-23 17:43:44'),
(715, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 608, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Associations and other bodies representing categories of controllers or processors may prepare codes of conduct, or amend or extend such codes, for the purpose of specifying the application of this Regulation, such as with regard to the exercise of the rights of data subjects\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:45', '2024-07-23 17:43:45'),
(716, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 609, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Associations and other bodies representing categories of controllers or processors may prepare codes of conduct, or amend or extend such codes, for the purpose of specifying the application of this Regulation, such as with regard to the information provided to, and the protection of, children, and the manner in which the consent of the holders of parental responsibility over children is to be obtained\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:47', '2024-07-23 17:43:47'),
(717, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 610, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Associations and other bodies representing categories of controllers or processors may prepare codes of conduct, or amend or extend such codes, for the purpose of specifying the application of this Regulation, such as with regard to the measures and procedures referred to in Articles 24 and 25 and the measures to ensure security of processing referred to in Article 32\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:49', '2024-07-23 17:43:49'),
(718, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 611, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Associations and other bodies representing categories of controllers or processors may prepare codes of conduct, or amend or extend such codes, for the purpose of specifying the application of this Regulation, such as with regard to the notification of personal data breaches to supervisory authorities and the communication of such personal data breaches to data subjects\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:51', '2024-07-23 17:43:51'),
(719, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 612, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Associations and other bodies representing categories of controllers or processors may prepare codes of conduct, or amend or extend such codes, for the purpose of specifying the application of this Regulation, such as with regard to the transfer of personal data to third countries or international organisations or\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:53', '2024-07-23 17:43:53'),
(720, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 613, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Associations and other bodies representing categories of controllers or processors may prepare codes of conduct, or amend or extend such codes, for the purpose of specifying the application of this Regulation, such as with regard to out-of-court proceedings and other dispute resolution procedures for resolving disputes between controllers and data subjects with regard to processing, without prejudice to the rights of data subjects pursuant to Articles 77 and 79.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:54', '2024-07-23 17:43:54'),
(721, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 614, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In addition to adherence by controllers or processors subject to this Regulation, codes of conduct approved pursuant to paragraph 5 of this Article and having general validity pursuant to paragraph 9 of this Article may also be adhered to by controllers or processors that are not subject to this Regulation pursuant to Article 3 in order to provide appropriate safeguards within the framework of personal data transfers to third countries or international organisations under the terms referred to in point (e) of Article 46(2).Such controllers or processors shall make binding and enforceable commitments, via contractual or other legally binding instruments, to apply those appropriate safeguards including with regard to the rights of data subjects.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 17:43:57', '2024-07-23 17:43:57'),
(722, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 615, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A code of conduct referred to in paragraph 2 of this Article shall contain mechanisms which enable the body referred to in Article 41(1) to carry out the mandatory monitoring of compliance with its provisions by the controllers or processors which undertake to apply it, without prejudice to the tasks and powers of supervisory authorities competent pursuant to Article 55 or 56.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:15:05', '2024-07-23 20:15:05'),
(723, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 616, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Associations and other bodies referred to in paragraph 2 of this Article which intend to prepare a code of conduct or to amend or extend an existing code shall submit the draft code, amendment or extension to the supervisory authority which is competent pursuant to Article 55.The supervisory authority shall provide an opinion on whether the draft code, amendment or extension complies with this Regulation and shall approve that draft code, amendment or extension if it finds that it provides sufficient appropriate safeguards.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:15:06', '2024-07-23 20:15:06'),
(724, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 617, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where the draft code, or amendment or extension is approved in accordance with paragraph 5, and where the code of conduct concerned does not relate to processing activities in several Member States, the supervisory authority shall register and publish the code.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:15:08', '2024-07-23 20:15:08'),
(725, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 618, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where a draft code of conduct relates to processing activities in several Member States, the supervisory authority which is competent pursuant to Article 55 shall, before approving the draft code, amendment or extension, submit it in the procedure referred to in Article 63 to the Board which shall provide an opinion on whether the draft code, amendment or extension complies with this Regulation or, in the situation referred to in paragraph 3 of this Article, provides appropriate safeguards.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:15:10', '2024-07-23 20:15:10'),
(726, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 619, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where the opinion referred to in paragraph 7 confirms that the draft code, amendment or extension complies with this Regulation, or, in the situation referred to in paragraph 3, provides appropriate safeguards, the Board shall submit its opinion to the Commission.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:15:15', '2024-07-23 20:15:15'),
(727, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 620, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Commission may, by way of implementing acts, decide that the approved code of conduct, amendment or extension submitted to it pursuant to paragraph 8 of this Article have general validity within the Union.Those implementing acts shall be adopted in accordance with the examination procedure set out in Article 93(2).\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:15:16', '2024-07-23 20:15:16'),
(728, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 621, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Commission shall ensure appropriate publicity for the approved codes which have been decided as having general validity in accordance with paragraph 9.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:15:18', '2024-07-23 20:15:18'),
(729, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 622, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Board shall collate all approved codes of conduct, amendments and extensions in a register and shall make them publicly available by way of appropriate means.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:15:19', '2024-07-23 20:15:19'),
(730, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 623, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Without prejudice to the tasks and powers of the competent supervisory authority under Articles 57 and 58, the monitoring of compliance with a code of conduct pursuant to Article 40 may be carried out by a body which has an appropriate level of expertise in relation to the subject-matter of the code and is accredited for that purpose by the competent supervisory authority.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:15:21', '2024-07-23 20:15:21'),
(731, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 624, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A body as referred to in paragraph 1 may be accredited to monitor compliance with a code of conduct where that body has demonstrated its independence and expertise in relation to the subject-matter of the code to the satisfaction of the competent supervisory authority\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:15:24', '2024-07-23 20:15:24'),
(732, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 625, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A body as referred to in paragraph 1 may be accredited to monitor compliance with a code of conduct where that body has established procedures which allow it to assess the eligibility of controllers and processors concerned to apply the code, to monitor their compliance with its provisions and to periodically review its operation\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:15:25', '2024-07-23 20:15:25'),
(733, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 626, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A body as referred to in paragraph 1 may be accredited to monitor compliance with a code of conduct where that body has established procedures and structures to handle complaints about infringements of the code or the manner in which the code has been, or is being, implemented by a controller or processor, and to make those procedures and structures transparent to data subjects and the public\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:15:59', '2024-07-23 20:15:59'),
(734, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 627, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A body as referred to in paragraph 1 may be accredited to monitor compliance with a code of conduct where that body has demonstrated to the satisfaction of the competent supervisory authority that its tasks and duties do not result in a conflict of interests.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:16:01', '2024-07-23 20:16:01'),
(735, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 628, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The competent supervisory authority shall submit the draft requirements for accreditation of a body as referred to in paragraph 1 of this Article to the Board pursuant to the consistency mechanism referred to in Article 63.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:16:02', '2024-07-23 20:16:02'),
(736, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 629, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Without prejudice to the tasks and powers of the competent supervisory authority and the provisions of Chapter VIII, a body as referred to in paragraph 1 of this Article shall, subject to appropriate safeguards, take appropriate action in cases of infringement of the code by a controller or processor, including suspension or exclusion of the controller or processor concerned from the code. It shall inform the competent supervisory authority of such actions and the reasons for taking them.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:16:04', '2024-07-23 20:16:04'),
(737, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 630, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The competent supervisory authority shall revoke the accreditation of a body as referred to in paragraph 1 if the requirements for accreditation are not, or are no longer, met or where actions taken by the body infringe this Regulation.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:16:06', '2024-07-23 20:16:06'),
(738, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 631, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"This Article shall not apply to processing carried out by public authorities and bodies.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:16:07', '2024-07-23 20:16:07'),
(739, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 632, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Member States, the supervisory authorities, the Board and the Commission shall encourage, in particular at Union level, the establishment of data protection certification mechanisms and of data protection seals and marks, for the purpose of demonstrating compliance with this Regulation of processing operations by controllers and processors.The specific needs of micro, small and medium-sized enterprises shall be taken into account.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:16:10', '2024-07-23 20:16:10'),
(740, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 633, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In addition to adherence by controllers or processors subject to this Regulation, data protection certification mechanisms, seals or marks approved pursuant to paragraph 5 of this Article may be established for the purpose of demonstrating the existence of appropriate safeguards provided by controllers or processors that are not subject to this Regulation pursuant to Article 3 within the framework of personal data transfers to third countries or international organisations under the terms referred to in point (f) of Article 46(2). Such controllers or processors shall make binding and enforceable commitments, via contractual or other legally binding instruments, to apply those appropriate safeguards, including with regard to the rights of data subjects.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:16:11', '2024-07-23 20:16:11'),
(741, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 634, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The certification shall be voluntary and available via a process that is transparent.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:16:14', '2024-07-23 20:16:14'),
(742, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 635, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A certification pursuant to this Article does not reduce the responsibility of the controller or the processor for compliance with this Regulation and is without prejudice to the tasks and powers of the supervisory authorities which are competent pursuant to Article 55 or 56.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:16:16', '2024-07-23 20:16:16'),
(743, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 636, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A certification pursuant to this Article shall be issued by the certification bodies referred to in Article 43 or by the competent supervisory authority, on the basis of criteria approved by that competent supervisory authority pursuant to Article 58(3) or by the Board pursuant to Article 63. Where the criteria are approved by the Board, this may result in a common certification, the European Data Protection Seal.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:16:18', '2024-07-23 20:16:18'),
(744, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 637, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller or processor which submits its processing to the certification mechanism shall provide the certification body referred to in Article 43, or where applicable, the competent supervisory authority, with all information and access to its processing activities which are necessary to conduct the certification procedure\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:16:21', '2024-07-23 20:16:21'),
(745, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 638, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Certification shall be issued to a controller or processor for a maximum period of three years and may be renewed, under the same conditions, provided that the relevant criteria continue to be met. Certification shall be withdrawn, as applicable, by the certification bodies referred to in Article 43 or by the competent supervisory authority where the criteria for the certification are not or are no longer met.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:16:23', '2024-07-23 20:16:23'),
(746, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 639, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Board shall collate all certification mechanisms and data protection seals and marks in a register and shall make them publicly available by any appropriate means.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:16:26', '2024-07-23 20:16:26'),
(747, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 640, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Without prejudice to the tasks and powers of the competent supervisory authority under Articles 57 and 58, certification bodies which have an appropriate level of expertise in relation to data protection shall, after informing the supervisory authority in order to allow it to exercise its powers pursuant to point (h) of Article 58(2) where necessary, issue and renew certification. Member States shall ensure that those certification bodies are accredited by one or both of the following the supervisory authority which is competent pursuant to Article 55 or 56\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:16:30', '2024-07-23 20:16:30'),
(748, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 641, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Without prejudice to the tasks and powers of the competent supervisory authority under Articles 57 and 58, certification bodies which have an appropriate level of expertise in relation to data protection shall, after informing the supervisory authority in order to allow it to exercise its powers pursuant to point (h) of Article 58(2) where necessary, issue and renew certification. Member States shall ensure that those certification bodies are accredited by one or both of the following the national accreditation body named in accordance with Regulation (EC) No 765\\/2008 of the European Parliament and of the Council\\u00b9 in accordance with EN-ISO\\/IEC 17065\\/2012 and with the additional requirements established by the supervisory authority which is competent pursuant to Article 55 or 56.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:16:32', '2024-07-23 20:16:32'),
(749, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 642, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Certification bodies referred to in paragraph 1 shall be accredited in accordance with that paragraph only where they have demonstrated their independence and expertise in relation to the subject-matter of the certification to the satisfaction of the competent supervisory authority\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:16:33', '2024-07-23 20:16:33'),
(750, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 643, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Certification bodies referred to in paragraph 1 shall be accredited in accordance with that paragraph only where they have undertaken to respect the criteria referred to in Article 42(5) and approved by the supervisory authority which is competent pursuant to Article 55 or 56 or by the Board pursuant to Article 63\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:16:35', '2024-07-23 20:16:35'),
(751, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 644, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Certification bodies referred to in paragraph 1 shall be accredited in accordance with that paragraph only where they have established procedures for the issuing, periodic review and withdrawal of data protection certification, seals and marks\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:17:58', '2024-07-23 20:17:58'),
(752, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 645, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Certification bodies referred to in paragraph 1 shall be accredited in accordance with that paragraph only where they have established procedures and structures to handle complaints about infringements of the certification or the manner in which the certification has been, or is being, implemented by the controller or processor, and to make those procedures and structures transparent to data subjects and the public\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:17:59', '2024-07-23 20:17:59'),
(753, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 646, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Certification bodies referred to in paragraph 1 shall be accredited in accordance with that paragraph only where they have demonstrated, to the satisfaction of the competent supervisory authority, that their tasks and duties do not result in a conflict of interests.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:18:01', '2024-07-23 20:18:01'),
(754, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 647, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The accreditation of certification bodies as referred to in paragraphs 1 and 2 of this Article shall take place on the basis of requirements approved by the supervisory authority which is competent pursuant to Article 55 or 56 or by the Board pursuant to Article 63. In the case of accreditation pursuant to point (b) of paragraph 1 of this Article, those requirements shall complement those envisaged in Regulation (EC) No 765\\/2008 and the technical rules that describe the methods and procedures of the certification bodies.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:18:02', '2024-07-23 20:18:02'),
(755, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 648, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The certification bodies referred to in paragraph 1 shall be responsible for the proper assessment leading to the certification or the withdrawal of such certification without prejudice to the responsibility of the controller or processor for compliance with this Regulation. The accreditation shall be issued for a maximum period of five years and may be renewed on the same conditions provided that the certification body meets the requirements set out in this Article.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:18:04', '2024-07-23 20:18:04'),
(756, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 649, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The certification bodies referred to in paragraph 1 shall provide the competent supervisory authorities with the reasons for granting or withdrawing the requested certification.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:18:05', '2024-07-23 20:18:05'),
(757, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 650, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The requirements referred to in paragraph 3 of this Article and the criteria referred to in Article 42(5) shall be made public by the supervisory authority in an easily accessible form. The supervisory authorities shall also transmit those requirements and criteria to the Board.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:18:07', '2024-07-23 20:18:07'),
(758, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 651, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Without prejudice to Chapter VIII, the competent supervisory authority or the national accreditation body shall revoke an accreditation of a certification body pursuant to paragraph 1 of this Article where the conditions for the accreditation are not, or are no longer, met or where actions taken by a certification body infringe this Regulation.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:18:09', '2024-07-23 20:18:09'),
(759, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 652, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Commission shall be empowered to adopt delegated acts in accordance with Article 92 for the purpose of specifying the requirements to be taken into account for the data protection certification mechanisms referred to in Article 42(1).\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:18:10', '2024-07-23 20:18:10'),
(760, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 653, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Commission may adopt implementing acts laying down technical standards for certification mechanisms and data protection seals and marks, and mechanisms to promote and recognise those certification mechanisms, seals and marks. Those implementing acts shall be adopted in accordance with the examination procedure referred to in Article 93(2).\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:18:13', '2024-07-23 20:18:13'),
(761, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 654, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Any transfer of personal data which are undergoing processing or are intended for processing after transfer to a third country or to an international organisation shall take place only if, subject to the other provisions of this Regulation, the conditions laid down in this Chapter are complied with by the controller and processor, including for onward transfers of personal data from the third country or an international organisation to another third country or to another international organisation. All provisions in this Chapter shall be applied in order to ensure that the level of protection of natural persons guaranteed by this Regulation is not undermined.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:18:14', '2024-07-23 20:18:14'),
(762, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 655, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A transfer of personal data to a third country or an international organisation may take place where the Commission has decided that the third country, a territory or one or more specified sectors within that third country, or the international organisation in question ensures an adequate level of protection. Such a transfer shall not require any specific authorisation.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:18:15', '2024-07-23 20:18:15'),
(763, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 656, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"When assessing the adequacy of the level of protection, the Commission shall, in particular, take account of the rule of law, respect for human rights and fundamental freedoms, relevant legislation, both general and sectoral, including concerning public security, defence, national security and criminal law and the access of public authorities to personal data, as well as the implementation of such legislation, data protection rules, professional rules and security measures, including rules for the onward transfer of personal data to another third country or international organisation which are complied with in that country or international organisation, case-law, as well as effective and enforceable data subject rights and effective administrative and judicial redress for the data subjects whose personal data are being transferred\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:18:18', '2024-07-23 20:18:18'),
(764, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 657, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"When assessing the adequacy of the level of protection, the Commission shall, in particular, take account of the existence and effective functioning of one or more independent supervisory authorities in the third country or to which an international organisation is subject, with responsibility for ensuring and enforcing compliance with the data protection rules, including adequate enforcement powers, for assisting and advising the data subjects in exercising their rights and for cooperation with the supervisory authorities of the Member States and\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:18:19', '2024-07-23 20:18:19'),
(765, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 658, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"When assessing the adequacy of the level of protection, the Commission shall, in particular, take account of the international commitments the third country or international organisation concerned has entered into, or other obligations arising from legally binding conventions or instruments as well as from its participation in multilateral or regional systems, in particular in relation to the protection of personal data.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:18:27', '2024-07-23 20:18:27'),
(766, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 659, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Commission, after assessing the adequacy of the level of protection, may decide, by means of implementing act, that a third country, a territory or one or more specified sectors within a third country, or an international organisation ensures an adequate level of protection within the meaning of paragraph 2 of this Article. The implementing act shall provide for a mechanism for a periodic review, at least every four years, which shall take into account all relevant developments in the third country or international organisation. The implementing act shall specify its territorial and sectoral application and, where applicable, identify the supervisory authority or authorities referred to in point (b) of paragraph 2 of this Article. The implementing act shall be adopted in accordance with the examination procedure referred to in Article 93(2).\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:18:28', '2024-07-23 20:18:28'),
(767, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 660, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Commission shall, on an ongoing basis, monitor developments in third countries and international organisations that could affect the functioning of decisions adopted pursuant to paragraph 3 of this Article and decisions adopted on the basis of Article 25(6) of Directive 95\\/46\\/EC.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:18:30', '2024-07-23 20:18:30'),
(768, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 661, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Commission shall, where available information reveals, in particular following the review referred to in paragraph 3 of this Article, that a third country, a territory or one or more specified sectors within a third country, or an international organisation no longer ensures an adequate level of protection within the meaning of paragraph 2 of this Article, to the extent necessary, repeal, amend or suspend the decision referred to in paragraph 3 of this Article by means of implementing acts without retro-active effect. Those implementing acts shall be adopted in accordance with the examination procedure referred to in Article 93(2).On duly justified imperative grounds of urgency, the Commission shall adopt immediately applicable implementing acts in accordance with the procedure referred to in Article 93(3).\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:18:32', '2024-07-23 20:18:32'),
(769, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 662, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Commission shall enter into consultations with the third country or international organisation with a view to remedying the situation giving rise to the decision made pursuant to paragraph 5.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:18:35', '2024-07-23 20:18:35'),
(770, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 663, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A decision pursuant to paragraph 5 of this Article is without prejudice to transfers of personal data to the third country, a territory or one or more specified sectors within that third country, or the international organisation in question pursuant to Articles 46 to 49.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:18:36', '2024-07-23 20:18:36'),
(771, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 664, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Commission shall publish in the Official Journal of the European Union and on its website a list of the third countries, territories and specified sectors within a third country and international organisations for which it has decided that an adequate level of protection is or is no longer ensured.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-23 20:18:38', '2024-07-23 20:18:38'),
(772, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 665, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In the absence of a decision pursuant to Article 45(3), a controller or processor may transfer personal data to a third country or an international organisation only if the controller or processor has provided appropriate safeguards, and on condition that enforceable data subject rights and effective legal remedies for data subjects are available.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:31:45', '2024-07-25 14:31:45'),
(773, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 666, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Decisions adopted by the Commission on the basis of Article 25(6) of Directive 95\\/46\\/EC shall remain in force until amended, replaced or repealed by a Commission Decision adopted in accordance with paragraph 3 or 5 of this Article.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:31:51', '2024-07-25 14:31:51'),
(774, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 667, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The appropriate safeguards referred to in paragraph 1 may be provided for, without requiring any specific authorisation from a supervisory authority, by a legally binding and enforceable instrument between public authorities or bodies\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:31:54', '2024-07-25 14:31:54'),
(775, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 668, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The appropriate safeguards referred to in paragraph 1 may be provided for, without requiring any specific authorisation from a supervisory authority, by binding corporate rules in accordance with Article 47\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:31:56', '2024-07-25 14:31:56'),
(776, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 669, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The appropriate safeguards referred to in paragraph 1 may be provided for, without requiring any specific authorisation from a supervisory authority, by standard data protection clauses adopted by the Commission in accordance with the examination procedure referred to in Article 93(2)\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:31:58', '2024-07-25 14:31:58'),
(777, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 670, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The appropriate safeguards referred to in paragraph 1 may be provided for, without requiring any specific authorisation from a supervisory authority, by standard data protection clauses adopted by a supervisory authority and approved by the Commission pursuant to the examination procedure referred to in Article 93(2)\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:31:59', '2024-07-25 14:31:59'),
(778, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 671, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The appropriate safeguards referred to in paragraph 1 may be provided for, without requiring any specific authorisation from a supervisory authority, by an approved code of conduct pursuant to Article 40 together with binding and enforceable commitments of the controller or processor in the third country to apply the appropriate safeguards, including as regards data subjects\\u2019 rights or\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:32:01', '2024-07-25 14:32:01'),
(779, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 672, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The appropriate safeguards referred to in paragraph 1 may be provided for, without requiring any specific authorisation from a supervisory authority, by an approved certification mechanism pursuant to Article 42 together with binding and enforceable commitments of the controller or processor in the third country to apply the appropriate safeguards, including as regards data subjects\\u2019 rights.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:32:03', '2024-07-25 14:32:03'),
(780, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 673, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Subject to the authorisation from the competent supervisory authority, the appropriate safeguards referred to in paragraph 1 may also be provided for, in particular, by contractual clauses between the controller or processor and the controller, processor or the recipient of the personal data in the third country or international organisation or\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:32:04', '2024-07-25 14:32:04'),
(781, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 674, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Subject to the authorisation from the competent supervisory authority, the appropriate safeguards referred to in paragraph 1 may also be provided for, in particular, by provisions to be inserted into administrative arrangements between public authorities or bodies which include enforceable and effective data subject rights.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:32:06', '2024-07-25 14:32:06'),
(782, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 675, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The supervisory authority shall apply the consistency mechanism referred to in Article 63 in the cases referred to in paragraph 3 of this Article.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:32:07', '2024-07-25 14:32:07'),
(783, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 676, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Authorisations by a Member State or supervisory authority on the basis of Article 26(2) of Directive 95\\/46\\/EC shall remain valid until amended, replaced or repealed, if necessary, by that supervisory authority. Decisions adopted by the Commission on the basis of Article 26(4) of Directive 95\\/46\\/EC shall remain in force until amended, replaced or repealed, if necessary, by a Commission Decision adopted in accordance with paragraph 2 of this Article.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:32:08', '2024-07-25 14:32:08'),
(784, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 677, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The competent supervisory authority shall approve binding corporate rules in accordance with the consistency mechanism set out in Article 63, provided that they are legally binding and apply to and are enforced by every member concerned of the group of undertakings, or group of enterprises engaged in a joint economic activity, including their employees expressly confer enforceable rights on data subjects with regard to the processing of their personal data; and fulfil the requirements laid down in paragraph 2.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:32:13', '2024-07-25 14:32:13');
INSERT INTO `activity_log` (`id`, `log_name`, `description`, `subject_type`, `event`, `subject_id`, `causer_type`, `causer_id`, `properties`, `batch_uuid`, `created_at`, `updated_at`) VALUES
(785, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 678, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The competent supervisory authority shall approve binding corporate rules in accordance with the consistency mechanism set out in Article 63, provided that they expressly confer enforceable rights on data subjects with regard to the processing of their personal data; and\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:32:17', '2024-07-25 14:32:17'),
(786, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 679, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The competent supervisory authority shall approve binding corporate rules in accordance with the consistency mechanism set out in Article 63, provided that they fulfil the requirements laid down in paragraph 2.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:32:18', '2024-07-25 14:32:18'),
(787, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 680, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The binding corporate rules referred to in paragraph 1 shall specify at least the structure and contact details of the group of undertakings, or group of enterprises engaged in a joint economic activity and of each of its members\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:32:22', '2024-07-25 14:32:22'),
(788, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 681, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The binding corporate rules referred to in paragraph 1 shall specify at least the data transfers or set of transfers, including the categories of personal data, the type of processing and its purposes, the type of data subjects affected and the identification of the third country or countries in question\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:32:23', '2024-07-25 14:32:23'),
(789, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 682, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The binding corporate rules referred to in paragraph 1 shall specify at least their legally binding nature, both internally and externally\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:32:48', '2024-07-25 14:32:48'),
(790, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 683, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The binding corporate rules referred to in paragraph 1 shall specify at least the application of the general data protection principles, in particular purpose limitation, data minimisation, limited storage periods, data quality, data protection by design and by default, legal basis for processing, processing of special categories of personal data, measures to ensure data security, and the requirements in respect of onward transfers to bodies not bound by the binding corporate rules\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:32:49', '2024-07-25 14:32:49'),
(791, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 684, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The binding corporate rules referred to in paragraph 1 shall specify at least the rights of data subjects in regard to processing and the means to exercise those rights, including the right not to be subject to decisions based solely on automated processing, including profiling in accordance with Article 22, the right to lodge a complaint with the competent supervisory authority and before the competent courts of the Member States in accordance with Article 79, and to obtain redress and, where appropriate, compensation for a breach of the binding corporate rules\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:32:52', '2024-07-25 14:32:52'),
(792, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 685, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The binding corporate rules referred to in paragraph 1 shall specify at least the acceptance by the controller or processor established on the territory of a Member State of liability for any breaches of the binding corporate rules by any member concerned not established in the Union; the controller or the processor shall be exempt from that liability, in whole or in part, only if it proves that that member is not responsible for the event giving rise to the damage\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:32:53', '2024-07-25 14:32:53'),
(793, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 686, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The binding corporate rules referred to in paragraph 1 shall specify at least how the information on the binding corporate rules, in particular on the provisions referred to in points (d), (e) and (f) of this paragraph is provided to the data subjects in addition to Articles 13 and 14\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:32:55', '2024-07-25 14:32:55'),
(794, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 687, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The binding corporate rules referred to in paragraph 1 shall specify at least the tasks of any data protection officer designated in accordance with Article 37 or any other person or entity in charge of the monitoring compliance with the binding corporate rules within the group of undertakings, or group of enterprises engaged in a joint economic activity, as well as monitoring training and complaint-handling\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:32:57', '2024-07-25 14:32:57'),
(795, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 688, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The binding corporate rules referred to in paragraph 1 shall specify at least the complaint procedures\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:32:58', '2024-07-25 14:32:58'),
(796, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 689, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The binding corporate rules referred to in paragraph 1 shall specify at least the mechanisms within the group of undertakings, or group of enterprises engaged in a joint economic activity for ensuring the verification of compliance with the binding corporate rules. Such mechanisms shall include data protection audits and methods for ensuring corrective actions to protect the rights of the data subject. Results of such verification should be communicated to the person or entity referred to in point (h) and to the board of the controlling undertaking of a group of undertakings, or of the group of enterprises engaged in a joint economic activity, and should be available upon request to the competent supervisory authority\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:33:01', '2024-07-25 14:33:01'),
(797, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 690, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The binding corporate rules referred to in paragraph 1 shall specify at least the mechanisms for reporting and recording changes to the rules and reporting those changes to the supervisory authority\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-07-25 14:33:02', '2024-07-25 14:33:02'),
