(990, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 850, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Processing of personal data relating to criminal convictions and offences or related security measures based on Article 6(1) shall be carried out only under the control of official authority or when the processing is authorised by Union or Member State law providing for appropriate safeguards for the rights and freedoms of data subjects.Any comprehensive register of criminal convictions shall be kept only under the control of official authority\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:30:01', '2024-08-09 13:30:01'),
(991, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 851, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"The free movement of personal data within the Union shall be neither restricted nor prohibited for reasons connected with the protection of natural persons with regard to the processing of personal data..\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:30:11', '2024-08-09 13:30:11'),
(992, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 852, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"This Regulation applies to the processing of personal data wholly or partly by automated means and to the processing other than by automated means of personal data which form part of a filing system or are intended to form part of a filing system.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:30:13', '2024-08-09 13:30:13'),
