(996, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 856, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"This Regulation does not apply to the processing of personal data by competent authorities for the purposes of the prevention, investigation, detection or prosecution of criminal offences or the execution of criminal penalties, including the safeguarding against and the prevention of threats to public security.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:30:19', '2024-08-09 13:30:19'),
(997, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 857, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the processing of personal data by the Union institutions, bodies, offices and agencies, Regulation (EC) No 45\\/2001 applies. Regulation (EC) No 45\\/2001 and other Union legal acts applicable to such processing of personal data shall be adapted to the principles and rules of this Regulation in accordance with Article 98.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:32:12', '2024-08-09 13:32:12'),
