(999, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 859, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"This Regulation applies to the processing of personal data in the context of the activities of an establishment of a controller or a processor in the Union, regardless of whether the processing takes place in the Union or not.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:32:46', '2024-08-09 13:32:46'),
(1000, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 860, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"This Regulation applies to the processing of personal data of data subjects who are in the Union by a controller or processor not established in the Union, where the processing activities are related to the offering of goods or services, irrespective of whether a payment of the data subject is required, to such data subjects in the Union\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:32:47', '2024-08-09 13:32:47'),
(1001, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 861, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"This Regulation applies to the processing of personal data of data subjects who are in the Union by a controller or processor not established in the Union, where the processing activities are related to the monitoring of their behaviour as far as their behaviour takes place within the Union\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:32:48', '2024-08-09 13:32:48'),
(1002, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 862, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"This Regulation applies to the processing of personal data by a controller not established in the Union, but in a place where Member State law applies by virtue of public international law.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:32:49', '2024-08-09 13:32:49'),
(1003, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 863, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018personal data\\u2019 means any information relating to an identified or identifiable natural person (\\u2018data subject\\u2019); an identifiable natural person is one who can be identified, directly or indirectly, in particular by reference to an identifier such as a name, an identification number, location data, an online identifier or to one or more factors specific to the physical, physiological, genetic, mental, economic, cultural or social identity of that natural person\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:32:51', '2024-08-09 13:32:51'),
(1004, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 864, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018processing\\u2019 means any operation or set of operations which is performed on personal data or on sets of personal data, whether or not by automated means, such as collection, recording, organisation, structuring, storage, adaptation or alteration, retrieval, consultation, use, disclosure by transmission, dissemination or otherwise making available, alignment or combination, restriction, erasure or destruction\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:33:09', '2024-08-09 13:33:09'),
(1005, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 865, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018restriction of processing\\u2019 means the marking of stored personal data with the aim of limiting their processing in the future\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:33:10', '2024-08-09 13:33:10'),
(1006, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 866, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018profiling\\u2019 means any form of automated processing of personal data consisting of the use of personal data to evaluate certain personal aspects relating to a natural person, in particular to analyse or predict aspects concerning that natural person\\u2019s performance at work, economic situation, health, personal preferences, interests, reliability, behaviour, location or movements\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:33:11', '2024-08-09 13:33:11'),
(1007, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 867, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018pseudonymisation\\u2019 means the processing of personal data in such a manner that the personal data can no longer be attributed to a specific data subject without the use of additional information, provided that such additional information is kept separately and is subject to technical and organisational measures to ensure that the personal data are not attributed to an identified or identifiable natural person\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:33:22', '2024-08-09 13:33:22'),
(1008, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 868, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018filing system\\u2019 means any structured set of personal data which are accessible according to specific criteria, whether centralised, decentralised or dispersed on a functional or geographical basis\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:33:23', '2024-08-09 13:33:23'),
(1009, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 869, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018controller\\u2019 means the natural or legal person, public authority, agency or other body which, alone or jointly with others, determines the purposes and means of the processing of personal data; where the purposes and means of such processing are determined by Union or Member State law, the controller or the specific criteria for its nomination may be provided for by Union or Member State law\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:33:25', '2024-08-09 13:33:25'),
(1010, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 870, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018processor\\u2019 means a natural or legal person, public authority, agency or other body which processes personal data on behalf of the controller\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:33:26', '2024-08-09 13:33:26'),
(1011, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 871, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018recipient\\u2019 means a natural or legal person, public authority, agency or another body, to which the personal data are disclosed, whether a third party or not. However, public authorities which may receive personal data in the framework of a particular inquiry in accordance with Union or Member State law shall not be regarded as recipients; the processing of those data by those public authorities shall be in compliance with the applicable data protection rules according to the purposes of the processing\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:33:34', '2024-08-09 13:33:34'),
(1012, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 872, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018third party\\u2019 means a natural or legal person, public authority, agency or body other than the data subject, controller, processor and persons who, under the direct authority of the controller or processor, are authorised to process personal data\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:33:35', '2024-08-09 13:33:35'),
(1013, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 873, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018consent\\u2019 of the data subject means any freely given, specific, informed and unambiguous indication of the data subject\\u2019s wishes by which he or she, by a statement or by a clear affirmative action, signifies agreement to the processing of personal data relating to him or her\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:33:36', '2024-08-09 13:33:36'),
(1014, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 874, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018personal data breach\\u2019 means a breach of security leading to the accidental or unlawful destruction, loss, alteration, unauthorised disclosure of, or access to, personal data transmitted, stored or otherwise processed\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:33:37', '2024-08-09 13:33:37'),
(1015, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 875, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018genetic data\\u2019 means personal data relating to the inherited or acquired genetic characteristics of a natural person which give unique information about the physiology or the health of that natural person and which result, in particular, from an analysis of a biological sample from the natural person in question\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:33:39', '2024-08-09 13:33:39'),
(1016, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 876, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018biometric data\\u2019 means personal data resulting from specific technical processing relating to the physical, physiological or behavioural characteristics of a natural person, which allow or confirm the unique identification of that natural person, such as facial images or dactyloscopic data\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:33:55', '2024-08-09 13:33:55'),
(1017, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 877, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018data concerning health\\u2019 means personal data related to the physical or mental health of a natural person, including the provision of health care services, which reveal information about his or her health status\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:33:58', '2024-08-09 13:33:58'),
(1018, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 878, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018main establishment\\u2019 means as regards a controller with establishments in more than one Member State, the place of its central administration in the Union, unless the decisions on the purposes and means of the processing of personal data are taken in another establishment of the controller in the Union and the latter establishment has the power to have such decisions implemented, in which case the establishment having taken such decisions is to be considered to be the main establishment\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:33:59', '2024-08-09 13:33:59'),
(1019, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 879, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018main establishment\\u2019 means as regards a processor with establishments in more than one Member State, the place of its central administration in the Union, or, if the processor has no central administration in the Union, the establishment of the processor in the Union where the main processing activities in the context of the activities of an establishment of the processor take place to the extent that the processor is subject to specific obligations under this Regulation\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:34:00', '2024-08-09 13:34:00'),
(1020, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 880, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018representative\\u2019 means a natural or legal person established in the Union who, designated by the controller or processor in writing pursuant to Article 27, represents the controller or processor with regard to their respective obligations under this Regulation\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:34:08', '2024-08-09 13:34:08'),
(1021, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 881, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018enterprise\\u2019 means a natural or legal person engaged in an economic activity, irrespective of its legal form, including partnerships or associations regularly engaged in an economic activity\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:34:10', '2024-08-09 13:34:10'),
(1022, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 882, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018group of undertakings\\u2019 means a controlling undertaking and its controlled undertakings\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:34:14', '2024-08-09 13:34:14'),
(1023, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 883, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018binding corporate rules\\u2019 means personal data protection policies which are adhered to by a controller or processor established on the territory of a Member State for transfers or a set of transfers of personal data to a controller or processor in one or more third countries within a group of undertakings, or group of enterprises engaged in a joint economic activity\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:34:15', '2024-08-09 13:34:15'),
(1024, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 884, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018supervisory authority\\u2019 means an independent public authority which is established by a Member State pursuant to Article 51\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:34:16', '2024-08-09 13:34:16'),
(1025, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 885, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018supervisory authority concerned\\u2019 means a supervisory authority which is concerned by the processing of personal data because the controller or processor is established on the territory of the Member State of that supervisory authority\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:34:18', '2024-08-09 13:34:18'),
(1026, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 886, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018supervisory authority concerned\\u2019 means a supervisory authority which is concerned by the processing of personal data because data subjects residing in the Member State of that supervisory authority are substantially affected or likely to be substantially affected by the processing\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:34:25', '2024-08-09 13:34:25'),
(1027, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 887, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018supervisory authority concerned\\u2019 means a supervisory authority which is concerned by the processing of personal data because a complaint has been lodged with that supervisory authority\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:34:26', '2024-08-09 13:34:26'),
(1028, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 888, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018cross-border processing\\u2019 means either processing of personal data which takes place in the context of the activities of establishments in more than one Member State of a controller or processor in the Union where the controller or processor is established in more than one Member State\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:34:28', '2024-08-09 13:34:28'),
(1029, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 889, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018cross-border processing\\u2019 means either processing of personal data which takes place in the context of the activities of a single establishment of a controller or processor in the Union but which substantially affects or is likely to substantially affect data subjects in more than one Member State.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:34:30', '2024-08-09 13:34:30'),
(1030, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 890, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018relevant and reasoned objection\\u2019 means an objection to a draft decision as to whether there is an infringement of this Regulation, or whether envisaged action in relation to the controller or processor complies with this Regulation, which clearly demonstrates the significance of the risks posed by the draft decision as regards the fundamental rights and freedoms of data subjects and, where applicable, the free flow of personal data within the Union\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:34:31', '2024-08-09 13:34:31'),
(1031, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 891, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018information society service\\u2019 means a service as defined in point (b) of Article 1(1) of Directive (EU) 2015\\/1535 of the European Parliament and of the Council\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:34:39', '2024-08-09 13:34:39'),
(1032, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 892, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018international organisation\\u2019 means an organisation and its subordinate bodies governed by public international law, or any other body which is set up by, or on the basis of, an agreement between two or more countries.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:34:40', '2024-08-09 13:34:40'),
(1033, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 893, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Personal data shall be processed lawfully, fairly and in a transparent manner in relation to the data subject (lawfulness, fairness and transparency)\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:34:41', '2024-08-09 13:34:41'),
(1034, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 894, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Personal data shall be processed lawfully, fairly and in a transparent manner in relation to the data subject (lawfulness, fairness and transparency)\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:34:42', '2024-08-09 13:34:42'),
(1035, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 895, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Personal data shall be adequate, relevant and limited to what is necessary in relation to the purposes for which they are processed (data minimisation)\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:34:44', '2024-08-09 13:34:44'),
(1036, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 896, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Personal data shall be kept in a form which permits identification of data subjects for no longer than is necessary for the purposes for which the personal data are processed; personal data may be stored for longer periods insofar as the personal data will be processed solely for archiving purposes in the public interest, scientific or historical research purposes or statistical purposes in accordance with Article 89(1) subject to implementation of the appropriate technical and organisational measures required by this Regulation in order to safeguard the rights and freedoms of the data subject\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:34:51', '2024-08-09 13:34:51'),
(1037, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 897, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Personal data shall be processed in a manner that ensures appropriate security of the personal data, including protection against unauthorised or unlawful processing and against accidental loss, destruction or damage, using appropriate technical or organisational measures (integrity and confidentiality)\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:34:52', '2024-08-09 13:34:52'),
(1038, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 898, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"The controller shall be responsible for, and be able to demonstrate compliance with, paragraph 1 (accountability)\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:34:54', '2024-08-09 13:34:54'),
(1039, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 899, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Processing shall be lawful only if and to the extent that the data subject has given consent to the processing of his or her personal data for one or more specific purposes\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:34:54', '2024-08-09 13:34:54'),
(1040, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 900, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Personal data shall be accurate and, where necessary, kept up to date; every reasonable step must be taken to ensure that personal data that are inaccurate, having regard to the purposes for which they are processed, are erased or rectified without delay (accuracy)\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:35:03', '2024-08-09 13:35:03'),
(1041, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 901, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Processing shall be lawful only if and to the extent that processing is necessary for the performance of a contract to which the data subject is party or in order to take steps at the request of the data subject prior to entering into a contract\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:35:14', '2024-08-09 13:35:14'),
(1042, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 902, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Processing shall be lawful only if and to the extent that processing is necessary for compliance with a legal obligation to which the controller is subject\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:35:14', '2024-08-09 13:35:14'),
(1043, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 903, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Processing shall be lawful only if and to the extent that processing is necessary in order to protect the vital interests of the data subject or of another natural person\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:35:16', '2024-08-09 13:35:16'),
(1044, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 904, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Processing shall be lawful only if and to the extent that processing is necessary for the performance of a task carried out in the public interest or in the exercise of official authority vested in the controller\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:35:17', '2024-08-09 13:35:17'),
(1045, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 905, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"If the purposes for which a controller processes personal data do not or do no longer require the identification of a data subject by the controller, the controller shall not be obliged to maintain, acquire or process additional information in order to identify the data subject for the sole purpose of complying with this Regulation.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:35:33', '2024-08-09 13:35:33'),
(1046, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 906, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Where, in cases referred to in paragraph 1 of this Article, the controller is able to demonstrate that it is not in a position to identify the data subject, the controller shall inform the data subject accordingly, if possible.In such cases, Articles 15 to 20 shall not apply except where the data subject, for the purpose of exercising his or her rights under those articles, provides additional information enabling his or her identification.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:35:34', '2024-08-09 13:35:34'),
(1047, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 907, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"The controller shall take appropriate measures to provide any information referred to in Articles 13 and 14 and any communication under Articles 15 to 22 and 34 relating to processing to the data subject in a concise, transparent, intelligible and easily accessible form, using clear and plain language, in particular for any information addressed specifically to a child. The information shall be provided in writing, or by other means, including, where appropriate, by electronic means. When requested by the data subject, the information may be provided orally, provided that the identity of the data subject is proven by other means.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:35:35', '2024-08-09 13:35:35'),
(1048, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 908, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"The controller shall facilitate the exercise of data subject rights under Articles 15 to 22. In the cases referred to in Article 11(2), the controller shall not refuse to act on the request of the data subject for exercising his or her rights under Articles 15 to 22, unless the controller demonstrates that it is not in a position to identify the data subject.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:35:36', '2024-08-09 13:35:36'),
(1049, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 909, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Paragraph 1 shall not apply if processing is necessary for archiving purposes in the public interest, scientific or historical research purposes or statistical purposes in accordance with Article 89(1) based on Union or Member State law which shall be proportionate to the aim pursued, respect the essence of the right to data protection and provide for suitable and specific measures to safeguard the fundamental rights and the interests of the data subject.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:35:38', '2024-08-09 13:35:38'),
(1050, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 910, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Paragraph 1 shall not apply if processing is necessary for reasons of public interest in the area of public health, such as protecting against serious cross-border threats to health or ensuring high standards of quality and safety of health care and of medicinal products or medical devices, on the basis of Union or Member State law which provides for suitable and specific measures to safeguard the rights and freedoms of the data subject, in particular professional secrecy\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:35:39', '2024-08-09 13:35:39'),
(1051, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 911, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Processing shall be lawful only if and to the extent processing is necessary for the purposes of the legitimate interests pursued by the controller or by a third party, except where such interests are overridden by the interests or fundamental rights and freedoms of the data subject which require protection of personal data, in particular where the data subject is a child. Point (f) of the first subparagraph shall not apply to processing carried out by public authorities in the performance of their tasks.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:35:44', '2024-08-09 13:35:44'),
(1052, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 912, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Member States may maintain or introduce more specific provisions to adapt the application of the rules of this Regulation with regard to processing for compliance with points (c) and (e) of paragraph 1 by determining more precisely specific requirements for the processing and other measures to ensure lawful and fair processing including for other specific processing situations as provided for in Chapter IX.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:35:46', '2024-08-09 13:35:46'),
(1053, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 913, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"The basis for the processing referred to in point (c) and (e) of paragraph 1 shall be laid down by a. Union law; b. Member State law to which the controller is subject. The purpose of the processing shall be determined in that legal basis or, as regards the processing referred to in point (e) of paragraph 1, shall be necessary for the performance of a task carried out in the public interest or in the exercise of official authority vested in the controller. That legal basis may contain specific provisions to adapt the application of rules of this Regulation, inter aliathe general conditions governing the lawfulness of processing by the controller; the types of data which are subject to the processing; the data subjects concerned; the entities to, and the purposes for which, the personal data may be disclosed; the purpose limitation; storage periods; and processing operations and processing procedures, including measures to ensure lawful and fair processing such as those for other specific processing situations as provided for in Chapter IX. The Union or the Member State law shall meet an objective of public interest and be proportionate to the legitimate aim pursued.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:35:47', '2024-08-09 13:35:47'),
(1054, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 914, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Where the processing for a purpose other than that for which the personal data have been collected is not based on the data subject\\u2019s consent or on a Union or Member State law which constitutes a necessary and proportionate measure in a democratic society to safeguard the objectives referred to in Article 23(1), the controller shall, in order to ascertain whether processing for another purpose is compatible with the purpose for which the personal data are initially collected, take into account, inter alia any link between the purposes for which the personal data have been collected and the purposes of the intended further processing\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:35:48', '2024-08-09 13:35:48'),
(1055, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 915, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Where the processing for a purpose other than that for which the personal data have been collected is not based on the data subject\\u2019s consent or on a Union or Member State law which constitutes a necessary and proportionate measure in a democratic society to safeguard the objectives referred to in Article 23(1), the controller shall, in order to ascertain whether processing for another purpose is compatible with the purpose for which the personal data are initially collected, take into account, inter alia the context in which the personal data have been collected, in particular regarding the relationship between data subjects and the controller\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:35:50', '2024-08-09 13:35:50'),
(1056, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 916, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Where the processing for a purpose other than that for which the personal data have been collected is not based on the data subject\\u2019s consent or on a Union or Member State law which constitutes a necessary and proportionate measure in a democratic society to safeguard the objectives referred to in Article 23(1), the controller shall, in order to ascertain whether processing for another purpose is compatible with the purpose for which the personal data are initially collected, take into account, inter alia the nature of the personal data, in particular whether special categories of personal data are processed, pursuant to Article 9, or whether personal data related to criminal convictions and offences are processed, pursuant to Article 10\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:35:51', '2024-08-09 13:35:51'),
(1057, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 917, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Where the processing for a purpose other than that for which the personal data have been collected is not based on the data subject\\u2019s consent or on a Union or Member State law which constitutes a necessary and proportionate measure in a democratic society to safeguard the objectives referred to in Article 23(1), the controller shall, in order to ascertain whether processing for another purpose is compatible with the purpose for which the personal data are initially collected, take into account, inter alia the possible consequences of the intended further processing for data subjects\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:35:52', '2024-08-09 13:35:52'),
(1058, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 918, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Where the processing for a purpose other than that for which the personal data have been collected is not based on the data subject\\u2019s consent or on a Union or Member State law which constitutes a necessary and proportionate measure in a democratic society to safeguard the objectives referred to in Article 23(1), the controller shall, in order to ascertain whether processing for another purpose is compatible with the purpose for which the personal data are initially collected, take into account, inter alia the existence of appropriate safeguards, which may include encryption or pseudonymisation.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:35:54', '2024-08-09 13:35:54'),
(1059, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 919, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Where processing is based on consent, the controller shall be able to demonstrate that the data subject has consented to processing of his or her personal data.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:35:55', '2024-08-09 13:35:55'),
(1060, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 920, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"If the data subject\\u2019s consent is given in the context of a written declaration which also concerns other matters, the request for consent shall be presented in a manner which is clearly distinguishable from the other matters, in an intelligible and easily accessible form, using clear and plain language. Any part of such a declaration which constitutes an infringement of this Regulation shall not be binding.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:35:55', '2024-08-09 13:35:55'),
(1061, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 921, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"The data subject shall have the right to withdraw his or her consent at any time. The withdrawal of consent shall not affect the lawfulness of processing based on consent before its withdrawal. Prior to giving consent, the data subject shall be informed thereof. It shall be as easy to withdraw as to give consent.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:00', '2024-08-09 13:36:00'),
(1062, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 922, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"When assessing whether consent is freely given, utmost account shall be taken of whether, inter alia, the performance of a contract, including the provision of a service, is conditional on consent to the processing of personal data that is not necessary for the performance of that contract.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:01', '2024-08-09 13:36:01'),
(1063, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 923, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Where point (a) of Article 6(1) applies, in relation to the offer of information society services directly to a child, the processing of the personal data of a child shall be lawful where the child is at least 16 years old. Where the child is below the age of 16 years, such processing shall be lawful only if and to the extent that consent is given or authorised by the holder of parental responsibility over the child.Member States may provide by law for a lower age for those purposes provided that such lower age is not below 13 years.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:03', '2024-08-09 13:36:03'),
(1064, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 924, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"The controller shall make reasonable efforts to verify in such cases that consent is given or authorised by the holder of parental responsibility over the child, taking into consideration available technology.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:04', '2024-08-09 13:36:04'),
(1065, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 925, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Paragraph 1 shall not affect the general contract law of Member States such as the rules on the validity, formation or effect of a contract in relation to a child.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:05', '2024-08-09 13:36:05'),
(1066, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 926, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Processing of personal data revealing racial or ethnic origin, political opinions, religious or philosophical beliefs, or trade union membership, and the processing of genetic data, biometric data for the purpose of uniquely identifying a natural person, data concerning health or data concerning a natural person\\u2019s sex life or sexual orientation shall be prohibited.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:06', '2024-08-09 13:36:06'),
(1067, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 927, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Paragraph 1 shall not apply if the data subject has given explicit consent to the processing of those personal data for one or more specified purposes, except where Union or Member State law provide that the prohibition referred to in paragraph 1 may not be lifted by the data subject\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:08', '2024-08-09 13:36:08'),
(1068, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 928, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Paragraph 1 shall not apply if processing is necessary for the purposes of carrying out the obligations and exercising specific rights of the controller or of the data subject in the field of employment and social security and social protection law in so far as it is authorised by Union or Member State law or a collective agreement pursuant to Member State law providing for appropriate safeguards for the fundamental rights and the interests of the data subject\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:08', '2024-08-09 13:36:08'),
(1069, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 929, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Paragraph 1 shall not apply if processing is necessary to protect the vital interests of the data subject or of another natural person where the data subject is physically or legally incapable of giving consent\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:10', '2024-08-09 13:36:10'),
(1070, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 930, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Paragraph 1 shall not apply if processing is carried out in the course of its legitimate activities with appropriate safeguards by a foundation, association or any other not-for-profit body with a political, philosophical, religious or trade union aim and on condition that the processing relates solely to the members or to former members of the body or to persons who have regular contact with it in connection with its purposes and that the personal data are not disclosed outside that body without the consent of the data subjects\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:11', '2024-08-09 13:36:11'),
(1071, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 931, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Paragraph 1 shall not apply if processing relates to personal data which are manifestly made public by the data subject; processing is necessary for the establishment, exercise or defence of legal claims or whenever courts are acting in their judicial capacity\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:12', '2024-08-09 13:36:12'),
(1072, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 932, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Paragraph 1 shall not apply if processing is necessary for the establishment, exercise or defence of legal claims or whenever courts are acting in their judicial capacity\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:13', '2024-08-09 13:36:13'),
(1073, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 933, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Paragraph 1 shall not apply if processing is necessary for reasons of substantial public interest, on the basis of Union or Member State law which shall be proportionate to the aim pursued, respect the essence of the right to data protection and provide for suitable and specific measures to safeguard the fundamental rights and the interests of the data subject\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:14', '2024-08-09 13:36:14');
INSERT INTO `activity_log` (`id`, `log_name`, `description`, `subject_type`, `event`, `subject_id`, `causer_type`, `causer_id`, `properties`, `batch_uuid`, `created_at`, `updated_at`) VALUES
(1074, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 934, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Paragraph 1 shall not apply if processing is necessary for the purposes of preventive or occupational medicine, for the assessment of the working capacity of the employee, medical diagnosis, the provision of health or social care or treatment or the management of health or social care systems and services on the basis of Union or Member State law or pursuant to contract with a health professional and subject to the conditions and safeguards referred to in paragraph 3\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:15', '2024-08-09 13:36:15'),
(1075, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 935, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"The controller shall provide information on action taken on a request under Articles 15 to 22 to the data subject without undue delay and in any event within one month of receipt of the request. That period may be extended by two further months where necessary, taking into account the complexity and number of the requests. The controller shall inform the data subject of any such extension within one month of receipt of the request, together with the reasons for the delay. Where the data subject makes the request by electronic form means, the information shall be provided by electronic means where possible, unless otherwise requested by the data subject.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:21', '2024-08-09 13:36:21'),
(1076, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 936, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"If the controller does not take action on the request of the data subject, the controller shall inform the data subject without delay and at the latest within one month of receipt of the request of the reasons for not taking action and on the possibility of lodging a complaint with a supervisory authority and seeking a judicial remedy.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:23', '2024-08-09 13:36:23'),
(1077, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 937, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Information provided under Articles 13 and 14 and any communication and any actions taken under Articles 15 to 22 and 34 shall be provided free of charge. 2Where requests from a data subject are manifestly unfounded or excessive, in particular because of their repetitive character, the controller may either: a. charge a reasonable fee taking into account the administrative costs of providing the information or communication or taking the action requested; or b. refuse to act on the request. The controller shall bear the burden of demonstrating the manifestly unfounded or excessive character of the request.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:25', '2024-08-09 13:36:25'),
(1078, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 938, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Without prejudice to Article 11, where the controller has reasonable doubts concerning the identity of the natural person making the request referred to in Articles 15 to 21, the controller may request the provision of additional information necessary to confirm the identity of the data subject.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:26', '2024-08-09 13:36:26'),
(1079, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 939, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"The information to be provided to data subjects pursuant to Articles 13 and 14 may be provided in combination with standardised icons in order to give in an easily visible, intelligible and clearly legible manner a meaningful overview of the intended processing. Where the icons are presented electronically they shall be machine-readable.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:27', '2024-08-09 13:36:27'),
(1080, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 940, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"The Commission shall be empowered to adopt delegated acts in accordance with Article 92 for the purpose of determining the information to be presented by the icons and the procedures for providing standardised icons.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:29', '2024-08-09 13:36:29'),
(1081, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 941, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Where personal data relating to a data subject are collected from the data subject, the controller shall, at the time when personal data are obtained, provide the data subject with information the contact details of the data protection officer, where applicable\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:31', '2024-08-09 13:36:31'),
(1082, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 942, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Where personal data relating to a data subject are collected from the data subject, the controller shall, at the time when personal data are obtained, provide the data subject with information the purposes of the processing for which the personal data are intended as well as the legal basis for the processing\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:32', '2024-08-09 13:36:32'),
(1083, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 943, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Where personal data relating to a data subject are collected from the data subject, the controller shall, at the time when personal data are obtained, provide the data subject with information where the processing is based on point (f) of Article 6(1), the legitimate interests pursued by the controller or by a third party\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:33', '2024-08-09 13:36:33'),
(1084, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 944, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Where personal data relating to a data subject are collected from the data subject, the controller shall, at the time when personal data are obtained, provide the data subject with the identity and the contact details of the controller and, where applicable, of the controller\\u2019s representative\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:35', '2024-08-09 13:36:35'),
(1085, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 945, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Where personal data relating to a data subject are collected from the data subject, the controller shall, at the time when personal data are obtained, provide the data subject with information the recipients or categories of recipients of the personal data, if any\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:36', '2024-08-09 13:36:36'),
(1086, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 946, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Where personal data relating to a data subject are collected from the data subject, the controller shall, at the time when personal data are obtained, provide the data subject where applicable, the fact that the controller intends to transfer personal data to a third country or international organisation and the existence or absence of an adequacy decision by the Commission, or in the case of transfers referred to in Article 46 or 47, or the second subparagraph of Article 49(1), reference to the appropriate or suitable safeguards and the means by which to obtain a copy of them or where they have been made available.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:37', '2024-08-09 13:36:37'),
(1087, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 947, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"In addition to the information referred to in paragraph 1, the controller shall, at the time when personal data are obtained, provide the data subject with the period for which the personal data will be stored, or if that is not possible, the criteria used to determine that period\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:39', '2024-08-09 13:36:39'),
(1088, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 948, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"In addition to the information referred to in paragraph 1, the controller shall, at the time when personal data are obtained, provide the data subject with the existence of the right to request from the controller access to and rectification or erasure of personal data or restriction of processing concerning the data subject or to object to processing as well as the right to data portability\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:40', '2024-08-09 13:36:40'),
(1089, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 949, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"In addition to the information referred to in paragraph 1, the controller shall, at the time when personal data are obtained, provide the data subject where the processing is based on point (a) of Article 6(1) or point (a) of Article 9(2), the existence of the right to withdraw consent at any time, without affecting the lawfulness of processing based on consent before its withdrawal\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:41', '2024-08-09 13:36:41'),
(1090, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 950, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"In addition to the information referred to in paragraph 1, the controller shall, at the time when personal data are obtained, provide the data subject the right to lodge a complaint with a supervisory authority\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:36:42', '2024-08-09 13:36:42'),
(1091, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 951, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"ISO 27001 2022\",\"provisions.provisions\":\"Understanding the needs and expectations of interested parties\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-09 13:37:49', '2024-08-09 13:37:49'),
