(1706, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1442, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018personal data\\u2019 means any information relating to an identified or identifiable natural person (\\u2018data subject\\u2019); an identifiable natural person is one who can be identified, directly or indirectly, in particular by reference to an identifier such as a name, an identification number, location data, an online identifier or to one or more factors specific to the physical, physiological, genetic, mental, economic, cultural or social identity of that natural person;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:33:41', '2024-08-14 08:33:41'),
(1707, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1443, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018processing\\u2019 means any operation or set of operations which is performed on personal data or on sets of personal data, whether or not by automated means, such as collection, recording, organisation, structuring, storage, adaptation or alteration, retrieval, consultation, use, disclosure by transmission, dissemination or otherwise making available, alignment or combination, restriction, erasure or destruction;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:33:44', '2024-08-14 08:33:44'),
(1708, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1444, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018restriction of processing\\u2019 means the marking of stored personal data with the aim of limiting their processing in the future;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:33:45', '2024-08-14 08:33:45'),
(1709, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1445, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018profiling\\u2019 means any form of automated processing of personal data consisting of the use of personal data to evaluate certain personal aspects relating to a natural person, in particular to analyse or predict aspects concerning that natural person\\u2019s performance at work, economic situation, health, personal preferences, interests, reliability, behaviour, location or movements;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:33:48', '2024-08-14 08:33:48'),
(1710, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1446, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018pseudonymisation\\u2019 means the processing of personal data in such a manner that the personal data can no longer be attributed to a specific data subject without the use of additional information, provided that such additional information is kept separately and is subject to technical and organisational measures to ensure that the personal data are not attributed to an identified or identifiable natural person;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:33:49', '2024-08-14 08:33:49'),
(1711, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1447, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018filing system\\u2019 means any structured set of personal data which are accessible according to specific criteria, whether centralised, decentralised or dispersed on a functional or geographical basis;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:33:50', '2024-08-14 08:33:50'),
(1712, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1448, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018controller\\u2019 means the natural or legal person, public authority, agency or other body which, alone or jointly with others, determines the purposes and means of the processing of personal data; where the purposes and means of such processing are determined by Union or Member State law, the controller or the specific criteria for its nomination may be provided for by Union or Member State law;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:33:53', '2024-08-14 08:33:53'),
(1713, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1449, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018processor\\u2019 means a natural or legal person, public authority, agency or other body which processes personal data on behalf of the controller;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:33:54', '2024-08-14 08:33:54'),
(1714, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1450, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018recipient\\u2019 means a natural or legal person, public authority, agency or another body, to which the personal data are disclosed, whether a third party or not. However, public authorities which may receive personal data in the framework of a particular inquiry in accordance with Union or Member State law shall not be regarded as recipients; the processing of those data by those public authorities shall be in compliance with the applicable data protection rules according to the purposes of the processing;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:33:57', '2024-08-14 08:33:57'),
(1715, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1451, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018third party\\u2019 means a natural or legal person, public authority, agency or body other than the data subject, controller, processor and persons who, under the direct authority of the controller or processor, are authorised to process personal data;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:33:58', '2024-08-14 08:33:58'),
(1716, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1452, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018consent\\u2019 of the data subject means any freely given, specific, informed and unambiguous indication of the data subject\\u2019s wishes by which he or she, by a statement or by a clear affirmative action, signifies agreement to the processing of personal data relating to him or her;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:00', '2024-08-14 08:34:00'),
(1717, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1453, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018personal data breach\\u2019 means a breach of security leading to the accidental or unlawful destruction, loss, alteration, unauthorised disclosure of, or access to, personal data transmitted, stored or otherwise processed;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:01', '2024-08-14 08:34:01'),
(1718, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1454, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018genetic data\\u2019 means personal data relating to the inherited or acquired genetic characteristics of a natural person which give unique information about the physiology or the health of that natural person and which result, in particular, from an analysis of a biological sample from the natural person in question;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:03', '2024-08-14 08:34:03'),
(1719, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1455, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018biometric data\\u2019 means personal data resulting from specific technical processing relating to the physical, physiological or behavioural characteristics of a natural person, which allow or confirm the unique identification of that natural person, such as facial images or dactyloscopic data;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:06', '2024-08-14 08:34:06'),
(1720, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1456, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018data concerning health\\u2019 means personal data related to the physical or mental health of a natural person, including the provision of health care services, which reveal information about his or her health status;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:07', '2024-08-14 08:34:07'),
(1721, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1457, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018main establishment\\u2019 means as regards a controller with establishments in more than one Member State, the place of its central administration in the Union, unless the decisions on the purposes and means of the processing of personal data are taken in another establishment of the controller in the Union and the latter establishment has the power to have such decisions implemented, in which case the establishment having taken such decisions is to be considered to be the main establishment;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:08', '2024-08-14 08:34:08'),
(1722, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1458, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018main establishment\\u2019 means as regards a processor with establishments in more than one Member State, the place of its central administration in the Union, or, if the processor has no central administration in the Union, the establishment of the processor in the Union where the main processing activities in the context of the activities of an establishment of the processor take place to the extent that the processor is subject to specific obligations under this Regulation;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:09', '2024-08-14 08:34:09'),
(1723, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1459, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018representative\\u2019 means a natural or legal person established in the Union who, designated by the controller or processor in writing pursuant to Article 27, represents the controller or processor with regard to their respective obligations under this Regulation;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:12', '2024-08-14 08:34:12'),
(1724, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1460, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018enterprise\\u2019 means a natural or legal person engaged in an economic activity, irrespective of its legal form, including partnerships or associations regularly engaged in an economic activity;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:13', '2024-08-14 08:34:13'),
(1725, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1461, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018group of undertakings\\u2019 means a controlling undertaking and its controlled undertakings;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:14', '2024-08-14 08:34:14'),
(1726, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1462, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018binding corporate rules\\u2019 means personal data protection policies which are adhered to by a controller or processor established on the territory of a Member State for transfers or a set of transfers of personal data to a controller or processor in one or more third countries within a group of undertakings, or group of enterprises engaged in a joint economic activity;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:15', '2024-08-14 08:34:15'),
(1727, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1463, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018supervisory authority\\u2019 means an independent public authority which is established by a Member State pursuant to Article 51;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:16', '2024-08-14 08:34:16'),
(1728, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1464, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018supervisory authority concerned\\u2019 means a supervisory authority which is concerned by the processing of personal data because the controller or processor is established on the territory of the Member State of that supervisory authority;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:22', '2024-08-14 08:34:22'),
(1729, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1465, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018supervisory authority concerned\\u2019 means a supervisory authority which is concerned by the processing of personal data because data subjects residing in the Member State of that supervisory authority are substantially affected or likely to be substantially affected by the processing;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:23', '2024-08-14 08:34:23'),
(1730, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1466, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018supervisory authority concerned\\u2019 means a supervisory authority which is concerned by the processing of personal data because a complaint has been lodged with that supervisory authority;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:24', '2024-08-14 08:34:24'),
(1731, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1467, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018cross-border processing\\u2019 means either processing of personal data which takes place in the context of the activities of establishments in more than one Member State of a controller or processor in the Union where the controller or processor is established in more than one Member State;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:25', '2024-08-14 08:34:25'),
(1732, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1468, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018cross-border processing\\u2019 means either processing of personal data which takes place in the context of the activities of a single establishment of a controller or processor in the Union but which substantially affects or is likely to substantially affect data subjects in more than one Member State.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:28', '2024-08-14 08:34:28'),
(1733, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1469, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018relevant and reasoned objection\\u2019 means an objection to a draft decision as to whether there is an infringement of this Regulation, or whether envisaged action in relation to the controller or processor complies with this Regulation, which clearly demonstrates the significance of the risks posed by the draft decision as regards the fundamental rights and freedoms of data subjects and, where applicable, the free flow of personal data within the Union;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:29', '2024-08-14 08:34:29'),
(1734, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1470, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018information society service\\u2019 means a service as defined in point (b) of Article 1(1) of Directive (EU) 2015\\/1535 of the European Parliament and of the Council;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:30', '2024-08-14 08:34:30'),
(1735, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1471, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"For the purpose of this Regulation \\u2018international organisation\\u2019 means an organisation and its subordinate bodies governed by public international law, or any other body which is set up by, or on the basis of, an agreement between two or more countries.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:31', '2024-08-14 08:34:31'),
(1736, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1472, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Personal data shall be processed lawfully, fairly and in a transparent manner in relation to the data subject (\\u2018lawfulness, fairness and transparency\\u2019);\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:33', '2024-08-14 08:34:33'),
(1737, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1473, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Personal data shall be collected for specified, explicit and legitimate purposes and not further processed in a manner that is incompatible with those purposes; further processing for archiving purposes in the public interest, scientific or historical research purposes or statistical purposes shall, in accordance with Article 89(1), not be considered to be incompatible with the initial purposes (\\u2018purpose limitation\\u2019);\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:34', '2024-08-14 08:34:34'),
(1738, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1474, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Personal data shall be adequate, relevant and limited to what is necessary in relation to the purposes for which they are processed (\\u2018data minimisation\\u2019);\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:35', '2024-08-14 08:34:35'),
(1739, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1475, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Personal data shall be accurate and, where necessary, kept up to date; every reasonable step must be taken to ensure that personal data that are inaccurate, having regard to the purposes for which they are processed, are erased or rectified without delay (\\u2018accuracy\\u2019);\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:36', '2024-08-14 08:34:36'),
(1740, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1476, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Personal data shall be kept in a form which permits identification of data subjects for no longer than is necessary for the purposes for which the personal data are processed; personal data may be stored for longer periods insofar as the personal data will be processed solely for archiving purposes in the public interest, scientific or historical research purposes or statistical purposes in accordance with Article 89(1) subject to implementation of the appropriate technical and organisational measures required by this Regulation in order to safeguard the rights and freedoms of the data subject (\\u2018storage limitation\\u2019);\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:39', '2024-08-14 08:34:39'),
(1741, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1477, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Personal data shall be processed in a manner that ensures appropriate security of the personal data, including protection against unauthorised or unlawful processing and against accidental loss, destruction or damage, using appropriate technical or organisational measures (\\u2018integrity and confidentiality\\u2019).\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:39', '2024-08-14 08:34:39'),
(1742, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1478, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller shall be responsible for, and be able to demonstrate compliance with, paragraph 1 (\\u2018accountability\\u2019).\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:41', '2024-08-14 08:34:41'),
(1743, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1479, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing shall be lawful only if and to the extent that the data subject has given consent to the processing of his or her personal data for one or more specific purposes;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:41', '2024-08-14 08:34:41'),
(1744, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1480, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing shall be lawful only if and to the extent that processing is necessary for the performance of a contract to which the data subject is party or in order to take steps at the request of the data subject prior to entering into a contract;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:42', '2024-08-14 08:34:42'),
(1745, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1481, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing shall be lawful only if and to the extent that processing is necessary for compliance with a legal obligation to which the controller is subject;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:46', '2024-08-14 08:34:46'),
(1746, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1482, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing shall be lawful only if and to the extent that processing is necessary in order to protect the vital interests of the data subject or of another natural person;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:46', '2024-08-14 08:34:46'),
(1747, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1483, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing shall be lawful only if and to the extent that processing is necessary for the performance of a task carried out in the public interest or in the exercise of official authority vested in the controller;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:47', '2024-08-14 08:34:47'),
(1748, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1484, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing shall be lawful only if and to the extent processing is necessary for the purposes of the legitimate interests pursued by the controller or by a third party, except where such interests are overridden by the interests or fundamental rights and freedoms of the data subject which require protection of personal data, in particular where the data subject is a child. Point (f) of the first subparagraph shall not apply to processing carried out by public authorities in the performance of their tasks.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:48', '2024-08-14 08:34:48'),
(1749, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1485, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Member States may maintain or introduce more specific provisions to adapt the application of the rules of this Regulation with regard to processing for compliance with points (c) and (e) of paragraph 1 by determining more precisely specific requirements for the processing and other measures to ensure lawful and fair processing including for other specific processing situations as provided for in Chapter IX.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:50', '2024-08-14 08:34:50'),
(1750, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1486, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The basis for the processing referred to in point (c) and (e) of paragraph 1 shall be laid down by a. Union law; b. Member State law to which the controller is subject. The purpose of the processing shall be determined in that legal basis or, as regards the processing referred to in point (e) of paragraph 1, shall be necessary for the performance of a task carried out in the public interest or in the exercise of official authority vested in the controller. That legal basis may contain specific provisions to adapt the application of rules of this Regulation, inter alia: the general conditions governing the lawfulness of processing by the controller; the types of data which are subject to the processing; the data subjects concerned; the entities to, and the purposes for which, the personal data may be disclosed; the purpose limitation; storage periods; and processing operations and processing procedures, including measures to ensure lawful and fair processing such as those for other specific processing situations as provided for in Chapter IX. The Union or the Member State law shall meet an objective of public interest and be proportionate to the legitimate aim pursued.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:52', '2024-08-14 08:34:52'),
(1751, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1487, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where the processing for a purpose other than that for which the personal data have been collected is not based on the data subject\\u2019s consent or on a Union or Member State law which constitutes a necessary and proportionate measure in a democratic society to safeguard the objectives referred to in Article 23(1), the controller shall, in order to ascertain whether processing for another purpose is compatible with the purpose for which the personal data are initially collected, take into account, inter alia any link between the purposes for which the personal data have been collected and the purposes of the intended further processing;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:53', '2024-08-14 08:34:53'),
(1752, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1488, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where the processing for a purpose other than that for which the personal data have been collected is not based on the data subject\\u2019s consent or on a Union or Member State law which constitutes a necessary and proportionate measure in a democratic society to safeguard the objectives referred to in Article 23(1), the controller shall, in order to ascertain whether processing for another purpose is compatible with the purpose for which the personal data are initially collected, take into account, inter alia the context in which the personal data have been collected, in particular regarding the relationship between data subjects and the controller;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:56', '2024-08-14 08:34:56'),
(1753, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1489, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where the processing for a purpose other than that for which the personal data have been collected is not based on the data subject\\u2019s consent or on a Union or Member State law which constitutes a necessary and proportionate measure in a democratic society to safeguard the objectives referred to in Article 23(1), the controller shall, in order to ascertain whether processing for another purpose is compatible with the purpose for which the personal data are initially collected, take into account, inter alia the nature of the personal data, in particular whether special categories of personal data are processed, pursuant to Article 9, or whether personal data related to criminal convictions and offences are processed, pursuant to Article 10;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:34:57', '2024-08-14 08:34:57'),
(1754, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1490, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where the processing for a purpose other than that for which the personal data have been collected is not based on the data subject\\u2019s consent or on a Union or Member State law which constitutes a necessary and proportionate measure in a democratic society to safeguard the objectives referred to in Article 23(1), the controller shall, in order to ascertain whether processing for another purpose is compatible with the purpose for which the personal data are initially collected, take into account, inter alia the possible consequences of the intended further processing for data subjects;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:00', '2024-08-14 08:35:00'),
(1755, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1491, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where the processing for a purpose other than that for which the personal data have been collected is not based on the data subject\\u2019s consent or on a Union or Member State law which constitutes a necessary and proportionate measure in a democratic society to safeguard the objectives referred to in Article 23(1), the controller shall, in order to ascertain whether processing for another purpose is compatible with the purpose for which the personal data are initially collected, take into account, inter alia the existence of appropriate safeguards, which may include encryption or pseudonymisation.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:01', '2024-08-14 08:35:01'),
(1756, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1492, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where processing is based on consent, the controller shall be able to demonstrate that the data subject has consented to processing of his or her personal data.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:02', '2024-08-14 08:35:02'),
(1757, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1493, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"If the data subject\\u2019s consent is given in the context of a written declaration which also concerns other matters, the request for consent shall be presented in a manner which is clearly distinguishable from the other matters, in an intelligible and easily accessible form, using clear and plain language. Any part of such a declaration which constitutes an infringement of this Regulation shall not be binding.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:04', '2024-08-14 08:35:04'),
(1758, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1494, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data subject shall have the right to withdraw his or her consent at any time. The withdrawal of consent shall not affect the lawfulness of processing based on consent before its withdrawal. Prior to giving consent, the data subject shall be informed thereof. It shall be as easy to withdraw as to give consent.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:05', '2024-08-14 08:35:05'),
(1759, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1495, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"When assessing whether consent is freely given, utmost account shall be taken of whether, inter alia, the performance of a contract, including the provision of a service, is conditional on consent to the processing of personal data that is not necessary for the performance of that contract.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:06', '2024-08-14 08:35:06');
INSERT INTO `activity_log` (`id`, `log_name`, `description`, `subject_type`, `event`, `subject_id`, `causer_type`, `causer_id`, `properties`, `batch_uuid`, `created_at`, `updated_at`) VALUES
(1760, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1496, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where point (a) of Article 6(1) applies, in relation to the offer of information society services directly to a child, the processing of the personal data of a child shall be lawful where the child is at least 16 years old. Where the child is below the age of 16 years, such processing shall be lawful only if and to the extent that consent is given or authorised by the holder of parental responsibility over the child.Member States may provide by law for a lower age for those purposes provided that such lower age is not below 13 years.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:07', '2024-08-14 08:35:07'),
(1761, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1497, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller shall make reasonable efforts to verify in such cases that consent is given or authorised by the holder of parental responsibility over the child, taking into consideration available technology.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:08', '2024-08-14 08:35:08'),
(1762, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1498, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Paragraph 1 shall not affect the general contract law of Member States such as the rules on the validity, formation or effect of a contract in relation to a child.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:10', '2024-08-14 08:35:10'),
(1763, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1499, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing of personal data revealing racial or ethnic origin, political opinions, religious or philosophical beliefs, or trade union membership, and the processing of genetic data, biometric data for the purpose of uniquely identifying a natural person, data concerning health or data concerning a natural person\\u2019s sex life or sexual orientation shall be prohibited.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:11', '2024-08-14 08:35:11'),
(1764, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1500, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Paragraph 1 shall not apply if the data subject has given explicit consent to the processing of those personal data for one or more specified purposes, except where Union or Member State law provide that the prohibition referred to in paragraph 1 may not be lifted by the data subject;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:13', '2024-08-14 08:35:13'),
(1765, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1501, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Paragraph 1 shall not apply if processing is necessary for the purposes of carrying out the obligations and exercising specific rights of the controller or of the data subject in the field of employment and social security and social protection law in so far as it is authorised by Union or Member State law or a collective agreement pursuant to Member State law providing for appropriate safeguards for the fundamental rights and the interests of the data subject;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:15', '2024-08-14 08:35:15'),
(1766, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1502, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Paragraph 1 shall not apply if processing is necessary to protect the vital interests of the data subject or of another natural person where the data subject is physically or legally incapable of giving consent;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:16', '2024-08-14 08:35:16'),
(1767, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1503, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Paragraph 1 shall not apply if processing is carried out in the course of its legitimate activities with appropriate safeguards by a foundation, association or any other not-for-profit body with a political, philosophical, religious or trade union aim and on condition that the processing relates solely to the members or to former members of the body or to persons who have regular contact with it in connection with its purposes and that the personal data are not disclosed outside that body without the consent of the data subjects;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:18', '2024-08-14 08:35:18'),
(1768, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1504, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Paragraph 1 shall not apply if processing relates to personal data which are manifestly made public by the data subject; processing is necessary for the establishment, exercise or defence of legal claims or whenever courts are acting in their judicial capacity;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:21', '2024-08-14 08:35:21'),
(1769, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1505, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Paragraph 1 shall not apply if processing is necessary for the establishment, exercise or defence of legal claims or whenever courts are acting in their judicial capacity;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:25', '2024-08-14 08:35:25'),
(1770, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1506, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Paragraph 1 shall not apply if processing is necessary for reasons of substantial public interest, on the basis of Union or Member State law which shall be proportionate to the aim pursued, respect the essence of the right to data protection and provide for suitable and specific measures to safeguard the fundamental rights and the interests of the data subject;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:26', '2024-08-14 08:35:26'),
(1771, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1507, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Paragraph 1 shall not apply if processing is necessary for the purposes of preventive or occupational medicine, for the assessment of the working capacity of the employee, medical diagnosis, the provision of health or social care or treatment or the management of health or social care systems and services on the basis of Union or Member State law or pursuant to contract with a health professional and subject to the conditions and safeguards referred to in paragraph 3;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:27', '2024-08-14 08:35:27'),
(1772, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1508, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Paragraph 1 shall not apply if processing is necessary for reasons of public interest in the area of public health, such as protecting against serious cross-border threats to health or ensuring high standards of quality and safety of health care and of medicinal products or medical devices, on the basis of Union or Member State law which provides for suitable and specific measures to safeguard the rights and freedoms of the data subject, in particular professional secrecy;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:28', '2024-08-14 08:35:28'),
(1773, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1509, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Paragraph 1 shall not apply if processing is necessary for archiving purposes in the public interest, scientific or historical research purposes or statistical purposes in accordance with Article 89(1) based on Union or Member State law which shall be proportionate to the aim pursued, respect the essence of the right to data protection and provide for suitable and specific measures to safeguard the fundamental rights and the interests of the data subject.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:30', '2024-08-14 08:35:30'),
(1774, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1510, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Personal data referred to in paragraph 1 may be processed for the purposes referred to in point (h) of paragraph 2 when those data are processed by or under the responsibility of a professional subject to the obligation of professional secrecy under Union or Member State law or rules established by national competent bodies or by another person also subject to an obligation of secrecy under Union or Member State law or rules established by national competent bodies.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:32', '2024-08-14 08:35:32'),
(1775, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1511, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Member States may maintain or introduce further conditions, including limitations, with regard to the processing of genetic data, biometric data or data concerning health.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:33', '2024-08-14 08:35:33'),
(1776, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1512, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing of personal data relating to criminal convictions and offences or related security measures based on Article 6(1) shall be carried out only under the control of official authority or when the processing is authorised by Union or Member State law providing for appropriate safeguards for the rights and freedoms of data subjects.Any comprehensive register of criminal convictions shall be kept only under the control of official authority\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:34', '2024-08-14 08:35:34'),
(1777, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1513, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"If the purposes for which a controller processes personal data do not or do no longer require the identification of a data subject by the controller, the controller shall not be obliged to maintain, acquire or process additional information in order to identify the data subject for the sole purpose of complying with this Regulation.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:37', '2024-08-14 08:35:37'),
(1778, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1514, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where, in cases referred to in paragraph 1 of this Article, the controller is able to demonstrate that it is not in a position to identify the data subject, the controller shall inform the data subject accordingly, if possible.In such cases, Articles 15 to 20 shall not apply except where the data subject, for the purpose of exercising his or her rights under those articles, provides additional information enabling his or her identification.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:38', '2024-08-14 08:35:38'),
(1779, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1515, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller shall take appropriate measures to provide any information referred to in Articles 13 and 14 and any communication under Articles 15 to 22 and 34 relating to processing to the data subject in a concise, transparent, intelligible and easily accessible form, using clear and plain language, in particular for any information addressed specifically to a child. The information shall be provided in writing, or by other means, including, where appropriate, by electronic means. When requested by the data subject, the information may be provided orally, provided that the identity of the data subject is proven by other means.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:39', '2024-08-14 08:35:39'),
(1780, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1516, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller shall facilitate the exercise of data subject rights under Articles 15 to 22. In the cases referred to in Article 11(2), the controller shall not refuse to act on the request of the data subject for exercising his or her rights under Articles 15 to 22, unless the controller demonstrates that it is not in a position to identify the data subject.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:41', '2024-08-14 08:35:41'),
(1781, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1517, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller shall provide information on action taken on a request under Articles 15 to 22 to the data subject without undue delay and in any event within one month of receipt of the request. That period may be extended by two further months where necessary, taking into account the complexity and number of the requests. The controller shall inform the data subject of any such extension within one month of receipt of the request, together with the reasons for the delay. Where the data subject makes the request by electronic form means, the information shall be provided by electronic means where possible, unless otherwise requested by the data subject.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:42', '2024-08-14 08:35:42'),
(1782, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1518, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"If the controller does not take action on the request of the data subject, the controller shall inform the data subject without delay and at the latest within one month of receipt of the request of the reasons for not taking action and on the possibility of lodging a complaint with a supervisory authority and seeking a judicial remedy.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:43', '2024-08-14 08:35:43'),
(1783, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1519, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Information provided under Articles 13 and 14 and any communication and any actions taken under Articles 15 to 22 and 34 shall be provided free of charge. 2Where requests from a data subject are manifestly unfounded or excessive, in particular because of their repetitive character, the controller may either: a. charge a reasonable fee taking into account the administrative costs of providing the information or communication or taking the action requested; or b. refuse to act on the request. The controller shall bear the burden of demonstrating the manifestly unfounded or excessive character of the request.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:46', '2024-08-14 08:35:46'),
(1784, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1520, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Without prejudice to Article 11, where the controller has reasonable doubts concerning the identity of the natural person making the request referred to in Articles 15 to 21, the controller may request the provision of additional information necessary to confirm the identity of the data subject.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:46', '2024-08-14 08:35:46'),
(1785, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1521, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The information to be provided to data subjects pursuant to Articles 13 and 14 may be provided in combination with standardised icons in order to give in an easily visible, intelligible and clearly legible manner a meaningful overview of the intended processing. Where the icons are presented electronically they shall be machine-readable.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:48', '2024-08-14 08:35:48'),
(1786, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1522, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Commission shall be empowered to adopt delegated acts in accordance with Article 92 for the purpose of determining the information to be presented by the icons and the procedures for providing standardised icons.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:48', '2024-08-14 08:35:48'),
(1787, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1523, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where personal data relating to a data subject are collected from the data subject, the controller shall, at the time when personal data are obtained, provide the data subject with the identity and the contact details of the controller and, where applicable, of the controller\\u2019s representative;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:51', '2024-08-14 08:35:51'),
(1788, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1524, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where personal data relating to a data subject are collected from the data subject, the controller shall, at the time when personal data are obtained, provide the data subject with information the contact details of the data protection officer, where applicable;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:55', '2024-08-14 08:35:55'),
(1789, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1525, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where personal data relating to a data subject are collected from the data subject, the controller shall, at the time when personal data are obtained, provide the data subject with information the purposes of the processing for which the personal data are intended as well as the legal basis for the processing;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:56', '2024-08-14 08:35:56'),
(1790, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1526, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where personal data relating to a data subject are collected from the data subject, the controller shall, at the time when personal data are obtained, provide the data subject with information where the processing is based on point (f) of Article 6(1), the legitimate interests pursued by the controller or by a third party;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:35:57', '2024-08-14 08:35:57'),
(1791, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1527, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where personal data relating to a data subject are collected from the data subject, the controller shall, at the time when personal data are obtained, provide the data subject with information the recipients or categories of recipients of the personal data, if any;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:36:00', '2024-08-14 08:36:00'),
