(1840, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1576, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where the controller has made the personal data public and is obliged pursuant to paragraph 1 to erase the personal data, the controller, taking account of available technology and the cost of implementation, shall take reasonable steps, including technical measures, to inform controllers which are processing the personal data that the data subject has requested the erasure by such controllers of any links to, or copy or replication of, those personal data\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:18', '2024-08-14 08:37:18'),
(1841, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1577, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Paragraphs 1 and 2 shall not apply to the extent that processing is necessary for exercising the right of freedom of expression and information;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:19', '2024-08-14 08:37:19'),
(1842, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1578, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Paragraphs 1 and 2 shall not apply to the extent that processing is necessary for compliance with a legal obligation which requires processing by Union or Member State law to which the controller is subject or for the performance of a task carried out in the public interest or in the exercise of official authority vested in the controller;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:20', '2024-08-14 08:37:20'),
(1843, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1579, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Paragraphs 1 and 2 shall not apply to the extent that processing is necessary for reasons of public interest in the area of public health in accordance with points (h) and (i) of Article 9(2) as well as Article 9(3);\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:21', '2024-08-14 08:37:21'),
(1844, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1580, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Paragraphs 1 and 2 shall not apply to the extent that processing is necessary for archiving purposes in the public interest, scientific or historical research purposes or statistical purposes in accordance with Article 89(1) in so far as the right referred to in paragraph 1 is likely to render impossible or seriously impair the achievement of the objectives of that processing; or\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:23', '2024-08-14 08:37:23'),
(1845, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1581, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Paragraphs 1 and 2 shall not apply to the extent that processing is necessary for the establishment, exercise or defence of legal claims.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:26', '2024-08-14 08:37:26'),
(1846, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1582, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data subject shall have the right to obtain from the controller restriction of processing where the accuracy of the personal data is contested by the data subject, for a period enabling the controller to verify the accuracy of the personal data;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:27', '2024-08-14 08:37:27'),
(1847, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1583, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data subject shall have the right to obtain from the controller restriction of processing where the processing is unlawful and the data subject opposes the erasure of the personal data and requests the restriction of their use instead;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:27', '2024-08-14 08:37:27'),
(1848, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1584, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data subject shall have the right to obtain from the controller restriction of processing where the controller no longer needs the personal data for the purposes of the processing, but they are required by the data subject for the establishment, exercise or defence of legal claims;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:28', '2024-08-14 08:37:28'),
(1849, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1585, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data subject shall have the right to obtain from the controller restriction of processing where the data subject has objected to processing pursuant to Article 21(1) pending the verification whether the legitimate grounds of the controller override those of the data subject.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:29', '2024-08-14 08:37:29'),
(1850, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1586, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where processing has been restricted under paragraph 1, such personal data shall, with the exception of storage, only be processed with the data subject\\u2019s consent or for the establishment, exercise or defence of legal claims or for the protection of the rights of another natural or legal person or for reasons of important public interest of the Union or of a Member State.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:30', '2024-08-14 08:37:30'),
(1851, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1587, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A data subject who has obtained restriction of processing pursuant to paragraph 1 shall be informed by the controller before the restriction of processing is lifted.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:34', '2024-08-14 08:37:34'),
(1852, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1588, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller shall communicate any rectification or erasure of personal data or restriction of processing carried out in accordance with Article 16, Article 17(1) and Article 18 to each recipient to whom the personal data have been disclosed, unless this proves impossible or involves disproportionate effort.The controller shall inform the data subject about those recipients if the data subject requests it.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:35', '2024-08-14 08:37:35'),
(1853, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1589, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data subject shall have the right to receive the personal data concerning him or her, which he or she has provided to a controller, in a structured, commonly used and machine-readable format and have the right to transmit those data to another controller without hindrance from the controller to which the personal data have been provided, where the processing is based on consent pursuant to point (a) of Article 6(1) or point (a) of Article 9(2) or on a contract pursuant to point (b) of Article 6(1); and carried out by automated means.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:37', '2024-08-14 08:37:37'),
(1854, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1590, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In exercising his or her right to data portability pursuant to paragraph 1, the data subject shall have the right to have the personal data transmitted directly from one controller to another, where technically feasible.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:37', '2024-08-14 08:37:37'),
(1855, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1591, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The exercise of the right referred to in paragraph 1 of this Article shall be without prejudice to Article 17.That right shall not apply to processing necessary for the performance of a task carried out in the public interest or in the exercise of official authority vested in the controller.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:40', '2024-08-14 08:37:40'),
(1856, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1592, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The right referred to in paragraph 1 shall not adversely affect the rights and freedoms of others.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:41', '2024-08-14 08:37:41'),
(1857, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1593, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data subject shall have the right to object, on grounds relating to his or her particular situation, at any time to processing of personal data concerning him or her which is based on point (e) or (f) of Article 6(1), including profiling based on those provisions. The controller shall no longer process the personal data unless the controller demonstrates compelling legitimate grounds for the processing which override the interests, rights and freedoms of the data subject or for the establishment, exercise or defence of legal claims.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:42', '2024-08-14 08:37:42'),
(1858, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1594, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where personal data are processed for direct marketing purposes, the data subject shall have the right to object at any time to processing of personal data concerning him or her for such marketing, which includes profiling to the extent that it is related to such direct marketing.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:43', '2024-08-14 08:37:43'),
(1859, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1595, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where the data subject objects to processing for direct marketing purposes, the personal data shall no longer be processed for such purposes.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:46', '2024-08-14 08:37:46'),
(1860, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1596, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"At the latest at the time of the first communication with the data subject, the right referred to in paragraphs 1 and 2 shall be explicitly brought to the attention of the data subject and shall be presented clearly and separately from any other information.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:47', '2024-08-14 08:37:47'),
(1861, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1597, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In the context of the use of information society services, and notwithstanding Directive 2002\\/58\\/EC, the data subject may exercise his or her right to object by automated means using technical specifications.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:48', '2024-08-14 08:37:48'),
(1862, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1598, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where personal data are processed for scientific or historical research purposes or statistical purposes pursuant to Article 89(1), the data subject, on grounds relating to his or her particular situation, shall have the right to object to processing of personal data concerning him or her, unless the processing is necessary for the performance of a task carried out for reasons of public interest.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:51', '2024-08-14 08:37:51'),
(1863, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1599, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data subject shall have the right not to be subject to a decision based solely on automated processing, including profiling, which produces legal effects concerning him or her or similarly significantly affects him or her.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:52', '2024-08-14 08:37:52'),
(1864, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1600, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Paragraph 1 shall not apply if the decision is necessary for entering into, or performance of, a contract between the data subject and a data controller;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:52', '2024-08-14 08:37:52'),
(1865, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1601, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Paragraph 1 shall not apply if the decision is authorised by Union or Member State law to which the controller is subject and which also lays down suitable measures to safeguard the data subject\\u2019s rights and freedoms and legitimate interests; or\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:53', '2024-08-14 08:37:53'),
(1866, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1602, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Paragraph 1 shall not apply if the decision is based on the data subject\\u2019s explicit consent.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:54', '2024-08-14 08:37:54'),
(1867, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1603, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In the cases referred to in points (a) and (c) of paragraph 2, the data controller shall implement suitable measures to safeguard the data subject\\u2019s rights and freedoms and legitimate interests, at least the right to obtain human intervention on the part of the controller, to express his or her point of view and to contest the decision.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:37:57', '2024-08-14 08:37:57'),
