(1881, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1617, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In particular, any legislative measure referred to in paragraph 1 shall contain specific provisions at least, where relevant, as to the scope of the restrictions introduced;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:19', '2024-08-14 08:38:19'),
(1882, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1618, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In particular, any legislative measure referred to in paragraph 1 shall contain specific provisions at least, where relevant, as to the safeguards to prevent abuse or unlawful access or transfer;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:20', '2024-08-14 08:38:20'),
(1883, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1619, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In particular, any legislative measure referred to in paragraph 1 shall contain specific provisions at least, where relevant, as to the specification of the controller or categories of controllers;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:20', '2024-08-14 08:38:20'),
(1884, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1620, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In particular, any legislative measure referred to in paragraph 1 shall contain specific provisions at least, where relevant, as to the storage periods and the applicable safeguards taking into account the nature, scope and purposes of the processing or categories of processing;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:21', '2024-08-14 08:38:21'),
(1885, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1621, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In particular, any legislative measure referred to in paragraph 1 shall contain specific provisions at least, where relevant, as to the risks to the rights and freedoms of data subjects; and\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:22', '2024-08-14 08:38:22'),
(1886, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1622, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In particular, any legislative measure referred to in paragraph 1 shall contain specific provisions at least, where relevant, as to the right of data subjects to be informed about the restriction, unless that may be prejudicial to the purpose of the restriction.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:25', '2024-08-14 08:38:25'),
(1887, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1623, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Taking into account the nature, scope, context and purposes of processing as well as the risks of varying likelihood and severity for the rights and freedoms of natural persons, the controller shall implement appropriate technical and organisational measures to ensure and to be able to demonstrate that processing is performed in accordance with this Regulation.Those measures shall be reviewed and updated where necessary.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:26', '2024-08-14 08:38:26'),
(1888, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1624, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where proportionate in relation to processing activities, the measures referred to in paragraph 1 shall include the implementation of appropriate data protection policies by the controller.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:27', '2024-08-14 08:38:27'),
(1889, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1625, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Adherence to approved codes of conduct as referred to in Article 40 or approved certification mechanisms as referred to in Article 42 may be used as an element by which to demonstrate compliance with the obligations of the controller.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:28', '2024-08-14 08:38:28'),
(1890, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1626, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Taking into account the state of the art, the cost of implementation and the nature, scope, context and purposes of processing as well as the risks of varying likelihood and severity for rights and freedoms of natural persons posed by the processing, the controller shall, both at the time of the determination of the means for processing and at the time of the processing itself, implement appropriate technical and organisational measures, such as pseudonymisation, which are designed to implement data-protection principles, such as data minimisation, in an effective manner and to integrate the necessary safeguards into the processing in order to meet the requirements of this Regulation and protect the rights of data subjects.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:35', '2024-08-14 08:38:35'),
(1891, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1627, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller shall implement appropriate technical and organisational measures for ensuring that, by default, only personal data which are necessary for each specific purpose of the processing are processed.That obligation applies to the amount of personal data collected, the extent of their processing, the period of their storage and their accessibility. In particular, such measures shall ensure that by default personal data are not made accessible without the individual\\u2019s intervention to an indefinite number of natural persons.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:35', '2024-08-14 08:38:35'),
(1892, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1628, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"An approved certification mechanism pursuant to Article 42 may be used as an element to demonstrate compliance with the requirements set out in paragraphs 1 and 2 of this Article.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:36', '2024-08-14 08:38:36'),
(1893, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1629, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where two or more controllers jointly determine the purposes and means of processing, they shall be joint controllers.They shall in a transparent manner determine their respective responsibilities for compliance with the obligations under this Regulation, in particular as regards the exercising of the rights of the data subject and their respective duties to provide the information referred to in Articles 13 and 14, by means of an arrangement between them unless, and in so far as, the respective responsibilities of the controllers are determined by Union or Member State law to which the controllers are subject.The arrangement may designate a contact point for data subjects.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:39', '2024-08-14 08:38:39'),
(1894, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1630, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The arrangement referred to in paragraph 1 shall duly reflect the respective roles and relationships of the joint controllers vis-\\u00e0-vis the data subjects.The essence of the arrangement shall be made available to the data subject.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:39', '2024-08-14 08:38:39'),
(1895, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1631, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Irrespective of the terms of the arrangement referred to in paragraph 1, the data subject may exercise his or her rights under this Regulation in respect of and against each of the controllers.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:40', '2024-08-14 08:38:40'),
(1896, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1632, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where Article 3(2) applies, the controller or the processor shall designate in writing a representative in the Union.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:42', '2024-08-14 08:38:42'),
(1897, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1633, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The obligation laid down in paragraph 1 of this Article shall not apply to processing which is occasional, does not include, on a large scale, processing of special categories of data as referred to in Article 9(1) or processing of personal data relating to criminal convictions and offences referred to in Article 10, and is unlikely to result in a risk to the rights and freedoms of natural persons, taking into account the nature, context, scope and purposes of the processing.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:43', '2024-08-14 08:38:43'),
(1898, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1634, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The obligation laid down in paragraph 1 of this Article shall not apply to a public authority or body.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:45', '2024-08-14 08:38:45'),
(1899, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1635, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The representative shall be established in one of the Member States where the data subjects, whose personal data are processed in relation to the offering of goods or services to them, or whose behaviour is monitored.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:46', '2024-08-14 08:38:46'),
(1900, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1636, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The representative shall be mandated by the controller or processor to be addressed in addition to or instead of the controller or the processor by, in particular, supervisory authorities and data subjects, on all issues related to processing, for the purposes of ensuring compliance with this Regulation.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:47', '2024-08-14 08:38:47'),
(1901, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1637, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The designation of a representative by the controller or processor shall be without prejudice to legal actions which could be initiated against the controller or the processor themselves.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:50', '2024-08-14 08:38:50'),
(1902, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1638, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where processing is to be carried out on behalf of a controller, the controller shall use only processors providing sufficient guarantees to implement appropriate technical and organisational measures in such a manner that processing will meet the requirements of this Regulation and ensure the protection of the rights of the data subject.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:50', '2024-08-14 08:38:50'),
(1903, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1639, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The processor shall not engage another processor without prior specific or general written authorisation of the controller.In the case of general written authorisation, the processor shall inform the controller of any intended changes concerning the addition or replacement of other processors, thereby giving the controller the opportunity to object to such changes.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:51', '2024-08-14 08:38:51'),
(1904, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1640, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing by a processor shall be governed by a contract or other legal act under Union or Member State law, that is binding on the processor with regard to the controller and that sets out the subject-matter and duration of the processing, the nature and purpose of the processing, the type of personal data and categories of data subjects and the obligations and rights of the controller. That contract or other legal act shall stipulate, in particular, that the processor processes the personal data only on documented instructions from the controller, including with regard to transfers of personal data to a third country or an international organisation, unless required to do so by Union or Member State law to which the processor is subject; in such a case, the processor shall inform the controller of that legal requirement before processing, unless that law prohibits such information on important grounds of public interest;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:53', '2024-08-14 08:38:53'),
(1905, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1641, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing by a processor shall be governed by a contract or other legal act under Union or Member State law, that is binding on the processor with regard to the controller and that sets out the subject-matter and duration of the processing, the nature and purpose of the processing, the type of personal data and categories of data subjects and the obligations and rights of the controller. That contract or other legal act shall stipulate, in particular, that the processor ensures that persons authorised to process the personal data have committed themselves to confidentiality or are under an appropriate statutory obligation of confidentiality;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:56', '2024-08-14 08:38:56'),
(1906, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1642, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing by a processor shall be governed by a contract or other legal act under Union or Member State law, that is binding on the processor with regard to the controller and that sets out the subject-matter and duration of the processing, the nature and purpose of the processing, the type of personal data and categories of data subjects and the obligations and rights of the controller. That contract or other legal act shall stipulate, in particular, that the processor takes all measures required pursuant to Article 32;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:38:57', '2024-08-14 08:38:57'),
(1907, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1643, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing by a processor shall be governed by a contract or other legal act under Union or Member State law, that is binding on the processor with regard to the controller and that sets out the subject-matter and duration of the processing, the nature and purpose of the processing, the type of personal data and categories of data subjects and the obligations and rights of the controller. That contract or other legal act shall stipulate, in particular, that the processor respects the conditions referred to in paragraphs 2 and 4 for engaging another processor;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:00', '2024-08-14 08:39:00'),
(1908, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1644, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing by a processor shall be governed by a contract or other legal act under Union or Member State law, that is binding on the processor with regard to the controller and that sets out the subject-matter and duration of the processing, the nature and purpose of the processing, the type of personal data and categories of data subjects and the obligations and rights of the controller. That contract or other legal act shall stipulate, in particular, that the processor taking into account the nature of the processing, assists the controller by appropriate technical and organisational measures, insofar as this is possible, for the fulfilment of the controller\\u2019s obligation to respond to requests for exercising the data subject\\u2019s rights laid down in Chapter III;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:01', '2024-08-14 08:39:01'),
(1909, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1645, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing by a processor shall be governed by a contract or other legal act under Union or Member State law, that is binding on the processor with regard to the controller and that sets out the subject-matter and duration of the processing, the nature and purpose of the processing, the type of personal data and categories of data subjects and the obligations and rights of the controller. That contract or other legal act shall stipulate, in particular, that the processor assists the controller in ensuring compliance with the obligations pursuant to Articles 32 to 36 taking into account the nature of processing and the information available to the processor;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:04', '2024-08-14 08:39:04'),
(1910, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1646, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing by a processor shall be governed by a contract or other legal act under Union or Member State law, that is binding on the processor with regard to the controller and that sets out the subject-matter and duration of the processing, the nature and purpose of the processing, the type of personal data and categories of data subjects and the obligations and rights of the controller. That contract or other legal act shall stipulate, in particular, that the processor at the choice of the controller, deletes or returns all the personal data to the controller after the end of the provision of services relating to processing, and deletes existing copies unless Union or Member State law requires storage of the personal data;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:05', '2024-08-14 08:39:05');
INSERT INTO `activity_log` (`id`, `log_name`, `description`, `subject_type`, `event`, `subject_id`, `causer_type`, `causer_id`, `properties`, `batch_uuid`, `created_at`, `updated_at`) VALUES
(1911, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1647, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Processing by a processor shall be governed by a contract or other legal act under Union or Member State law, that is binding on the processor with regard to the controller and that sets out the subject-matter and duration of the processing, the nature and purpose of the processing, the type of personal data and categories of data subjects and the obligations and rights of the controller. That contract or other legal act shall stipulate, in particular, that the processor makes available to the controller all information necessary to demonstrate compliance with the obligations laid down in this Article and allow for and contribute to audits, including inspections, conducted by the controller or another auditor mandated by the controller. With regard to point (h) of the first subparagraph, the processor shall immediately inform the controller if, in its opinion, an instruction infringes this Regulation or other Union or Member State data protection provisions.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:07', '2024-08-14 08:39:07'),
(1912, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1648, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where a processor engages another processor for carrying out specific processing activities on behalf of the controller, the same data protection obligations as set out in the contract or other legal act between the controller and the processor as referred to in paragraph 3 shall be imposed on that other processor by way of a contract or other legal act under Union or Member State law, in particular providing sufficient guarantees to implement appropriate technical and organisational measures in such a manner that the processing will meet the requirements of this Regulation.Where that other processor fails to fulfil its data protection obligations, the initial processor shall remain fully liable to the controller for the performance of that other processor\\u2019s obligations.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:10', '2024-08-14 08:39:10'),
(1913, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1649, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Adherence of a processor to an approved code of conduct as referred to in Article 40 or an approved certification mechanism as referred to in Article 42 may be used as an element by which to demonstrate sufficient guarantees as referred to in paragraphs 1 and 4 of this Article.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:11', '2024-08-14 08:39:11'),
(1914, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1650, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Without prejudice to an individual contract between the controller and the processor, the contract or the other legal act referred to in paragraphs 3 and 4 of this Article may be based, in whole or in part, on standard contractual clauses referred to in paragraphs 7 and 8 of this Article, including when they are part of a certification granted to the controller or processor pursuant to Articles 42 and 43.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:13', '2024-08-14 08:39:13'),
(1915, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1651, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Commission may lay down standard contractual clauses for the matters referred to in paragraph 3 and 4 of this Article and in accordance with the examination procedure referred to in Article 93(2).\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:14', '2024-08-14 08:39:14'),
(1916, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1652, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A supervisory authority may adopt standard contractual clauses for the matters referred to in paragraph 3 and 4 of this Article and in accordance with the consistency mechanism referred to in Article 63.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:15', '2024-08-14 08:39:15'),
(1917, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1653, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The contract or the other legal act referred to in paragraphs 3 and 4 shall be in writing, including in electronic form.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:16', '2024-08-14 08:39:16'),
(1918, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1654, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Without prejudice to Articles 82, 83 and 84, if a processor infringes this Regulation by determining the purposes and means of processing, the processor shall be considered to be a controller in respect of that processing.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:16', '2024-08-14 08:39:16'),
(1919, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1655, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The processor and any person acting under the authority of the controller or of the processor, who has access to personal data, shall not process those data except on instructions from the controller, unless required to do so by Union or Member State law.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:19', '2024-08-14 08:39:19'),
(1920, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1656, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Each controller and, where applicable, the controller\\u2019s representative, shall maintain a record of processing activities under its responsibility. That record shall contain the name and contact details of the controller and, where applicable, the joint controller, the controller\\u2019s representative and the data protection officer;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:20', '2024-08-14 08:39:20'),
(1921, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1657, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Each controller and, where applicable, the controller\\u2019s representative, shall maintain a record of processing activities under its responsibility. That record shall contain the purposes of the processing;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:21', '2024-08-14 08:39:21'),
(1922, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1658, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Each controller and, where applicable, the controller\\u2019s representative, shall maintain a record of processing activities under its responsibility. That record shall contain a description of the categories of data subjects and of the categories of personal data;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:22', '2024-08-14 08:39:22'),
(1923, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1659, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Each controller and, where applicable, the controller\\u2019s representative, shall maintain a record of processing activities under its responsibility. That record shall contain the categories of recipients to whom the personal data have been or will be disclosed including recipients in third countries or international organisations;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:25', '2024-08-14 08:39:25'),
(1924, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1660, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Each controller and, where applicable, the controller\\u2019s representative, shall maintain a record of processing activities under its responsibility. That record shall contain applicable transfers of personal data to a third country or an international organisation, including the identification of that third country or international organisation and, in the case of transfers referred to in the second subparagraph of Article 49(1), the documentation of suitable safeguards;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:26', '2024-08-14 08:39:26'),
(1925, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1661, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Each controller and, where applicable, the controller\\u2019s representative, shall maintain a record of processing activities under its responsibility. That record shall contain all of the following information where possible, the envisaged time limits for erasure of the different categories of data;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:27', '2024-08-14 08:39:27'),
(1926, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1662, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Each controller and, where applicable, the controller\\u2019s representative, shall maintain a record of processing activities under its responsibility. That record shall contain a general description of the technical and organisational security measures referred to in Article 32(1).\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:30', '2024-08-14 08:39:30'),
(1927, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1663, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Each processor and, where applicable, the processor\\u2019s representative shall maintain a record of all categories of processing activities carried out on behalf of a controller, containing the name and contact details of the processor or processors and of each controller on behalf of which the processor is acting, and, where applicable, of the controller\\u2019s or the processor\\u2019s representative, and the data protection officer;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:31', '2024-08-14 08:39:31'),
(1928, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1664, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Each processor and, where applicable, the processor\\u2019s representative shall maintain a record of all categories of processing activities carried out on behalf of a controller, containing the categories of processing carried out on behalf of each controller;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:32', '2024-08-14 08:39:32'),
(1929, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1665, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Each processor and, where applicable, the processor\\u2019s representative shall maintain a record of all categories of processing activities carried out on behalf of a controller, containing where applicable, transfers of personal data to a third country or an international organisation, including the identification of that third country or international organisation and, in the case of transfers referred to in the second subparagraph of Article 49(1), the documentation of suitable safeguards;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:34', '2024-08-14 08:39:34'),
(1930, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1666, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Each processor and, where applicable, the processor\\u2019s representative shall maintain a record of all categories of processing activities carried out on behalf of a controller, containing where possible, a general description of the technical and organisational security measures referred to in Article 32(1).\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:35', '2024-08-14 08:39:35'),
(1931, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1667, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The records referred to in paragraphs 1 and 2 shall be in writing, including in electronic form.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:36', '2024-08-14 08:39:36'),
(1932, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1668, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller or the processor and, where applicable, the controller\\u2019s or the processor\\u2019s representative, shall make the record available to the supervisory authority on request.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:37', '2024-08-14 08:39:37'),
(1933, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1669, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The obligations referred to in paragraphs 1 and 2 shall not apply to an enterprise or an organisation employing fewer than 250 persons unless the processing it carries out is likely to result in a risk to the rights and freedoms of data subjects, the processing is not occasional, or the processing includes special categories of data as referred to in Article 9(1) or personal data relating to criminal convictions and offences referred to in Article 10.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:39', '2024-08-14 08:39:39'),
(1934, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1670, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller and the processor and, where applicable, their representatives, shall cooperate, on request, with the supervisory authority in the performance of its tasks.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:40', '2024-08-14 08:39:40'),
(1935, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1671, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Taking into account the state of the art, the costs of implementation and the nature, scope, context and purposes of processing as well as the risk of varying likelihood and severity for the rights and freedoms of natural persons, the controller and the processor shall implement appropriate - technical and organisational measures to ensure a level of security appropriate - to the risk, including inter alia as appropriate - the pseudonymisation and encryption of personal data;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:42', '2024-08-14 08:39:42'),
(1936, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1672, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Taking into account the state of the art, the costs of implementation and the nature, scope, context and purposes of processing as well as the risk of varying likelihood and severity for the rights and freedoms of natural persons, the controller and the processor shall implement appropriate - technical and organisational measures to ensure a level of security appropriate - to the risk, including inter alia as appropriate - the ability to ensure the ongoing confidentiality, integrity, availability and resilience of processing systems and services;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:43', '2024-08-14 08:39:43'),
(1937, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1673, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Taking into account the state of the art, the costs of implementation and the nature, scope, context and purposes of processing as well as the risk of varying likelihood and severity for the rights and freedoms of natural persons, the controller and the processor shall implement appropriate - technical and organisational measures to ensure a level of security appropriate - to the risk, including inter alia as appropriate - the ability to restore the availability and access to personal data in a timely manner in the event of a physical or technical incident;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:46', '2024-08-14 08:39:46'),
(1938, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1674, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Taking into account the state of the art, the costs of implementation and the nature, scope, context and purposes of processing as well as the risk of varying likelihood and severity for the rights and freedoms of natural persons, the controller and the processor shall implement appropriate - technical and organisational measures to ensure a level of security appropriate - to the risk, including inter alia as appropriate - a process for regularly testing, assessing and evaluating the effectiveness of technical and organisational measures for ensuring the security of the processing.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:47', '2024-08-14 08:39:47'),
(1939, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1675, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In assessing the appropriate level of security account shall be taken in particular of the risks that are presented by processing, in particular from accidental or unlawful destruction, loss, alteration, unauthorised disclosure of, or access to personal data transmitted, stored or otherwise processed.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:50', '2024-08-14 08:39:50'),
(1940, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1676, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Adherence to an approved code of conduct as referred to in Article 40 or an approved certification mechanism as referred to in Article 42 may be used as an element by which to demonstrate compliance with the requirements set out in paragraph 1 of this Article.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:51', '2024-08-14 08:39:51'),
(1941, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1677, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller and processor shall take steps to ensure that any natural person acting under the authority of the controller or the processor who has access to personal data does not process them except on instructions from the controller, unless he or she is required to do so by Union or Member State law.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:52', '2024-08-14 08:39:52'),
(1942, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1678, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In the case of a personal data breach, the controller shall without undue delay and, where feasible, not later than 72 hours after having become aware of it, notify the personal data breach to the supervisory authority competent in accordance with Article 55, unless the personal data breach is unlikely to result in a risk to the rights and freedoms of natural persons.Where the notification to the supervisory authority is not made within 72 hours, it shall be accompanied by reasons for the delay.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:53', '2024-08-14 08:39:53'),
(1943, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1679, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The notification referred to in paragraph 1 shall at least describe the nature of the personal data breach including where possible, the categories and approximate number of data subjects concerned and the categories and approximate number of personal data records concerned;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:59', '2024-08-14 08:39:59'),
(1944, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1680, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The processor shall notify the controller without undue delay after becoming aware of a personal data breach.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:39:59', '2024-08-14 08:39:59'),
(1945, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1681, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The notification referred to in paragraph 1 shall at least communicate the name and contact details of the data protection officer or other contact point where more information can be obtained;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:00', '2024-08-14 08:40:00'),
(1946, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1682, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The notification referred to in paragraph 1 shall at least describe the likely consequences of the personal data breach;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:01', '2024-08-14 08:40:01'),
(1947, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1683, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The notification referred to in paragraph 1 shall at least describe the measures taken or proposed to be taken by the controller to address the personal data breach, including, where appropriate, measures to mitigate its possible adverse effects.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:01', '2024-08-14 08:40:01'),
(1948, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1684, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where, and in so far as, it is not possible to provide the information at the same time, the information may be provided in phases without undue further delay.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:02', '2024-08-14 08:40:02'),
(1949, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1685, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller shall document any personal data breaches, comprising the facts relating to the personal data breach, its effects and the remedial action taken.That documentation shall enable the supervisory authority to verify compliance with this Article.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:05', '2024-08-14 08:40:05'),
(1950, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1686, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"When the personal data breach is likely to result in a high risk to the rights and freedoms of natural persons, the controller shall communicate the personal data breach to the data subject without undue delay.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:09', '2024-08-14 08:40:09'),
(1951, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1687, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The communication to the data subject referred to in paragraph 1 of this Article shall describe in clear and plain language the nature of the personal data breach and contain at least the information and measures referred to in points (b), (c) and (d) of Article 33(3).\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:10', '2024-08-14 08:40:10'),
(1952, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1688, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The communication to the data subject referred to in paragraph 1 shall not be required the controller has implemented appropriate technical and organisational protection measures, and those measures were applied to the personal data affected by the personal data breach, in particular those that render the personal data unintelligible to any person who is not authorised to access it, such as encryption;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:11', '2024-08-14 08:40:11'),
(1953, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1689, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The communication to the data subject referred to in paragraph 1 shall not be required the controller has taken subsequent measures which ensure that the high risk to the rights and freedoms of data subjects referred to in paragraph 1 is no longer likely to materialise;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:12', '2024-08-14 08:40:12'),
(1954, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1690, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"iThe communication to the data subject referred to in paragraph 1 shall not be required t would involve disproportionate effort. In such a case, there shall instead be a public communication or similar measure whereby the data subjects are informed in an equally effective manner.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:16', '2024-08-14 08:40:16'),
(1955, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1691, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"If the controller has not already communicated the personal data breach to the data subject, the supervisory authority, having considered the likelihood of the personal data breach resulting in a high risk, may require it to do so or may decide that any of the conditions referred to in paragraph 3 are met.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:17', '2024-08-14 08:40:17'),
(1956, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1692, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where a type of processing in particular using new technologies, and taking into account the nature, scope, context and purposes of the processing, is likely to result in a high risk to the rights and freedoms of natural persons, the controller shall, prior to the processing, carry out an assessment of the impact of the envisaged processing operations on the protection of personal data.A single assessment may address a set of similar processing operations that present similar high risks.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:20', '2024-08-14 08:40:20'),
(1957, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1693, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller shall seek the advice of the data protection officer, where designated, when carrying out a data protection impact assessment.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:21', '2024-08-14 08:40:21'),
(1958, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1694, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A data protection impact assessment referred to in paragraph 1 shall in particular be required in the case of a systematic and extensive evaluation of personal aspects relating to natural persons which is based on automated processing, including profiling, and on which decisions are based that produce legal effects concerning the natural person or similarly significantly affect the natural person;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:24', '2024-08-14 08:40:24'),
(1959, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1695, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A data protection impact assessment referred to in paragraph 1 shall in particular be required in the case of processing on a large scale of special categories of data referred to in Article 9(1), or of personal data relating to criminal convictions and offences referred to in Article 10; or\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:25', '2024-08-14 08:40:25'),
(1960, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1696, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A data protection impact assessment referred to in paragraph 1 shall in particular be required in the case of a systematic monitoring of a publicly accessible area on a large scale.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:25', '2024-08-14 08:40:25'),
(1961, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1697, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The supervisory authority shall establish and make public a list of the kind of processing operations which are subject to the requirement for a data protection impact assessment pursuant to paragraph 1.The supervisory authority shall communicate those lists to the Board referred to in Article 68.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:27', '2024-08-14 08:40:27'),
(1962, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1698, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The supervisory authority may also establish and make public a list of the kind of processing operations for which no data protection impact assessment is required. The supervisory authority shall communicate those lists to the Board.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:29', '2024-08-14 08:40:29'),
(1963, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1699, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Prior to the adoption of the lists referred to in paragraphs 4 and 5, the competent supervisory authority shall apply the consistency mechanism referred to in Article 63 where such lists involve processing activities which are related to the offering of goods or services to data subjects or to the monitoring of their behaviour in several Member States, or may substantially affect the free movement of personal data within the Union.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:30', '2024-08-14 08:40:30'),
(1964, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1700, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The assessment shall contain an assessment of the necessity and proportionality of the processing operations in relation to the purposes;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:32', '2024-08-14 08:40:32'),
(1965, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1701, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The assessment shall contain an assessment of the risks to the rights and freedoms of data subjects referred to in paragraph 1; and\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:33', '2024-08-14 08:40:33'),
(1966, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1702, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The assessment shall contain a systematic description of the envisaged processing operations and the purposes of the processing, including, where applicable, the legitimate interest pursued by the controller;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:34', '2024-08-14 08:40:34'),
(1967, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1703, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The assessment shall contain the measures envisaged to address the risks, including safeguards, security measures and mechanisms to ensure the protection of personal data and to demonstrate compliance with this Regulation taking into account the rights and legitimate interests of data subjects and other persons concerned.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:38', '2024-08-14 08:40:38'),
(1968, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1704, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Compliance with approved codes of conduct referred to in Article 40 by the relevant controllers or processors shall be taken into due account in assessing the impact of the processing operations performed by such controllers or processors, in particular for the purposes of a data protection impact assessment.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:38', '2024-08-14 08:40:38'),
(1969, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1705, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where appropriate, the controller shall seek the views of data subjects or their representatives on the intended processing, without prejudice to the protection of commercial or public interests or the security of processing operations.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:40', '2024-08-14 08:40:40'),
(1970, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1706, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where processing pursuant to point (c) or (e) of Article 6(1) has a legal basis in Union law or in the law of the Member State to which the controller is subject, that law regulates the specific processing operation or set of operations in question, and a data protection impact assessment has already been carried out as part of a general impact assessment in the context of the adoption of that legal basis, paragraphs 1 to 7 shall not apply unless Member States deem it to be necessary to carry out such an assessment prior to processing activities.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:42', '2024-08-14 08:40:42'),
(1971, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1707, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where necessary, the controller shall carry out a review to assess if processing is performed in accordance with the data protection impact assessment at least when there is a change of the risk represented by processing operations.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:43', '2024-08-14 08:40:43'),
(1972, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1708, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller shall consult the supervisory authority prior to processing where a data protection impact assessment under Article 35 indicates that the processing would result in a high risk in the absence of measures taken by the controller to mitigate the risk.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:44', '2024-08-14 08:40:44'),
(1973, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1709, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where the supervisory authority is of the opinion that the intended processing referred to in paragraph 1 would infringe this Regulation, in particular where the controller has insufficiently identified or mitigated the risk, the supervisory authority shall, within period of up to eight weeks of receipt of the request for consultation, provide written advice to the controller and, where applicable to the processor, and may use any of its powers referred to in Article 58.That period may be extended by six weeks, taking into account the complexity of the intended processing.The supervisory authority shall inform the controller and, where applicable, the processor, of any such extension within one month of receipt of the request for consultation together with the reasons for the delay.Those periods may be suspended until the supervisory authority has obtained information it has requested for the purposes of the consultation.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:47', '2024-08-14 08:40:47'),
(1974, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1710, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"When consulting the supervisory authority pursuant to paragraph 1, the controller shall provide the supervisory authority with where applicable, the respective responsibilities of the controller, joint controllers and processors involved in the processing, in particular for processing within a group of undertakings;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:50', '2024-08-14 08:40:50'),
(1975, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1711, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"When consulting the supervisory authority pursuant to paragraph 1, the controller shall provide the supervisory authority with the purposes and means of the intended processing;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:51', '2024-08-14 08:40:51'),
(1976, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1712, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"When consulting the supervisory authority pursuant to paragraph 1, the controller shall provide the supervisory authority with the measures and safeguards provided to protect the rights and freedoms of data subjects pursuant to this Regulation;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:52', '2024-08-14 08:40:52'),
(1977, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1713, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"When consulting the supervisory authority pursuant to paragraph 1, the controller shall provide the supervisory authority with where applicable, the contact details of the data protection officer;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:52', '2024-08-14 08:40:52'),
(1978, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1714, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"When consulting the supervisory authority pursuant to paragraph 1, the controller shall provide the supervisory authority with the data protection impact assessment provided for in Article 35; and\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:55', '2024-08-14 08:40:55'),
(1979, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1715, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"When consulting the supervisory authority pursuant to paragraph 1, the controller shall provide the supervisory authority with any other information requested by the supervisory authority.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:56', '2024-08-14 08:40:56'),
(1980, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1716, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Member States shall consult the supervisory authority during the preparation of a proposal for a legislative measure to be adopted by a national parliament, or of a regulatory measure based on such a legislative measure, which relates to processing.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:56', '2024-08-14 08:40:56'),
(1981, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1717, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Notwithstanding paragraph 1, Member State law may require controllers to consult with, and obtain prior authorisation from, the supervisory authority in relation to processing by a controller for the performance of a task carried out by the controller in the public interest, including processing in relation to social protection and public health.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:40:57', '2024-08-14 08:40:57'),
(1982, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1718, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller and the processor shall designate a data protection officer in any case where the processing is carried out by a public authority or body, except for courts acting in their judicial capacity;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:00', '2024-08-14 08:41:00'),
(1983, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1719, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller and the processor shall designate a data protection officer in any case where the core activities of the controller or the processor consist of processing operations which, by virtue of their nature, their scope and\\/or their purposes, require regular and systematic monitoring of data subjects on a large scale; or\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:01', '2024-08-14 08:41:01'),
(1984, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1720, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller and the processor shall designate a data protection officer in any case where the core activities of the controller or the processor consist of processing on a large scale of special categories of data pursuant to Article 9 or personal data relating to criminal convictions and offences referred to in Article 10.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:02', '2024-08-14 08:41:02'),
(1985, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1721, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A group of undertakings may appoint a single data protection officer provided that a data protection officer is easily accessible from each establishment.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:04', '2024-08-14 08:41:04'),
(1986, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1722, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where the controller or the processor is a public authority or body, a single data protection officer may be designated for several such authorities or bodies, taking account of their organisational structure and size.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:07', '2024-08-14 08:41:07'),
(1987, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1723, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In cases other than those referred to in paragraph 1, the controller or processor or associations and other bodies representing categories of controllers or processors may or, where required by Union or Member State law shall, designate a data protection officer.The data protection officer may act for such associations and other bodies representing controllers or processors.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:08', '2024-08-14 08:41:08'),
(1988, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1724, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data protection officer shall be designated on the basis of professional qualities and, in particular, expert knowledge of data protection law and practices and the ability to fulfil the tasks referred to in Article 39.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:09', '2024-08-14 08:41:09'),
(1989, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1725, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data protection officer may be a staff member of the controller or processor, or fulfil the tasks on the basis of a service contract.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:10', '2024-08-14 08:41:10'),
(1990, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1726, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller or the processor shall publish the contact details of the data protection officer and communicate them to the supervisory authority.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:14', '2024-08-14 08:41:14'),
(1991, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1727, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller and the processor shall ensure that the data protection officer is involved, properly and in a timely manner, in all issues which relate to the protection of personal data.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:14', '2024-08-14 08:41:14'),
(1992, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1728, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller and processor shall support the data protection officer in performing the tasks referred to in Article 39 by providing resources necessary to carry out those tasks and access to personal data and processing operations, and to maintain his or her expert knowledge.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:15', '2024-08-14 08:41:15');
INSERT INTO `activity_log` (`id`, `log_name`, `description`, `subject_type`, `event`, `subject_id`, `causer_type`, `causer_id`, `properties`, `batch_uuid`, `created_at`, `updated_at`) VALUES
(1993, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1729, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller and processor shall ensure that the data protection officer does not receive any instructions regarding the exercise of those tasks.He or she shall not be dismissed or penalised by the controller or the processor for performing his tasks.The data protection officer shall directly report to the highest management level of the controller or the processor.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:17', '2024-08-14 08:41:17'),
(1994, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1730, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Data subjects may contact the data protection officer with regard to all issues related to processing of their personal data and to the exercise of their rights under this Regulation.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:18', '2024-08-14 08:41:18'),
(1995, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1731, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data protection officer shall be bound by secrecy or confidentiality concerning the performance of his or her tasks, in accordance with Union or Member State law.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:18', '2024-08-14 08:41:18'),
(1996, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1732, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data protection officer may fulfil other tasks and duties.The controller or processor shall ensure that any such tasks and duties do not result in a conflict of interests.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:19', '2024-08-14 08:41:19'),
(1997, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1733, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data protection officer shall have to inform and advise the controller or the processor and the employees who carry out processing of their obligations pursuant to this Regulation and to other Union or Member State data protection provisions;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:21', '2024-08-14 08:41:21'),
(1998, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1734, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data protection officer shall have to monitor compliance with this Regulation, with other Union or Member State data protection provisions and with the policies of the controller or processor in relation to the protection of personal data, including the assignment of responsibilities, awareness-raising and training of staff involved in processing operations, and the related audits;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:22', '2024-08-14 08:41:22'),
(1999, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1735, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data protection officer shall have to provide advice where requested as regards the data protection impact assessment and monitor its performance pursuant to Article 35;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:23', '2024-08-14 08:41:23'),
(2000, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1736, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data protection officer shall have to cooperate with the supervisory authority;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:26', '2024-08-14 08:41:26'),
(2001, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1737, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data protection officer shall have to act as the contact point for the supervisory authority on issues relating to processing, including the prior consultation referred to in Article 36, and to consult, where appropriate, with regard to any other matter.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:27', '2024-08-14 08:41:27'),
(2002, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1738, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The data protection officer shall in the performance of his or her tasks have due regard to the risk associated with processing operations, taking into account the nature, scope, context and purposes of processing.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:28', '2024-08-14 08:41:28'),
(2003, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1739, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Member States, the supervisory authorities, the Board and the Commission shall encourage the drawing up of codes of conduct intended to contribute to the proper application of this Regulation, taking account of the specific features of the various processing sectors and the specific needs of micro, small and medium-sized enterprises.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:31', '2024-08-14 08:41:31'),
(2004, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1740, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Associations and other bodies representing categories of controllers or processors may prepare codes of conduct, or amend or extend such codes, for the purpose of specifying the application of this Regulation, such as with regard to fair and transparent processing;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:31', '2024-08-14 08:41:31'),
(2005, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1741, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Associations and other bodies representing categories of controllers or processors may prepare codes of conduct, or amend or extend such codes, for the purpose of specifying the application of this Regulation, such as with regard to the legitimate interests pursued by controllers in specific contexts;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:32', '2024-08-14 08:41:32'),
(2006, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1742, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Associations and other bodies representing categories of controllers or processors may prepare codes of conduct, or amend or extend such codes, for the purpose of specifying the application of this Regulation, such as with regard to the collection of personal data;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:35', '2024-08-14 08:41:35'),
(2007, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1743, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Associations and other bodies representing categories of controllers or processors may prepare codes of conduct, or amend or extend such codes, for the purpose of specifying the application of this Regulation, such as with regard to the pseudonymisation of personal data;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:35', '2024-08-14 08:41:35'),
(2008, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1744, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Associations and other bodies representing categories of controllers or processors may prepare codes of conduct, or amend or extend such codes, for the purpose of specifying the application of this Regulation, such as with regard to the information provided to the public and to data subjects;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:38', '2024-08-14 08:41:38'),
(2009, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1745, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Associations and other bodies representing categories of controllers or processors may prepare codes of conduct, or amend or extend such codes, for the purpose of specifying the application of this Regulation, such as with regard to the exercise of the rights of data subjects;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:38', '2024-08-14 08:41:38'),
(2010, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1746, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Associations and other bodies representing categories of controllers or processors may prepare codes of conduct, or amend or extend such codes, for the purpose of specifying the application of this Regulation, such as with regard to the information provided to, and the protection of, children, and the manner in which the consent of the holders of parental responsibility over children is to be obtained;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:42', '2024-08-14 08:41:42'),
(2011, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1747, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Associations and other bodies representing categories of controllers or processors may prepare codes of conduct, or amend or extend such codes, for the purpose of specifying the application of this Regulation, such as with regard to the measures and procedures referred to in Articles 24 and 25 and the measures to ensure security of processing referred to in Article 32;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:42', '2024-08-14 08:41:42'),
(2012, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1748, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Associations and other bodies representing categories of controllers or processors may prepare codes of conduct, or amend or extend such codes, for the purpose of specifying the application of this Regulation, such as with regard to the notification of personal data breaches to supervisory authorities and the communication of such personal data breaches to data subjects;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:44', '2024-08-14 08:41:44'),
(2013, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1749, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Associations and other bodies representing categories of controllers or processors may prepare codes of conduct, or amend or extend such codes, for the purpose of specifying the application of this Regulation, such as with regard to the transfer of personal data to third countries or international organisations; or\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:47', '2024-08-14 08:41:47'),
(2014, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1750, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Associations and other bodies representing categories of controllers or processors may prepare codes of conduct, or amend or extend such codes, for the purpose of specifying the application of this Regulation, such as with regard to out-of-court proceedings and other dispute resolution procedures for resolving disputes between controllers and data subjects with regard to processing, without prejudice to the rights of data subjects pursuant to Articles 77 and 79.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:47', '2024-08-14 08:41:47'),
(2015, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1751, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In addition to adherence by controllers or processors subject to this Regulation, codes of conduct approved pursuant to paragraph 5 of this Article and having general validity pursuant to paragraph 9 of this Article may also be adhered to by controllers or processors that are not subject to this Regulation pursuant to Article 3 in order to provide appropriate safeguards within the framework of personal data transfers to third countries or international organisations under the terms referred to in point (e) of Article 46(2).Such controllers or processors shall make binding and enforceable commitments, via contractual or other legally binding instruments, to apply those appropriate safeguards including with regard to the rights of data subjects.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:48', '2024-08-14 08:41:48'),
(2016, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1752, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A code of conduct referred to in paragraph 2 of this Article shall contain mechanisms which enable the body referred to in Article 41(1) to carry out the mandatory monitoring of compliance with its provisions by the controllers or processors which undertake to apply it, without prejudice to the tasks and powers of supervisory authorities competent pursuant to Article 55 or 56.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:50', '2024-08-14 08:41:50'),
(2017, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1753, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Associations and other bodies referred to in paragraph 2 of this Article which intend to prepare a code of conduct or to amend or extend an existing code shall submit the draft code, amendment or extension to the supervisory authority which is competent pursuant to Article 55.The supervisory authority shall provide an opinion on whether the draft code, amendment or extension complies with this Regulation and shall approve that draft code, amendment or extension if it finds that it provides sufficient appropriate safeguards.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:51', '2024-08-14 08:41:51'),
(2018, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1754, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where the draft code, or amendment or extension is approved in accordance with paragraph 5, and where the code of conduct concerned does not relate to processing activities in several Member States, the supervisory authority shall register and publish the code.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:54', '2024-08-14 08:41:54'),
(2019, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1755, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where a draft code of conduct relates to processing activities in several Member States, the supervisory authority which is competent pursuant to Article 55 shall, before approving the draft code, amendment or extension, submit it in the procedure referred to in Article 63 to the Board which shall provide an opinion on whether the draft code, amendment or extension complies with this Regulation or, in the situation referred to in paragraph 3 of this Article, provides appropriate safeguards.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:55', '2024-08-14 08:41:55'),
(2020, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1756, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Where the opinion referred to in paragraph 7 confirms that the draft code, amendment or extension complies with this Regulation, or, in the situation referred to in paragraph 3, provides appropriate safeguards, the Board shall submit its opinion to the Commission.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:56', '2024-08-14 08:41:56'),
(2021, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1757, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Commission may, by way of implementing acts, decide that the approved code of conduct, amendment or extension submitted to it pursuant to paragraph 8 of this Article have general validity within the Union.Those implementing acts shall be adopted in accordance with the examination procedure set out in Article 93(2).\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:41:57', '2024-08-14 08:41:57'),
(2022, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1758, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Commission shall ensure appropriate publicity for the approved codes which have been decided as having general validity in accordance with paragraph 9.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:00', '2024-08-14 08:42:00'),
(2023, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1759, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Board shall collate all approved codes of conduct, amendments and extensions in a register and shall make them publicly available by way of appropriate means.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:00', '2024-08-14 08:42:00'),
(2024, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1760, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Without prejudice to the tasks and powers of the competent supervisory authority under Articles 57 and 58, the monitoring of compliance with a code of conduct pursuant to Article 40 may be carried out by a body which has an appropriate level of expertise in relation to the subject-matter of the code and is accredited for that purpose by the competent supervisory authority.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:01', '2024-08-14 08:42:01'),
(2025, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1761, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A body as referred to in paragraph 1 may be accredited to monitor compliance with a code of conduct where that body has demonstrated its independence and expertise in relation to the subject-matter of the code to the satisfaction of the competent supervisory authority;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:03', '2024-08-14 08:42:03'),
(2026, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1762, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A body as referred to in paragraph 1 may be accredited to monitor compliance with a code of conduct where that body has established procedures which allow it to assess the eligibility of controllers and processors concerned to apply the code, to monitor their compliance with its provisions and to periodically review its operation;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:04', '2024-08-14 08:42:04'),
(2027, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1763, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A body as referred to in paragraph 1 may be accredited to monitor compliance with a code of conduct where that body has established procedures and structures to handle complaints about infringements of the code or the manner in which the code has been, or is being, implemented by a controller or processor, and to make those procedures and structures transparent to data subjects and the public;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:07', '2024-08-14 08:42:07'),
(2028, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1764, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A body as referred to in paragraph 1 may be accredited to monitor compliance with a code of conduct where that body has demonstrated to the satisfaction of the competent supervisory authority that its tasks and duties do not result in a conflict of interests.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:08', '2024-08-14 08:42:08'),
(2029, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1765, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The competent supervisory authority shall submit the draft requirements for accreditation of a body as referred to in paragraph 1 of this Article to the Board pursuant to the consistency mechanism referred to in Article 63.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:08', '2024-08-14 08:42:08'),
(2030, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1766, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Without prejudice to the tasks and powers of the competent supervisory authority and the provisions of Chapter VIII, a body as referred to in paragraph 1 of this Article shall, subject to appropriate safeguards, take appropriate action in cases of infringement of the code by a controller or processor, including suspension or exclusion of the controller or processor concerned from the code. It shall inform the competent supervisory authority of such actions and the reasons for taking them.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:09', '2024-08-14 08:42:09'),
(2031, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1767, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The competent supervisory authority shall revoke the accreditation of a body as referred to in paragraph 1 if the requirements for accreditation are not, or are no longer, met or where actions taken by the body infringe this Regulation.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:10', '2024-08-14 08:42:10'),
(2032, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1768, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Member States, the supervisory authorities, the Board and the Commission shall encourage, in particular at Union level, the establishment of data protection certification mechanisms and of data protection seals and marks, for the purpose of demonstrating compliance with this Regulation of processing operations by controllers and processors.The specific needs of micro, small and medium-sized enterprises shall be taken into account.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:13', '2024-08-14 08:42:13'),
(2033, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1769, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"This Article shall not apply to processing carried out by public authorities and bodies.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:14', '2024-08-14 08:42:14'),
(2034, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1770, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In addition to adherence by controllers or processors subject to this Regulation, data protection certification mechanisms, seals or marks approved pursuant to paragraph 5 of this Article may be established for the purpose of demonstrating the existence of appropriate safeguards provided by controllers or processors that are not subject to this Regulation pursuant to Article 3 within the framework of personal data transfers to third countries or international organisations under the terms referred to in point (f) of Article 46(2). Such controllers or processors shall make binding and enforceable commitments, via contractual or other legally binding instruments, to apply those appropriate safeguards, including with regard to the rights of data subjects.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:17', '2024-08-14 08:42:17'),
(2035, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1771, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The certification shall be voluntary and available via a process that is transparent.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:19', '2024-08-14 08:42:19'),
(2036, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1772, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A certification pursuant to this Article does not reduce the responsibility of the controller or the processor for compliance with this Regulation and is without prejudice to the tasks and powers of the supervisory authorities which are competent pursuant to Article 55 or 56.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:20', '2024-08-14 08:42:20'),
(2037, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1773, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A certification pursuant to this Article shall be issued by the certification bodies referred to in Article 43 or by the competent supervisory authority, on the basis of criteria approved by that competent supervisory authority pursuant to Article 58(3) or by the Board pursuant to Article 63. Where the criteria are approved by the Board, this may result in a common certification, the European Data Protection Seal.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:21', '2024-08-14 08:42:21'),
(2038, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1774, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller or processor which submits its processing to the certification mechanism shall provide the certification body referred to in Article 43, or where applicable, the competent supervisory authority, with all information and access to its processing activities which are necessary to conduct the certification procedure\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:24', '2024-08-14 08:42:24'),
(2039, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1775, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Certification shall be issued to a controller or processor for a maximum period of three years and may be renewed, under the same conditions, provided that the relevant criteria continue to be met. Certification shall be withdrawn, as applicable, by the certification bodies referred to in Article 43 or by the competent supervisory authority where the criteria for the certification are not or are no longer met.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:25', '2024-08-14 08:42:25'),
(2040, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1776, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Board shall collate all certification mechanisms and data protection seals and marks in a register and shall make them publicly available by any appropriate means.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:26', '2024-08-14 08:42:26'),
(2041, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1777, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Without prejudice to the tasks and powers of the competent supervisory authority under Articles 57 and 58, certification bodies which have an appropriate level of expertise in relation to data protection shall, after informing the supervisory authority in order to allow it to exercise its powers pursuant to point (h) of Article 58(2) where necessary, issue and renew certification. Member States shall ensure that those certification bodies are accredited by one or both of the following the supervisory authority which is competent pursuant to Article 55 or 56;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:31', '2024-08-14 08:42:31'),
(2042, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1778, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Without prejudice to the tasks and powers of the competent supervisory authority under Articles 57 and 58, certification bodies which have an appropriate level of expertise in relation to data protection shall, after informing the supervisory authority in order to allow it to exercise its powers pursuant to point (h) of Article 58(2) where necessary, issue and renew certification. Member States shall ensure that those certification bodies are accredited by one or both of the following the national accreditation body named in accordance with Regulation (EC) No 765\\/2008 of the European Parliament and of the Council\\u00b9 in accordance with EN-ISO\\/IEC 17065\\/2012 and with the additional requirements established by the supervisory authority which is competent pursuant to Article 55 or 56.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:32', '2024-08-14 08:42:32'),
(2043, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1779, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Certification bodies referred to in paragraph 1 shall be accredited in accordance with that paragraph only where they have demonstrated their independence and expertise in relation to the subject-matter of the certification to the satisfaction of the competent supervisory authority;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:34', '2024-08-14 08:42:34'),
(2044, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1780, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Certification bodies referred to in paragraph 1 shall be accredited in accordance with that paragraph only where they have undertaken to respect the criteria referred to in Article 42(5) and approved by the supervisory authority which is competent pursuant to Article 55 or 56 or by the Board pursuant to Article 63;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:36', '2024-08-14 08:42:36'),
(2045, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1781, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Certification bodies referred to in paragraph 1 shall be accredited in accordance with that paragraph only where they have established procedures for the issuing, periodic review and withdrawal of data protection certification, seals and marks;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:37', '2024-08-14 08:42:37'),
(2046, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1782, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Certification bodies referred to in paragraph 1 shall be accredited in accordance with that paragraph only where they have established procedures and structures to handle complaints about infringements of the certification or the manner in which the certification has been, or is being, implemented by the controller or processor, and to make those procedures and structures transparent to data subjects and the public;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:39', '2024-08-14 08:42:39'),
(2047, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1783, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Certification bodies referred to in paragraph 1 shall be accredited in accordance with that paragraph only where they have demonstrated, to the satisfaction of the competent supervisory authority, that their tasks and duties do not result in a conflict of interests.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:40', '2024-08-14 08:42:40'),
(2048, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1784, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The accreditation of certification bodies as referred to in paragraphs 1 and 2 of this Article shall take place on the basis of requirements approved by the supervisory authority which is competent pursuant to Article 55 or 56 or by the Board pursuant to Article 63. In the case of accreditation pursuant to point (b) of paragraph 1 of this Article, those requirements shall complement those envisaged in Regulation (EC) No 765\\/2008 and the technical rules that describe the methods and procedures of the certification bodies.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:43', '2024-08-14 08:42:43'),
(2049, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1785, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The certification bodies referred to in paragraph 1 shall be responsible for the proper assessment leading to the certification or the withdrawal of such certification without prejudice to the responsibility of the controller or processor for compliance with this Regulation. The accreditation shall be issued for a maximum period of five years and may be renewed on the same conditions provided that the certification body meets the requirements set out in this Article.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:44', '2024-08-14 08:42:44'),
(2050, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1786, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The certification bodies referred to in paragraph 1 shall provide the competent supervisory authorities with the reasons for granting or withdrawing the requested certification.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:47', '2024-08-14 08:42:47'),
(2051, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1787, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The requirements referred to in paragraph 3 of this Article and the criteria referred to in Article 42(5) shall be made public by the supervisory authority in an easily accessible form. The supervisory authorities shall also transmit those requirements and criteria to the Board.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:49', '2024-08-14 08:42:49'),
(2052, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1788, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Without prejudice to Chapter VIII, the competent supervisory authority or the national accreditation body shall revoke an accreditation of a certification body pursuant to paragraph 1 of this Article where the conditions for the accreditation are not, or are no longer, met or where actions taken by a certification body infringe this Regulation.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:51', '2024-08-14 08:42:51'),
(2053, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1789, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Commission shall be empowered to adopt delegated acts in accordance with Article 92 for the purpose of specifying the requirements to be taken into account for the data protection certification mechanisms referred to in Article 42(1).\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:52', '2024-08-14 08:42:52'),
(2054, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1790, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Commission may adopt implementing acts laying down technical standards for certification mechanisms and data protection seals and marks, and mechanisms to promote and recognise those certification mechanisms, seals and marks. Those implementing acts shall be adopted in accordance with the examination procedure referred to in Article 93(2).\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:53', '2024-08-14 08:42:53'),
(2055, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1791, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Any transfer of personal data which are undergoing processing or are intended for processing after transfer to a third country or to an international organisation shall take place only if, subject to the other provisions of this Regulation, the conditions laid down in this Chapter are complied with by the controller and processor, including for onward transfers of personal data from the third country or an international organisation to another third country or to another international organisation. All provisions in this Chapter shall be applied in order to ensure that the level of protection of natural persons guaranteed by this Regulation is not undermined.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:55', '2024-08-14 08:42:55'),
(2056, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1792, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A transfer of personal data to a third country or an international organisation may take place where the Commission has decided that the third country, a territory or one or more specified sectors within that third country, or the international organisation in question ensures an adequate level of protection. Such a transfer shall not require any specific authorisation.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:56', '2024-08-14 08:42:56'),
(2057, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1793, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"When assessing the adequacy of the level of protection, the Commission shall, in particular, take account of the rule of law, respect for human rights and fundamental freedoms, relevant legislation, both general and sectoral, including concerning public security, defence, national security and criminal law and the access of public authorities to personal data, as well as the implementation of such legislation, data protection rules, professional rules and security measures, including rules for the onward transfer of personal data to another third country or international organisation which are complied with in that country or international organisation, case-law, as well as effective and enforceable data subject rights and effective administrative and judicial redress for the data subjects whose personal data are being transferred;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:42:57', '2024-08-14 08:42:57'),
(2058, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1794, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"When assessing the adequacy of the level of protection, the Commission shall, in particular, take account of the existence and effective functioning of one or more independent supervisory authorities in the third country or to which an international organisation is subject, with responsibility for ensuring and enforcing compliance with the data protection rules, including adequate enforcement powers, for assisting and advising the data subjects in exercising their rights and for cooperation with the supervisory authorities of the Member States; and\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:00', '2024-08-14 08:43:00'),
(2059, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1795, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"When assessing the adequacy of the level of protection, the Commission shall, in particular, take account of the international commitments the third country or international organisation concerned has entered into, or other obligations arising from legally binding conventions or instruments as well as from its participation in multilateral or regional systems, in particular in relation to the protection of personal data.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:00', '2024-08-14 08:43:00'),
(2060, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1796, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Commission, after assessing the adequacy of the level of protection, may decide, by means of implementing act, that a third country, a territory or one or more specified sectors within a third country, or an international organisation ensures an adequate level of protection within the meaning of paragraph 2 of this Article. The implementing act shall provide for a mechanism for a periodic review, at least every four years, which shall take into account all relevant developments in the third country or international organisation. The implementing act shall specify its territorial and sectoral application and, where applicable, identify the supervisory authority or authorities referred to in point (b) of paragraph 2 of this Article. The implementing act shall be adopted in accordance with the examination procedure referred to in Article 93(2).\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:03', '2024-08-14 08:43:03'),
(2061, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1797, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Commission shall, on an ongoing basis, monitor developments in third countries and international organisations that could affect the functioning of decisions adopted pursuant to paragraph 3 of this Article and decisions adopted on the basis of Article 25(6) of Directive 95\\/46\\/EC.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:04', '2024-08-14 08:43:04'),
(2062, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1798, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Commission shall, where available information reveals, in particular following the review referred to in paragraph 3 of this Article, that a third country, a territory or one or more specified sectors within a third country, or an international organisation no longer ensures an adequate level of protection within the meaning of paragraph 2 of this Article, to the extent necessary, repeal, amend or suspend the decision referred to in paragraph 3 of this Article by means of implementing acts without retro-active effect. Those implementing acts shall be adopted in accordance with the examination procedure referred to in Article 93(2).On duly justified imperative grounds of urgency, the Commission shall adopt immediately applicable implementing acts in accordance with the procedure referred to in Article 93(3).\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:05', '2024-08-14 08:43:05'),
(2063, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1799, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Commission shall enter into consultations with the third country or international organisation with a view to remedying the situation giving rise to the decision made pursuant to paragraph 5.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:09', '2024-08-14 08:43:09'),
(2064, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1800, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A decision pursuant to paragraph 5 of this Article is without prejudice to transfers of personal data to the third country, a territory or one or more specified sectors within that third country, or the international organisation in question pursuant to Articles 46 to 49.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:09', '2024-08-14 08:43:09'),
(2065, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1801, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Commission shall publish in the Official Journal of the European Union and on its website a list of the third countries, territories and specified sectors within a third country and international organisations for which it has decided that an adequate level of protection is or is no longer ensured.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:11', '2024-08-14 08:43:11'),
(2066, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1802, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Decisions adopted by the Commission on the basis of Article 25(6) of Directive 95\\/46\\/EC shall remain in force until amended, replaced or repealed by a Commission Decision adopted in accordance with paragraph 3 or 5 of this Article.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:12', '2024-08-14 08:43:12'),
(2067, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1803, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In the absence of a decision pursuant to Article 45(3), a controller or processor may transfer personal data to a third country or an international organisation only if the controller or processor has provided appropriate safeguards, and on condition that enforceable data subject rights and effective legal remedies for data subjects are available.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:13', '2024-08-14 08:43:13'),
(2068, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1804, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The appropriate safeguards referred to in paragraph 1 may be provided for, without requiring any specific authorisation from a supervisory authority, by a legally binding and enforceable instrument between public authorities or bodies;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:16', '2024-08-14 08:43:16');
