(2069, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1805, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The appropriate safeguards referred to in paragraph 1 may be provided for, without requiring any specific authorisation from a supervisory authority, by binding corporate rules in accordance with Article 47;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:17', '2024-08-14 08:43:17'),
(2070, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1806, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The appropriate safeguards referred to in paragraph 1 may be provided for, without requiring any specific authorisation from a supervisory authority, by standard data protection clauses adopted by the Commission in accordance with the examination procedure referred to in Article 93(2);\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:19', '2024-08-14 08:43:19'),
(2071, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1807, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The appropriate safeguards referred to in paragraph 1 may be provided for, without requiring any specific authorisation from a supervisory authority, by standard data protection clauses adopted by a supervisory authority and approved by the Commission pursuant to the examination procedure referred to in Article 93(2);\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:20', '2024-08-14 08:43:20'),
(2072, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1808, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The appropriate safeguards referred to in paragraph 1 may be provided for, without requiring any specific authorisation from a supervisory authority, by an approved code of conduct pursuant to Article 40 together with binding and enforceable commitments of the controller or processor in the third country to apply the appropriate safeguards, including as regards data subjects\\u2019 rights; or\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:21', '2024-08-14 08:43:21'),
(2073, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1809, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The appropriate safeguards referred to in paragraph 1 may be provided for, without requiring any specific authorisation from a supervisory authority, by an approved certification mechanism pursuant to Article 42 together with binding and enforceable commitments of the controller or processor in the third country to apply the appropriate safeguards, including as regards data subjects\\u2019 rights.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:24', '2024-08-14 08:43:24'),
(2074, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1810, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Subject to the authorisation from the competent supervisory authority, the appropriate safeguards referred to in paragraph 1 may also be provided for, in particular, by contractual clauses between the controller or processor and the controller, processor or the recipient of the personal data in the third country or international organisation; or\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:25', '2024-08-14 08:43:25'),
(2075, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1811, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Subject to the authorisation from the competent supervisory authority, the appropriate safeguards referred to in paragraph 1 may also be provided for, in particular, by provisions to be inserted into administrative arrangements between public authorities or bodies which include enforceable and effective data subject rights.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:26', '2024-08-14 08:43:26'),
(2076, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1812, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The supervisory authority shall apply the consistency mechanism referred to in Article 63 in the cases referred to in paragraph 3 of this Article.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:27', '2024-08-14 08:43:27'),
(2077, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1813, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Authorisations by a Member State or supervisory authority on the basis of Article 26(2) of Directive 95\\/46\\/EC shall remain valid until amended, replaced or repealed, if necessary, by that supervisory authority. Decisions adopted by the Commission on the basis of Article 26(4) of Directive 95\\/46\\/EC shall remain in force until amended, replaced or repealed, if necessary, by a Commission Decision adopted in accordance with paragraph 2 of this Article.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:29', '2024-08-14 08:43:29'),
(2078, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1814, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The competent supervisory authority shall approve binding corporate rules in accordance with the consistency mechanism set out in Article 63, provided that they are legally binding and apply to and are enforced by every member concerned of the group of undertakings, or group of enterprises engaged in a joint economic activity, including their employees expressly confer enforceable rights on data subjects with regard to the processing of their personal data; and fulfil the requirements laid down in paragraph 2.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:30', '2024-08-14 08:43:30'),
(2079, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1815, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The competent supervisory authority shall approve binding corporate rules in accordance with the consistency mechanism set out in Article 63, provided that they expressly confer enforceable rights on data subjects with regard to the processing of their personal data; and\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:33', '2024-08-14 08:43:33'),
(2080, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1816, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The competent supervisory authority shall approve binding corporate rules in accordance with the consistency mechanism set out in Article 63, provided that they fulfil the requirements laid down in paragraph 2.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:33', '2024-08-14 08:43:33'),
(2081, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1817, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The binding corporate rules referred to in paragraph 1 shall specify at least the structure and contact details of the group of undertakings, or group of enterprises engaged in a joint economic activity and of each of its members;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:36', '2024-08-14 08:43:36'),
(2082, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1818, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The binding corporate rules referred to in paragraph 1 shall specify at least the data transfers or set of transfers, including the categories of personal data, the type of processing and its purposes, the type of data subjects affected and the identification of the third country or countries in question;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:37', '2024-08-14 08:43:37'),
(2083, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1819, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The binding corporate rules referred to in paragraph 1 shall specify at least their legally binding nature, both internally and externally;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:38', '2024-08-14 08:43:38'),
(2084, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1820, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The binding corporate rules referred to in paragraph 1 shall specify at least the application of the general data protection principles, in particular purpose limitation, data minimisation, limited storage periods, data quality, data protection by design and by default, legal basis for processing, processing of special categories of personal data, measures to ensure data security, and the requirements in respect of onward transfers to bodies not bound by the binding corporate rules;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:40', '2024-08-14 08:43:40'),
(2085, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1821, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The binding corporate rules referred to in paragraph 1 shall specify at least the rights of data subjects in regard to processing and the means to exercise those rights, including the right not to be subject to decisions based solely on automated processing, including profiling in accordance with Article 22, the right to lodge a complaint with the competent supervisory authority and before the competent courts of the Member States in accordance with Article 79, and to obtain redress and, where appropriate, compensation for a breach of the binding corporate rules;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:41', '2024-08-14 08:43:41'),
(2086, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1822, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The binding corporate rules referred to in paragraph 1 shall specify at least the acceptance by the controller or processor established on the territory of a Member State of liability for any breaches of the binding corporate rules by any member concerned not established in the Union; the controller or the processor shall be exempt from that liability, in whole or in part, only if it proves that that member is not responsible for the event giving rise to the damage;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:44', '2024-08-14 08:43:44'),
(2087, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1823, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The binding corporate rules referred to in paragraph 1 shall specify at least how the information on the binding corporate rules, in particular on the provisions referred to in points (d), (e) and (f) of this paragraph is provided to the data subjects in addition to Articles 13 and 14;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:45', '2024-08-14 08:43:45'),
(2088, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1824, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The binding corporate rules referred to in paragraph 1 shall specify at least the tasks of any data protection officer designated in accordance with Article 37 or any other person or entity in charge of the monitoring compliance with the binding corporate rules within the group of undertakings, or group of enterprises engaged in a joint economic activity, as well as monitoring training and complaint-handling;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:46', '2024-08-14 08:43:46'),
(2089, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1825, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The binding corporate rules referred to in paragraph 1 shall specify at least the complaint procedures;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:49', '2024-08-14 08:43:49'),
(2090, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1826, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The binding corporate rules referred to in paragraph 1 shall specify at least the mechanisms within the group of undertakings, or group of enterprises engaged in a joint economic activity for ensuring the verification of compliance with the binding corporate rules. Such mechanisms shall include data protection audits and methods for ensuring corrective actions to protect the rights of the data subject. Results of such verification should be communicated to the person or entity referred to in point (h) and to the board of the controlling undertaking of a group of undertakings, or of the group of enterprises engaged in a joint economic activity, and should be available upon request to the competent supervisory authority;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:50', '2024-08-14 08:43:50'),
(2091, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1827, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The binding corporate rules referred to in paragraph 1 shall specify at least the mechanisms for reporting and recording changes to the rules and reporting those changes to the supervisory authority;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:54', '2024-08-14 08:43:54'),
(2092, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1828, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The binding corporate rules referred to in paragraph 1 shall specify at least the cooperation mechanism with the supervisory authority to ensure compliance by any member of the group of undertakings, or group of enterprises engaged in a joint economic activity, in particular by making available to the supervisory authority the results of verifications of the measures referred to in point (j);\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:54', '2024-08-14 08:43:54'),
(2093, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1829, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The binding corporate rules referred to in paragraph 1 shall specify at least the mechanisms for reporting to the competent supervisory authority any legal requirements to which a member of the group of undertakings, or group of enterprises engaged in a joint economic activity is subject in a third country which are likely to have a substantial adverse effect on the guarantees provided by the binding corporate rules; and\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:55', '2024-08-14 08:43:55'),
(2094, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1830, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The binding corporate rules referred to in paragraph 1 shall specify at least the appropriate data protection training to personnel having permanent or regular access to personal data.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:58', '2024-08-14 08:43:58'),
(2095, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1831, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The Commission may specify the format and procedures for the exchange of information between controllers, processors and supervisory authorities for binding corporate rules within the meaning of this Article. Those implementing acts shall be adopted in accordance with the examination procedure set out in Article 93(2).\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:43:59', '2024-08-14 08:43:59'),
(2096, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1832, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Any judgment of a court or tribunal and any decision of an administrative authority of a third country requiring a controller or processor to transfer or disclose personal data may only be recognised or enforceable in any manner if based on an international agreement, such as a mutual legal assistance treaty, in force between the requesting third country and the Union or a Member State, without prejudice to other grounds for transfer pursuant to this Chapter.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:44:01', '2024-08-14 08:44:01'),
(2097, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1833, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In the absence of an adequacy decision pursuant to Article 45(3), or of appropriate safeguards pursuant to Article 46, including binding corporate rules, a transfer or a set of transfers of personal data to a third country or an international organisation shall take place only on the data subject has explicitly consented to the proposed transfer, after having been informed of the possible risks of such transfers for the data subject due to the absence of an adequacy decision and appropriate safeguards;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:44:03', '2024-08-14 08:44:03'),
(2098, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1834, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In the absence of an adequacy decision pursuant to Article 45(3), or of appropriate safeguards pursuant to Article 46, including binding corporate rules, a transfer or a set of transfers of personal data to a third country or an international organisation shall take place only on the transfer is necessary for the performance of a contract between the data subject and the controller or the implementation of pre-contractual measures taken at the data subject\\u2019s request;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:44:04', '2024-08-14 08:44:04'),
(2099, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1835, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In the absence of an adequacy decision pursuant to Article 45(3), or of appropriate safeguards pursuant to Article 46, including binding corporate rules, a transfer or a set of transfers of personal data to a third country or an international organisation shall take place only on the transfer is necessary for the conclusion or performance of a contract concluded in the interest of the data subject between the controller and another natural or legal person;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:44:08', '2024-08-14 08:44:08'),
(2100, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1836, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In the absence of an adequacy decision pursuant to Article 45(3), or of appropriate safeguards pursuant to Article 46, including binding corporate rules, a transfer or a set of transfers of personal data to a third country or an international organisation shall take place only on the transfer is necessary for important reasons of public interest;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:44:10', '2024-08-14 08:44:10'),
(2101, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1837, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In the absence of an adequacy decision pursuant to Article 45(3), or of appropriate safeguards pursuant to Article 46, including binding corporate rules, a transfer or a set of transfers of personal data to a third country or an international organisation shall take place only on the transfer is necessary for the establishment, exercise or defence of legal claims;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:44:12', '2024-08-14 08:44:12'),
(2102, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1838, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In the absence of an adequacy decision pursuant to Article 45(3), or of appropriate safeguards pursuant to Article 46, including binding corporate rules, a transfer or a set of transfers of personal data to a third country or an international organisation shall take place only on the transfer is necessary in order to protect the vital interests of the data subject or of other persons, where the data subject is physically or legally incapable of giving consent;\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:44:13', '2024-08-14 08:44:13'),
(2103, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1839, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In the absence of an adequacy decision pursuant to Article 45(3), or of appropriate safeguards pursuant to Article 46, including binding corporate rules, a transfer or a set of transfers of personal data to a third country or an international organisation shall take place only on the transfer is made from a register which according to Union or Member State law is intended to provide information to the public and which is open to consultation either by the public in general or by any person who can demonstrate a legitimate interest, but only to the extent that the conditions laid down by Union or Member State law for consultation are fulfilled in the particular case. Where a transfer could not be based on a provision in Article 45 or 46, including the provisions on binding corporate rules, and none of the derogations for a specific situation referred to in the first subparagraph of this paragraph is applicable, a transfer to a third country or an international organisation may take place only if the transfer is not repetitive, concerns only a limited number of data subjects, is necessary for the purposes of compelling legitimate interests pursued by the controller which are not overridden by the interests or rights and freedoms of the data subject, and the controller has assessed all the circumstances surrounding the data transfer and has on the basis of that assessment provided suitable safeguards with regard to the protection of personal data. The controller shall inform the supervisory authority of the transfer. The controller shall, in addition to providing the information referred to in Articles 13 and 14, inform the data subject of the transfer and on the compelling legitimate interests pursued.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:44:15', '2024-08-14 08:44:15'),
(2104, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1840, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"A transfer pursuant to point (g) of the first subparagraph of paragraph 1 shall not involve the entirety of the personal data or entire categories of the personal data contained in the register. Where the register is intended for consultation by persons having a legitimate interest, the transfer shall be made only at the request of those persons or if they are to be the recipients.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:44:18', '2024-08-14 08:44:18'),
(2105, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1841, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"Points (a), (b) and (c) of the first subparagraph of paragraph 1 and the second subparagraph thereof shall not apply to activities carried out by public authorities in the exercise of their public powers.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:44:18', '2024-08-14 08:44:18'),
(2106, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1842, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The public interest referred to in point (d) of the first subparagraph of paragraph 1 shall be recognised in Union law or in the law of the Member State to which the controller is subject.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:44:22', '2024-08-14 08:44:22'),
(2107, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1843, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"In the absence of an adequacy decision, Union or Member State law may, for important reasons of public interest, expressly set limits to the transfer of specific categories of personal data to a third country or an international organisation. Member States shall notify such provisions to the Commission.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:44:22', '2024-08-14 08:44:22'),
(2108, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1844, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"GDPR\",\"provisions.provisions\":\"The controller or processor shall document the assessment as well as the suitable safeguards referred to in the second subparagraph of paragraph 1 of this Article in the records referred to in Article 30.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 08:44:24', '2024-08-14 08:44:24'),
(2109, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1845, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"SOC 2\",\"provisions.provisions\":\"COSO Principle 1: The entity demonstrates a commitment to integrity and ethical values.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 10:17:20', '2024-08-14 10:17:20'),
(2110, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1846, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"SOC 2\",\"provisions.provisions\":\"COSO Principle 2: The board of directors demonstrates independence from management and exercises oversight of the development and performance of internal control.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 10:17:21', '2024-08-14 10:17:21'),
(2111, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1847, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"SOC 2\",\"provisions.provisions\":\"COSO Principle 3: Management establishes, with board oversight, structures, reporting lines, and appropriate authorities and responsibilities in the pursuit of objectives.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 10:17:22', '2024-08-14 10:17:22'),
(2112, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1848, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"SOC 2\",\"provisions.provisions\":\"COSO Principle 4: The entity demonstrates a commitment to attract, develop, and retain competent individuals in alignment with objectives.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 10:17:23', '2024-08-14 10:17:23'),
