(2128, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1864, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"SOC 2\",\"provisions.provisions\":\"Prior to issuing system credentials and granting system access, the entity registers and authorizes new internal and external users whose access is administered by the entity. For those users whose access is administered by the entity, user system credentials are removed when user access is no longer authorized.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 10:17:42', '2024-08-14 10:17:42'),
(2129, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1865, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"SOC 2\",\"provisions.provisions\":\"The entity authorizes, modifies, or removes access to data, software, functions, and other protected information assets based on roles, responsibilities, or the system design and changes, giving consideration to the concepts of least privilege and segregation of duties, to meet the entity\\u2019s objectives.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 10:17:43', '2024-08-14 10:17:43'),
(2130, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1866, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"SOC 2\",\"provisions.provisions\":\"The entity restricts physical access to facilities and protected information assets (for example, data center facilities, back-up media storage, and other sensitive locations) to authorized personnel to meet the entity\\u2019s objectives.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 10:17:44', '2024-08-14 10:17:44'),
