(2160, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1896, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"SOC 2\",\"provisions.provisions\":\"The entity grants identified and authenticated data subjects the ability to access their stored personal information for review and, upon request, provides physical or electronic copies of that information to data subjects to meet the entity\\u2019s objectives related to privacy. If access is denied, data subjects are informed of the denial and reason for such denial, as required, to meet the entity\\u2019s objectives related to privacy.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 10:18:19', '2024-08-14 10:18:19'),
(2161, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1897, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"SOC 2\",\"provisions.provisions\":\"The entity corrects, amends, or appends personal information based on information provided by data subjects and communicates such information to third parties, as committed or required, to meet the entity\\u2019s objectives related to privacy. If a request for correction is denied, data subjects are informed of the denial and reason for such denial to meet the entity\\u2019s objectives related to privacy.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 10:18:20', '2024-08-14 10:18:20'),
(2162, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1898, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"SOC 2\",\"provisions.provisions\":\"Privacy Criteria Related to Disclosure and Notification\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 10:18:21', '2024-08-14 10:18:21'),
(2163, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1899, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"SOC 2\",\"provisions.provisions\":\"The entity discloses personal information to third parties with the explicit consent of data subjects, and such consent is obtained prior to disclosure to meet the entity\\u2019s objectives related to privacy.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 10:18:22', '2024-08-14 10:18:22'),
(2164, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1900, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"SOC 2\",\"provisions.provisions\":\"The entity creates and retains a complete, accurate, and timely record of authorized disclosures of personal information to meet the entity\\u2019s objectives related to privacy.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 10:18:23', '2024-08-14 10:18:23'),
(2165, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1901, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"SOC 2\",\"provisions.provisions\":\"The entity creates and retains a complete, accurate, and timely record of detected or reported unauthorized disclosures (including breaches) of personal information to meet the entity\\u2019s objectives related to privacy.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 10:18:25', '2024-08-14 10:18:25'),
(2166, 'Framework-Provision', 'created', 'App\\Models\\FrameworkProvision', 'created', 1902, 'App\\Models\\User', 1, '{\"attributes\":{\"frameworks.name\":\"SOC 2\",\"provisions.provisions\":\"The entity obtains privacy commitments from vendors and other third parties who have access to personal information to meet the entity\\u2019s objectives related to privacy. The entity assesses those parties\\u2019 compliance on a periodic and as-needed basis and takes corrective action, if necessary.\",\"createdBy.name\":null,\"updatedBy.name\":null}}', NULL, '2024-08-14 10:18:25', '2024-08-14 10:18:25'),
